gameforge.ghost_story._010_sayTitle = "Duch sury: "
gameforge.ghost_story._020_npcChat = "Jsi duch? "
gameforge.ghost_story._030_say = "Ne, jsem tvoje babička... Jasně, že jsem duch![ENTER]Někdo tě snad jako dítě upustil na hlavu? "
gameforge.ghost_story._040_npcChat = "Pomóóóc, je to duch! "
gameforge.ghost_story._050_say = "Hele ho, nebojácného bojovníka! Baf!! "
gameforge.ghost_story._060_npcChat = "Kdo jsi? "
gameforge.ghost_story._070_say = "Mé jméno je Akuma. Byl jsem jeden z největších[ENTER]bojovníků, někteří říkají, nejlepší svého času.[ENTER]Dokonce jsem tomu věřil i já sám! Ale podívej se[ENTER]na mne teď! Podívejte se na to, co ze mne zbylo[ENTER]když sem našel jeskyni... a zahynul. "
gameforge.ghost_story._080_npcChat = "Znáš historii tohoto místa? "
gameforge.ghost_story._090_say = "Ach, já vím co se tenkrát stalo. Dlouhou dobu[ENTER]jsem pátral, snažil se zjistit důvod této snahy[ENTER]najít jeskyni.[ENTER]A od mé smrti jsem měl hodně času na přemýšlení.[ENTER]Jen se ptej, co potřebuješ vědět! "
gameforge.ghost_story._100_npcChat = "Co to je za portál? "
gameforge.ghost_story._110_say = "Tento portál vede do temné dračí komnaty, padlého[ENTER]Boha Beran-Setaou. Může být aktivován pomocí[ENTER]magického klíče, který ale mohou mít jen[ENTER]bojovníci Beran Setaou... "
gameforge.ghost_story._120_npcChat = "Božská Trojice "
gameforge.ghost_story._130_say = "Kdysi dávno božská sourozenecká trojice udržovala[ENTER]podstatu života.[ENTER]Bahar-Taraji, Bohyně stvoření, byla nejmladší z[ENTER]trojice. Jejím úkolem bylo vytvářet nové bytí -[ENTER]kudy šla tam květiny kvetly a život vzkvétal. "
gameforge.ghost_story._140_say = "Nejstarší bratr byl nazýván Beran-Setaou. Byl[ENTER]odpovědný za ukončení života a za rozklad.[ENTER]Kdykoli byl životní cyklus u konce, oddělil tělo[ENTER]od duše a vedl duše do posmrtného života.[ENTER]A tak byl tedy zajištěn nepřetržitý koloběh[ENTER]života. "
gameforge.ghost_story._150_say = "Prostřední bratr byl Baljit-Elvedin. Jako Bůh[ENTER]uchování, umírňoval oba sourozence a umožňoval[ENTER]věčný koloběh života pro všechna pozemská[ENTER]stvoření. Byl například zodpovědný za příchody a[ENTER]odchody ročních období. "
gameforge.ghost_story._160_npcChat = "Rozepře "
gameforge.ghost_story._170_say = "V dobách nouze, lidé prosili bohy o pomoc. Avšak[ENTER]přílis se báli a zároveň podceňovali[ENTER]Beran-Setaoua, v jejich krátkozrakosti[ENTER]nepochopili, že stejně jako existuje světlo a[ENTER]tma, tak existuje i on jako protějšek ke[ENTER]stvoření. "
gameforge.ghost_story._180_say = "Po dlouhou dobu to bůh ignoroval, ale po řadu let[ENTER]v něm rostla nevraživost, která se pomalu měnila[ENTER]v nenávist a zlomyslnost. Zpočátku směřoval svůj[ENTER]hněv vůči lidem na vyvolání válek a nemocí. Avšak[ENTER]to vedlo jen k jeho dalšímu odmítání a lidé se[ENTER]stále více obraceli k Bahar-Taraji, bohyni[ENTER]stvoření nového života. A tak vznikl bludný kruh,[ENTER]který nakonec vedl ke zkáze. "
gameforge.ghost_story._190_say = "A jak se stával boží hněv intenzivnější tak jej[ENTER]již nemohl déle kontrolovat. Zabil svoji sestru,[ENTER]která byla u lidí nejoblíbenější. Tento čin byl[ENTER]zcela nevyprovokovaný, ten den zacházela se svým[ENTER]bratrem se stejnou láskou a vírou jako to[ENTER]dělávala každý den. Podíval do jejích laskavých[ENTER]očí a její život navždy ukončil. "
gameforge.ghost_story._200_npcChat = "Následky "
gameforge.ghost_story._210_say = "Smrt Bahar-Taraji měla hrozivé následky jak pro[ENTER]svět bohů tak pro svět lidí: Přišla dlouhá a[ENTER]krutá zima, která zabíjela stvoření v moři i na[ENTER]zemi. Země zmrzla tak silně, že kameny praskaly a[ENTER]dokonce i nejstarší a nejsilnější stromy uschly. "
gameforge.ghost_story._220_say = "Mladí lidé a zvířata zahynuli a když se konečně[ENTER]oteplilo, porodnost se stala nižší než kdykoli[ENTER]předtim.[ENTER]Oslabená země toho nepřinesla dost, aby uživila[ENTER]dospělou populaci, natož aby uživila nový život.[ENTER]Naděje umírala spolu se životem a obě potřebovali[ENTER]dlouhý čas na plné zotavení. "
gameforge.ghost_story._230_say = "I bohové trpěli, avšak, čas pro ně plynul[ENTER]pomaleji než pro lidi. Jakmile si Beran-Setaou[ENTER]uvědomil obludnost činu, který spáchal, stáhl se[ENTER]a vyhledal samotu. Nedokázal se v sobě vypořádat[ENTER]se zničující lítostí a hlubokým uspokojením -[ENTER]dokud to druhé nezvítězilo v této duševní bitvě.[ENTER]Jeho srdce zmrzlo a Bůh Rozkladu se stal Bohem[ENTER]Ledu a Chladu, Nenávisti a Strachu. "
gameforge.ghost_story._240_npcChat = "Bitva "
gameforge.ghost_story._250_say = "Baljit-Elvedin, Bůh uchování, dlouho truchlil pro[ENTER]svou sestru. A jakmile pevné drápy bolesti[ENTER]konečně uvolnili své sevření kolem jeho srdce,[ENTER]vydal se hledat svého bratra. Porozumění a[ENTER]moudrost stále domivovali jeho životní cestě a[ENTER]tak i přes svůj žal uznal, že život nemůže[ENTER]pokračovat dále v nerovnováze. "
gameforge.ghost_story._260_say = "Sledoval stopy svého bratra po mnoho dlouhých[ENTER]let, a nakonec jej našel v jeskyni utvořené z[ENTER]ledu, nenávisti a samoty, daleko od jakékoli[ENTER]známky života. Baljit-Elvedin udělal co udělat[ENTER]musel, i kdyby to mělo zasadit další ránu jeho[ENTER]srdci - vyzval svého bratra na souboj.[ENTER]Beran-Setaou byl tak dalece odkloněn od cesty[ENTER]spravedlnosti, že neváhal ani na vteřinu... "
gameforge.ghost_story._270_say = "Bitva byla krutá a mimo chápání. Baljit-Elvedin[ENTER]bitvu vyhrál. Zůstal věrný svým zásadám a nezabil[ENTER]svého bratra, ale za pomoci své síly jej uzavřel[ENTER]do jeskyně. A tak zde Beran-Setaou pobývá s[ENTER]tvory, které několik staletí sám vytváří... "
gameforge.ghost_story._280_npcChat = "Již jsem slyšel dost. "
gameforge.ghost_story._290_say = "Možná bude tvůj osud jiný než můj. Hodně štěstí! "
gameforge.ghost_story._300_sayTitle = "Duch bojovníka: "
gameforge.ghost_story._310_npcChat = "Proč si průhledný? "
gameforge.ghost_story._320_say = "Proč asi?! Jsem duch a jsem v této skále uvězněn[ENTER]po tísíce let... "
gameforge.ghost_story._330_say = "Pomóóóc, je to duch! "
gameforge.ghost_story._340_say = "Správně, jsem duch. Rychle uteč nebo tě sním! "
gameforge.ghost_story._350_npcChat = "Kdo jsi? "
gameforge.ghost_story._360_say = "Kdysi jsem byl žoldák, prostý muž. Moji rodiče[ENTER]byli farmáři. Nechtěl jsem pracovat jako farmář[ENTER]po zbytek svého života, tak jsem se přidal k[ENTER]armádě. Bylo to tvrdé a krvavé, ale nebylo to tak[ENTER]špatné. Ale nakonec jsem byl unaven věčným[ENTER]rozkazovánim šlechticů, které nikam nevedlo a[ENTER]odešel jsem. A osud mne nakonec zavedl na toto[ENTER]temné místo. "
gameforge.ghost_story._370_npcChat = "Znáte příběh o jeskyni? "
gameforge.ghost_story._380_say = "Nevim, nejspíše jsem měl jenom smůlu. Poté co[ENTER]jsem dezertoval jsem se chtěl ukrýt v horách a[ENTER]narazil jsem na tuto jeskyni. Hned od začátku se[ENTER]mi zdála trochu podivná, ale byla pohodlná a[ENTER]zdála se být zcela opuštěná. Pár dní na to jsem[ENTER]se stal svědkem a zároveň obětí neuvěřitelné[ENTER]bitvy. Pokud se ti to zalíbilo, mohu ti o tom[ENTER]vyprávět. "
gameforge.ghost_story._390_npcChat = "Začátek Bitvy "
gameforge.ghost_story._400_say = "Jednoho dne jsem sbíral kořínky na ubočí skal,[ENTER]když tu najednou se z nebe snesl stříbrný drak.[ENTER]Myslel jsem si, že je po mě, ale on mne neviděl a[ENTER]zamířil ke vchodu do jeskyně. Aniž by zpomalil,[ENTER]snesl se dolů do údolí a vletěl do jeskyně. "
gameforge.ghost_story._410_say = "A pak bylo na chvíli ticho a já nevěděl co dělat.[ENTER]Veškeré moje vybavení bylo v jeskyni a já se[ENTER]samozřejmě nechtěl s drakem potkat! A pak se země[ENTER]zachvěla a z hlubin jeskyně se ozval dutý řev.[ENTER]Ale k mému zděšení jsem zjistil, že to nebyl řev[ENTER]jeden ale dva! Krátce na to z jeskyně vystřelili[ENTER]dva draci, stříbrný drak následován modrým[ENTER]drakem. "
gameforge.ghost_story._420_say = "Téměř nepopsatelná bitva mezi draky. Drásali svá[ENTER]těla v těsném sevření a bořili své zuby do krku[ENTER]protivníka... pršela krev... ale ani jeden[ENTER]nehodlal povolit své sevření ani na vteřinu. "
gameforge.ghost_story._430_npcChat = "Konec Bitvy "
gameforge.ghost_story._440_say = "Oba draci válcovali hory jako bouře, která[ENTER]vyvolává sesuvy a chaos. Blesk popálil tlamu[ENTER]modrého draka, ledové kroupy zase proděravěly[ENTER]křídla stříbrného draka. Tlusté masy ledu, které[ENTER]pokrývaly zem po mnoho let se během tohoto boje[ENTER]změnily na blátivé zbytky. Schovával jsem se za[ENTER]skálou, zkamenělý ale neschopný ignorovat boj,[ENTER]který se přede mnou odehrával. "
gameforge.ghost_story._450_say = "Trvalo několik hodin, než jejich moc začala[ENTER]slábnout. A pak jsem konečně viděl svou[ENTER]příležitost, odtrhl jsem se od scény a běžel jsem[ENTER]údolím do jeskyně pro svoje vybavení. A tak byl[ENTER]můj osud zpečetěn. Byl jsem již na cestě zpět,[ENTER]když oba draci vystřelili z hor, modrý drak před[ENTER]stříbrným drakem. Modrý drak utrpěl strašlivá[ENTER]zranění. "
gameforge.ghost_story._460_say = "Těsně před zmizením do jeskyně ho stříbrný drak[ENTER]chytil a pak se stalo něco zvláštního: Obloha[ENTER]začala probleskovat, zvedl se prudký vítr a[ENTER]vytvořil se vír energie, do kterého byl modrý[ENTER]drak chycen.[ENTER]Modrý drak začal řvát bolestí a vztekem... začal[ENTER]se smršťovat, řev se stával slabším a jeho šupiny[ENTER]ztratily lesk, zmatněly a vybledly. "
gameforge.ghost_story._470_say = "Nakonec stříbrný drak pustil modrého draka zpět[ENTER]do jeskyně. Z posledních zbytků sil se poražený[ENTER]drak dovlekl pod horu. Avšak, stříbrný drak[ENTER]pokračoval v uvolňování mocné magické bouře,[ENTER]která zapečetila jeskyni magickou bariérou a[ENTER]která sesula část hory. Temný drak a já jsme byli[ENTER]v pasti. Odsouzen k mizerné a mučivé smrti[ENTER]hladem... "
gameforge.ghost_story._480_npcChat = "Již jsem slyšel dost. "
gameforge.ghost_story._490_say = "Děkuji za vyslechnutí. Posledních tisíc let jsem[ENTER]byl opravdu osamělý... "
gameforge.goldbar = {}
gameforge.goldbar._100_say = "Než jsem unikl černému větru, přišel jsem na to,[ENTER]kde skrývají zlato. Měli tam celý stoh zlatých[ENTER]prutů! Ani by si nevšimli, když by tam nějaké[ENTER]chyběli. Takoví hlupáci to jsou.[ENTER]Jestli mi zaplatíš, půjdu a donesu ti prut zlata.[ENTER]Ale nemůžu ti přinést víc než jeden prut najednou. "
