gameforge.goldbar._20_sayTitle = "Zrádce Balso: "
gameforge.goldbar._210_say = "Tady je tvůj zlatý prut. Udělal jsi moudré[ENTER]rozhodnutí. Dělat investice je dobrá myšlenka. "
gameforge.goldbar._220_say = "Nemáš dost Yangů![ENTER]Stojí 2,2 miliónů Yangů. "
