gameforge.goldbar._270_say = "Co to povídáš? O tom nic nevím. "
gameforge.goldbar._280_say = "Cože? Ty chceš ten prut zlata prodat? Ty´s to[ENTER]ještě neslyšel? Bojovníci černého větru teď[ENTER]podezřívají každého. Když prodáš ten prut zlata[ENTER]teď, nebudeš už dlouho naživu. Nejlepší bude,[ENTER]když ho necháš tam, kde je. Dobře ho schovej! "
gameforge.goldbar._290_sayReward = "Teď nemůžeš prodávat žádné pruty zlata. "
gameforge.goldbar._300_say = "Jestli teď ten prut zlata chceš vrátit, "
gameforge.goldbar._30_say = "Cože? Oběť černého větru? Ha ha ha![ENTER]Vypadáš jako dítě. Kdepak jsi to slyšel?[ENTER]Běž si hrát s divokými psy! "
gameforge.goldbar._310_say = "musíš počkat zhruba %s %s. "
gameforge.goldbar._320_say = "Ty nemáš žádné pruty zlata.[ENTER]Když mi přineseš pruty zlata, dám ti za ně Yang. "
gameforge.goldbar._330_say = "Cože?[ENTER]Ty chceš vrátit tvůj prut zlata? Proč jsi ho teda[ENTER]vůbec kupoval? Pokud ho chceš vrátit, nic proti[ENTER]tomu nemám. Černý vítr je na stráži, proto tam[ENTER]nemůžu zas tak často. Ještě pořád ho chceš[ENTER]vrátit? "
