gameforge.goldbar._370_say = "Máš vůbec nějaké pruty zlata?[ENTER]Mám dost na práci. Nechoď mi sem, když nechceš[ENTER]dělat kšefty. "
gameforge.goldbar._40_sayReward = "Úkol 'Oběť bandy Černého větru' je k dispozici[ENTER]pouze pro postavy na 30. nebo vyšší úrovni. "
gameforge.goldbar._50_say = "Dávej si pozor! Válečníci Černého větru jsou[ENTER]velmi podezřívaví. Nejlepší bude se chovat úplně[ENTER]klidně. Žij blaze. "
gameforge.goldbar._60_sayReward = "Úkol 'Oběť bandy Černého větru' není momentálně k[ENTER]dispozici. "
gameforge.goldbar._70_say = "Pšššt![ENTER]Tady můžou být schovaní válečníci Černého větru.[ENTER]Kde´s to slyšel?[ENTER]No nic, už to je v poho. Není to zas tak[ENTER]důležité. Ale ty víš, co se děje? "
