gameforge.locale.monster_chat = {}
gameforge.locale.monster_chat_1001_1 = "Hehehee! Všichni mají zemřít. Hijááá!! "
gameforge.locale.monster_chat_1001_2 = "Kousek, kousek, kousek, kousek! "
gameforge.locale.monster_chat_1001_3 = "Není úniku! "
gameforge.locale.monster_chat_1001_4 = "Země bude nasáklá tvou krví! Zemři! "
gameforge.locale.monster_chat_1001_5 = "Klidně žebrej, ale nepřinese ti to nic dobrého! "
gameforge.locale.monster_chat_1001_6 = "Copak to tu máme za obličej, znetvořený strachem! Hehé! "
gameforge.locale.monster_chat_1002_1 = "Smrt! Smrt všem lidem! "
gameforge.locale.monster_chat_1002_2 = "Mizerná prasata! "
gameforge.locale.monster_chat_1002_3 = "Lezou mi fakt na nervy. "
gameforge.locale.monster_chat_1002_4 = {}
gameforge.locale.monster_chat_1002_5 = "Tady poteče krev, človíčku. "
gameforge.locale.monster_chat_1003_1 = "Smrt a zatracení! "
gameforge.locale.monster_chat_1003_2 = "Oštěp je horší než meč, co? "
gameforge.locale.monster_chat_1003_3 = "Hej, neutíkejte! Tak pojďte, pojďte! "
gameforge.locale.monster_chat_1003_4 = "Sbíhají se mi sliny v puse, jenom když se na tebe podívám. "
gameforge.locale.monster_chat_1003_5 = "Ty mě provokuješ. "
gameforge.locale.monster_chat_1003_6 = "Zemřeš! "
gameforge.locale.monster_chat_1004_1 = "Tvá duše bude moje, človíčku! "
gameforge.locale.monster_chat_1004_2 = "Mmmm, čerstvá krev! "
gameforge.locale.monster_chat_1004_3 = "Dneska bude veselo! "
gameforge.locale.monster_chat_1004_4 = "Ne, ještě ne! Nesmíš ještě umřít. Chachaha! "
gameforge.locale.monster_chat_1004_5 = "Co? Co se to tam děje? Třese se ti šíleně ruka. "
gameforge.locale.monster_chat_1004_6 = "To je fakt odvážné jít k nám na návštěvu! Nic si z toho nedělej! "
gameforge.locale.monster_chat_1035_1 = "Než umřu, pomstím se! "
gameforge.locale.monster_chat_1035_2 = "Budeš umírat a trpět velmi, velmi pomalu... "
gameforge.locale.monster_chat_1035_3 = "Bude mi potěšením nechat tě trpět. "
gameforge.locale.monster_chat_1035_4 = "Hahaha! Tělo se ti samým strachem třese. Hahaha! "
gameforge.locale.monster_chat_1035_5 = "Cítím tvůj strach, maličký.... "
gameforge.locale.monster_chat_1035_6 = "Co to je? Vypadá to, že každou chvíli spadne pár rukou na zem. To už je fakt konec? "
gameforge.locale.monster_chat_1036_1 = "S kým půjdeš do pekla? "
gameforge.locale.monster_chat_1036_2 = "Hajzlové! Vy na mě nezapomenete! "
gameforge.locale.monster_chat_1036_3 = "Hááhahahaha! "
gameforge.locale.monster_chat_1036_4 = "Žebrej o vlastní život, vykup ho vlastní duší! "
gameforge.locale.monster_chat_1036_5 = {}
gameforge.locale.monster_chat_1036_6 = "Vidíš všechno rozmazaně! Nemůžeš už ani rozlišovat vlevo a vpravo. Hááhahahaha! "
gameforge.locale.monster_chat_1037_1 = "Proč nebloumáš na věky věků takhle kolem? "
gameforge.locale.monster_chat_1037_2 = "Copak copak? Nemáš rád tmu? "
gameforge.locale.monster_chat_1037_3 = "Vítej v pekle. Hááhahahaha! "
gameforge.locale.monster_chat_1037_4 = "Jistě, jistě. Jen si křič! "
gameforge.locale.monster_chat_1037_5 = "Hááhahahaha! Takhle mě nemůžeš zabít! "
gameforge.locale.monster_chat_1037_6 = "Jo, jo, to už je lepší! "
gameforge.locale.monster_chat_1038_1 = "Tohle je peklo. "
gameforge.locale.monster_chat_1038_2 = "Vítej v pekle. "
gameforge.locale.monster_chat_1038_3 = "Umírej pěkně pomalu. "
gameforge.locale.monster_chat_1038_4 = "Nemá smysl mě napadat. Hahaha! "
gameforge.locale.monster_chat_1038_5 = {}
gameforge.locale.monster_chat_1038_6 = "Není dobré umřít příliš rychle. Chceme si předtím ještě trochu užít. "
gameforge.locale.monster_chat_1039_1 = "Do té doby, než přijde někdo jiný... "
gameforge.locale.monster_chat_1039_2 = "Myslíš, že můžeš uniknout? "
gameforge.locale.monster_chat_1039_3 = "Všechno, co je tady, nahání lidem strach... Hodně moc strachu! "
gameforge.locale.monster_chat_1039_4 = "Vítej na místě, kde najdeš svou smrt! "
gameforge.locale.monster_chat_1039_5 = "Vidím ti v očích paniku. "
gameforge.locale.monster_chat_1039_6 = "Hmmm, tak mladé a tak bezmocné... "
gameforge.locale.monster_chat_1040_1 = "Vidíme každý tvůj pohyb. "
gameforge.locale.monster_chat_1040_2 = "Spěcháš? Opravdu chceš zemřít? "
gameforge.locale.monster_chat_1040_3 = "Smrt si již našla místo kousek od tebe... "
gameforge.locale.monster_chat_1040_4 = "Slyšíš, jak zvoní můj zvon? Je to poslední věc, kterou uslyšíš! "
gameforge.locale.monster_chat_1040_5 = "Když tahle rána nepřestane krvácet, bude to pěkně nebezpečné. "
gameforge.locale.monster_chat_1040_6 = "Můžeš bojovat... nebo můžeš nechat prostě Smrt, aby si tě vzala rovnou... "
gameforge.locale.monster_chat_1041_1 = "Proč jsi zde, když chceš žít? "
gameforge.locale.monster_chat_1041_2 = "Hmmm, čerstvé maso do mlýnku.... "
gameforge.locale.monster_chat_1041_3 = "Bylo to tvé rozhodnutí vydat se sem, lidský červe! A nyní si poneseš za to následky... "
gameforge.locale.monster_chat_1041_4 = "My zase vstaneme z mrtvých, my nemůžeme zemřít. "
gameforge.locale.monster_chat_1041_5 = "Nemá smysl nás napadat. Hááhahahaha! "
gameforge.locale.monster_chat_1041_6 = "Co tě víc zmůže: Vyčerpání nebo smrt? "
gameforge.locale.monster_chat_1091_1 = "Dostal jsi se až sem... a já tě teď pošlu za tvou Smrtí... "
gameforge.locale.monster_chat_1091_2 = "Obětuj svou duši a staň se jedním z nás! "
gameforge.locale.monster_chat_1091_3 = "Koukni se na sebe! Slabý a pomalý.... "
gameforge.locale.monster_chat_1091_4 = "Tvoje tělo a tvoje duše patří mně! Hááhahahaha! "
gameforge.locale.monster_chat_1091_5 = "Už jsi vyčerpaný? Třesou se ti kolena! "
gameforge.locale.monster_chat_1091_6 = "Jak dlouho to ještě vydržíš? Hááhahahaha! "
gameforge.locale.monster_chat_1093_1 = "Kdo mohl tušit, že existuje ještě něco mnohem horšího než smrt? "
gameforge.locale.monster_chat_1093_2 = "Vítej! "
gameforge.locale.monster_chat_1093_3 = "Za svou troufalost zaplatíš. Zaplatíš tvou krví a tvou duší! "
gameforge.locale.monster_chat_1093_4 = "Občerstvím se tvým tělem a tvou duší! "
gameforge.locale.monster_chat_1093_5 = "Jak dlouho myslíš, že mi můžeš odolávat? "
gameforge.locale.monster_chat_1093_6 = "Smrt se ti směje za zády. Hehehe "
gameforge.locale.monster_chat_11000_1 = "Všichni, kdo si děláte starosti o další vývoj, naslouchejte! "
gameforge.locale.monster_chat_11000_10 = "Jestli chceš umět ovládat moc přírody - obzvláště blesků, doporučuji ti nauku o léčení! "
gameforge.locale.monster_chat_11000_11 = "Jednotliví válečníci se odlišují tím, jakým způsobem vedou boje. Soustřeď se na nepřetržité zocelování těla! "
gameforge.locale.monster_chat_11000_12 = "Magie je silnou stránkou Surů. Pracujte na vaší inteligenci, rychlosti a silném těle! "
gameforge.locale.monster_chat_11000_13 = "Využívej vlastností Ninjů! Soustřeď se na rychlost a tělesnou sílu! "
gameforge.locale.monster_chat_11000_14 = "Šamani využívají znovuobnovení, podporu a kouzla v boji na dálku. Na to je potřeba inteligence! "
gameforge.locale.monster_chat_11000_15 = "Nezapomeň, co ses naučil! "
gameforge.locale.monster_chat_11000_16 = "Běž na výcvik, pokud chceš trénovat tvé schopnosti! "
gameforge.locale.monster_chat_11000_17 = "Schopnosti? Můžeš se vypracovat až na úroveň mistra, která se náhodně nachází mezi úrovněmi 17 a 21! "
gameforge.locale.monster_chat_11000_18 = "Chceš se stát velmistrem? V tom případě potřebuješ extra příručku o schopnostech! "
gameforge.locale.monster_chat_11000_19 = "Příručka o schopnostech zmizí, jakmile ji dočteš! "
gameforge.locale.monster_chat_11000_2 = "Všichni válečníci, co nedosáhli úroveň 5, poslouchejte! "
gameforge.locale.monster_chat_11000_20 = "Chceš si přečíst další knihu schopností? Pak ale musíš počkat jeden den. "
gameforge.locale.monster_chat_11000_21 = "Vylepši inteligneci a zlepši schopnosti tvého Sury! "
gameforge.locale.monster_chat_11000_22 = "Zažij v boji velkou moc, kterou oplývají Surové! "
gameforge.locale.monster_chat_11000_23 = "Víš, kde se nachází chrám ezoterické sekty? Leží uprostřed údolí Seungryong! "
gameforge.locale.monster_chat_11000_24 = "Byl znovu objeven vchod do pavoučího podzemí! Nachází se v poušti Yongbi! "
gameforge.locale.monster_chat_11000_25 = "Dokonči úkol a použij odměnu na vylepšení! "
gameforge.locale.monster_chat_11000_26 = "Chytat ryby? To zní dobře! Ty už jsi přece dostal u rybáře udici a návnadu - nebo ne? "
gameforge.locale.monster_chat_11000_27 = "Chceš najít něco silnějšího, co bys chtěl honit? Vyhledej si portál! Běž na místo, na kterém se nalézají nestvůry které odpovídají tvé úrovni! "
gameforge.locale.monster_chat_11000_28 = "Dávej si pozor na nepřátele z jiných zemí. Nikdy nevíš, kdy ti vpadnou do zad! "
gameforge.locale.monster_chat_11000_29 = "Chceš obnovit svoje schopnosti? Tak běž ke stařence! Postarej se o to, abys měl u sebe dostatek Yangů. "
gameforge.locale.monster_chat_11000_3 = "Když se chceš proslavit jako mocný útočník, běž k učiteli boje tělo na tělo. "
gameforge.locale.monster_chat_11000_30 = "Usiluj o dokonalou úroveň mistra. Pak budeš patřit k opravdovým hrdinům! "
gameforge.locale.monster_chat_11000_31 = "Hledej staré lidi na všech kontinentech, jestli chceš vstoupit do jiného města nebo bojového území! "
gameforge.locale.monster_chat_11000_4 = "Zvol si výcvik v mentálním boji, jestli se chceš stát odborníkem ve schopnostech válečníka! "
gameforge.locale.monster_chat_11000_5 = "Pokud chceš patřit k Surům, kteří ovládají magické meče, zvol si výcvik v magických zbraních! "
gameforge.locale.monster_chat_11000_6 = "Přihlaš se na výcvik v černé magii, jestli se chceš stát odborníkem v používání nekalých kouzel! "
gameforge.locale.monster_chat_11000_7 = "Ve výcviku v boji na dálku jsou akceptováni Ninjové, kteří dosáhli úrovně 5! "
gameforge.locale.monster_chat_11000_8 = "Zvol si výcvik v boji zblízka, jestli se chceš stát Ninjou, který působí skrytě! "
gameforge.locale.monster_chat_11000_9 = "Seznam se s naukou o dračí moci, pokud chceš jako válečník dračího boha ovládat všechny bitvy! "
gameforge.locale.monster_chat_11002_1 = "Všichni, kdo si děláte starosti o vaše zdraví, naslouchejte! "
gameforge.locale.monster_chat_11002_10 = "Doporučuji nauku o léčení, pokud chceš umět ovládat moc přírody a obzvlášť blesků! "
gameforge.locale.monster_chat_11002_11 = "Jednotliví válečníci se odlišují tím, jakým způsobem vedou boje. Soustřeď se se na nepřetržité trénování těla! "
gameforge.locale.monster_chat_11002_12 = "Nejsilnější stránkou Surů je magie. Pracuj bez přestání na své inteligenci, rychlosti a tělesné síle! "
