gameforge.locale.monster_chat_11004_4 = "Zvol si výcvik v mentálním boji, jestli se chceš stát odborníkem ve schopnostech válečníka! "
gameforge.locale.monster_chat_11004_5 = "Pokud chceš patřit k Surům, kteří ovládají magické meče, zvol si výcvik v magických zbraních! "
gameforge.locale.monster_chat_11004_6 = "Přihlaš se na výcvik v černé magii, jestli se chceš stát odborníkem v používání nekalých kouzel! "
gameforge.locale.monster_chat_11004_7 = "Ve výcviku v boji na dálku jsou akceptováni Ninjové, kteří dosáhli úrovně 5! "
gameforge.locale.monster_chat_11004_8 = "Zvol si výcvik v boji zblízka, jestli se chceš stát Ninjou, který působí skrytě! "
gameforge.locale.monster_chat_11004_9 = "Seznam se s naukou o dračí moci, pokud chceš jako válečník dračího boha ovládat všechny bitvy! "
gameforge.locale.monster_chat_1105_1 = "Zamrzlý stav je dobrý. "
gameforge.locale.monster_chat_1105_2 = "Hora Sohan je nepochybně nejlepší. "
gameforge.locale.monster_chat_1105_3 = "Jaké krásné počasí. "
gameforge.locale.monster_chat_1105_4 = "Udělám z tebe mého dvojníka. "
gameforge.locale.monster_chat_1105_5 = "Můžeš žít věčně - aspoň dokud neroztaje ten led... "
gameforge.locale.monster_chat_1105_6 = "Jak dlouho to bude trvat, než z tebe bude kus ledu? "
gameforge.locale.monster_chat_1106_1 = "Lidé... tady? "
gameforge.locale.monster_chat_1106_2 = "Jsem unavený... ale na tebe budu stačit... "
gameforge.locale.monster_chat_1106_3 = "Někdo je na mém kopci?  "
gameforge.locale.monster_chat_1106_4 = "Ty sem nepatříš! "
gameforge.locale.monster_chat_1106_5 = "Měl bys raději zmizet! "
gameforge.locale.monster_chat_1106_6 = "Cizinec... a co tak sám? "
gameforge.locale.monster_chat_1191_1 = "Už je to dlouho, co jsem viděla zkušeného válečníka. "
gameforge.locale.monster_chat_1191_2 = "Není co dělat... jen čekat a leštit zbraně... "
gameforge.locale.monster_chat_1191_3 = "Mám na nich pracovat? "
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Něco chybí. Nejsem spokojená. "
gameforge.locale.monster_chat_1191_6 = "Ukončím tu bolest, kterou bys jinak cítil na věky! "
gameforge.locale.monster_chat_1301_1 = "Máš dlouhý krk! "
gameforge.locale.monster_chat_1301_2 = "Tvoje bledá kůže bude brzy ještě bledší! "
gameforge.locale.monster_chat_1301_3 = "Ty mne chceš vyzvat? No prosím... "
gameforge.locale.monster_chat_1301_4 = "Ty´s mě napadl! Pvou! Není ti dovoleno přežít! "
gameforge.locale.monster_chat_1301_5 = "Slabý, slabý člověk! "
gameforge.locale.monster_chat_1301_6 = "Tak se podíváme, co sneseš! "
gameforge.locale.monster_chat_1302_1 = "Chceš zmizet? Ani na to nemysli! "
gameforge.locale.monster_chat_1302_2 = "Správně. Jen se přibliž. Pvou! "
gameforge.locale.monster_chat_1302_3 = "Meč v mé ruce se jenom třese. Pvou! "
gameforge.locale.monster_chat_1302_4 = "Tenhle útok nemůžeš přežít! "
gameforge.locale.monster_chat_1302_5 = "Tumáš, můj meč! "
gameforge.locale.monster_chat_1302_6 = "Bolí to, že jo? Hahahá! "
gameforge.locale.monster_chat_1303_1 = "Dokonce nešťastnou náhodou pošlapaná tráva může zabíjet. "
gameforge.locale.monster_chat_1303_2 = "Dávej si na tu trávu pozor. "
gameforge.locale.monster_chat_1303_3 = "Správně. Tak, pěkně ke mně. Hahahá "
gameforge.locale.monster_chat_1303_4 = "Ničemo! Přišel jsi ke mně a já tě teď zabiju! "
gameforge.locale.monster_chat_1303_5 = "Zemři! Zemři! "
gameforge.locale.monster_chat_1303_6 = "Lituješ toho, že´s přišel? "
gameforge.locale.monster_chat_1305_1 = "Kdo mě přemůže? "
gameforge.locale.monster_chat_1305_2 = "Ty? Chceš si to se mnou rozdat? "
gameforge.locale.monster_chat_1305_3 = "Tak pojd', jestli chceš! "
gameforge.locale.monster_chat_1305_4 = "Pojd', rozdáme si to! Nechod' pryč! "
gameforge.locale.monster_chat_1305_5 = "Tak co, jaký mám meč? "
gameforge.locale.monster_chat_1305_6 = "Bojíš se? Hahahá "
gameforge.locale.monster_chat_1901_1 = "Myslím že dnes zemřeš mýma rukama! "
gameforge.locale.monster_chat_1901_2 = "Hmm, ty se mi líbíš. "
gameforge.locale.monster_chat_1901_3 = "Tak co? Moje milé rozptýlení... "
gameforge.locale.monster_chat_1901_4 = "Hej! To ti nepomůže, když utečeš jako zbabělec! "
gameforge.locale.monster_chat_1901_5 = "Už jsem ti učaroval? Hm? "
gameforge.locale.monster_chat_1901_6 = "Ty už si nechceš ani trošku zakřičet? Hahahá! "
gameforge.locale.monster_chat_20001_1 = "Vypadáš nervózně... neboj, nic tu nevybouchne. "
gameforge.locale.monster_chat_20001_2 = "Čas od času bych chtěl jít jen tak na procházku. "
gameforge.locale.monster_chat_20001_3 = "Je tu taková nuda. Ani moje vnoučata mě už nenavštěvují. "
gameforge.locale.monster_chat_20001_4 = "Když člověk zestárne, potřebuje společnost. "
gameforge.locale.monster_chat_20001_5 = "Moje nejlepší doby už pominuly. "
gameforge.locale.monster_chat_20002_1 = "To slunce tak pálí. "
gameforge.locale.monster_chat_20002_2 = "Nádherné počasí na to, usušit mokré plátno, že? "
gameforge.locale.monster_chat_20002_3 = "Kdepak je ten líný budižkničemu, co si říká manžel? "
gameforge.locale.monster_chat_20002_4 = "Říká si otec a ani před svými dětmi se za svoje vrtochy nestydí! "
gameforge.locale.monster_chat_20002_5 = "Knihy jsou knihy. Nechodí do práce a nic nevydělá. Pořád jenom čte! "
gameforge.locale.monster_chat_20003_1 = "Jéé, ťuťuťu, miminko. Pěkně ses vyspinkal? "
gameforge.locale.monster_chat_20003_2 = "Směješ se jako andílek. "
gameforge.locale.monster_chat_20003_3 = "Tvoje maminka má radost, že jsi tak zdravoučký. "
gameforge.locale.monster_chat_20003_4 = "Nikdo si tě nevšímá? Máš hlad? "
gameforge.locale.monster_chat_20003_5 = "Hodné děťátko, naše děťátko. Spi děťátko, spi. "
gameforge.locale.monster_chat_20004_1 = "Prostě jenom nový den... "
gameforge.locale.monster_chat_20004_2 = "Hmm, kde asi dostanu dneska najíst? "
gameforge.locale.monster_chat_20004_3 = "Tihle chlápci s mečema jsou stále tak nudní... "
gameforge.locale.monster_chat_20004_4 = "Uvidíme... možná něco vyžebrám u Octavia. "
gameforge.locale.monster_chat_20004_5 = "Jo, a mimochodem: už prý nezuří žádná válka... "
gameforge.locale.monster_chat_20005_1 = "Duše mistra hrnčíře protéká skrz konečky jeho prstů. "
gameforge.locale.monster_chat_20005_2 = "Od té doby, co je země zavalená chaosem, se všude poflakují bojovníci. "
gameforge.locale.monster_chat_20005_3 = "Každý chce pořád bojovat. "
gameforge.locale.monster_chat_20005_4 = "Modlím se za klidný a pokojný svět. "
gameforge.locale.monster_chat_20005_5 = "Hej, ty tam! Udělej si aspoň na chvilku čas vypnout a zamyslet se. "
gameforge.locale.monster_chat_20006_1 = "Panebože, co teď budu dělat? "
gameforge.locale.monster_chat_20006_2 = "Po mém bratru není ani vidu ani slechu a přitom říkal, že přijde. "
gameforge.locale.monster_chat_20006_3 = "A teď tady vidím jenom tyhle bojovníky s těmi meči. "
gameforge.locale.monster_chat_20006_4 = "Stýská se mi po mém bratrovi. "
gameforge.locale.monster_chat_20006_5 = "Viděl někdo mého bratra? "
gameforge.locale.monster_chat_20007_1 = "Hmmm, čerstvé maso! "
gameforge.locale.monster_chat_20007_2 = "To je dobře, že lidi zase jedí víc masa. "
gameforge.locale.monster_chat_20007_3 = "Kšefty rostou, ale masa je málo. "
gameforge.locale.monster_chat_20007_4 = "Kdy konečně dorazí to maso, co jsme objednali? "
gameforge.locale.monster_chat_20007_5 = "Když nedojde zavčas, musíme jít na lov! "
gameforge.locale.monster_chat_20008_1 = "Doufám, že při takovém počasí se nám zákazníci jen pohrnou. "
gameforge.locale.monster_chat_20008_2 = "Ten líný budižkničemu už určitě brzo přijde a bude chtít něco k jídlu. "
gameforge.locale.monster_chat_20008_3 = "Panebože, copak to je za ubožáka! "
gameforge.locale.monster_chat_20008_4 = "Dřív to byl jeden hodně dobrý válečník. "
gameforge.locale.monster_chat_20008_5 = "Ptám se sám sebe, proč už nebojuje? "
gameforge.locale.monster_chat_20009_1 = "Ti mladí chlapíci jsou dost čilí a jenom září vitalitou! "
gameforge.locale.monster_chat_20009_2 = "Bojovníci vypadají mnohem líp, když nosí zbraně. "
gameforge.locale.monster_chat_20009_3 = "Tenhle vražedný duch zaplaví každého. "
gameforge.locale.monster_chat_20009_4 = "Ovládej své srdce, spočiň ve vlastních myšlenkách a uklidni svou duši. "
gameforge.locale.monster_chat_20009_5 = "Neexistuje žádný zázračný recept, ale je několik postupů, které lze následovat. "
gameforge.locale.monster_chat_20010_1 = "Přinesl jsem jeden dobrý předmět! "
gameforge.locale.monster_chat_20010_2 = "Zboží ze vzdálených zemí! Překrásné, vyjímečné kousky! "
gameforge.locale.monster_chat_20010_3 = "Všichni sem, pojd'te všichni sem! "
gameforge.locale.monster_chat_20010_4 = "Kupujte, kupujte dokud je ještě něco k mání. To neseženete všude a rozhodně ne každý den! "
gameforge.locale.monster_chat_20010_5 = "Mimochodem, proč je tady tolik zlodějů? "
gameforge.locale.monster_chat_20011_1 = "V dnešní době je tady dost sucho. "
gameforge.locale.monster_chat_20011_2 = "Úroda obilí už není tak vydatná jako dřív. "
gameforge.locale.monster_chat_20011_3 = "Co zpomaluje růst na polích? "
gameforge.locale.monster_chat_20011_4 = "To obilí chutná jako mořská voda. "
gameforge.locale.monster_chat_20011_5 = "To je špatné znamení. Zemědělství je základ všeho. "
gameforge.locale.monster_chat_20012_1 = "Rýžové buchty! Vynikající rýžové buchty! "
gameforge.locale.monster_chat_20012_2 = "Prodávám sladké rýžové buchty! "
gameforge.locale.monster_chat_20012_3 = "Ach jo! Obchody váznou. "
gameforge.locale.monster_chat_20012_4 = "Možná bych to měla zkusit v jiném městě. "
gameforge.locale.monster_chat_20012_5 = "Ale tam je mnoho divokých predátorů... "
gameforge.locale.monster_chat_20013_1 = "Nejlepší je, když člověk stráví takový den v člunu. "
gameforge.locale.monster_chat_20013_2 = "Perfektní počasí na chytání ryb. "
gameforge.locale.monster_chat_20013_3 = "A teď další uzlík... Tak! Žádná ryba z tohohle háčku neuteče! "
gameforge.locale.monster_chat_20013_4 = "Tenhle zatracený medvěd mi zničil udici. "
gameforge.locale.monster_chat_20013_5 = "Měl bych znovu zkouknout své sítě. "
gameforge.locale.monster_chat_20014_1 = "Hůů! "
gameforge.locale.monster_chat_20014_2 = "Nedělej si kvůli mně žádné starosti. Já jsem prostě takový. "
gameforge.locale.monster_chat_20014_3 = "Áááááá! "
gameforge.locale.monster_chat_20014_4 = "Co musím udělat, aby... Panebože, co jsem to právě řekla? "
gameforge.locale.monster_chat_20014_5 = "Vůbec neumím být statečný. Hu! "
gameforge.locale.monster_chat_20015_1 = "Tahle sekera mi už 20 let prokazuje věrnou službu. "
gameforge.locale.monster_chat_20015_2 = "Je ještě pořád ostrá, ale už ne tak dobrá jako dřív. "
gameforge.locale.monster_chat_20015_3 = "Chtěl bych ji posílit tím, že ji budu používat a s ní sekat. "
gameforge.locale.monster_chat_20015_4 = "Ale na to potřebuju několik kamenů, které obsahují minerály. "
gameforge.locale.monster_chat_20015_5 = "Ale jak se k nim dostanu? "
gameforge.locale.monster_chat_20016_1 = "Zákazníci se ke mně jenom hrnou. "
gameforge.locale.monster_chat_20016_2 = "Doufám, že vše dokončím včas. "
gameforge.locale.monster_chat_20016_3 = "Vypadá zachmuřeně. "
gameforge.locale.monster_chat_20016_4 = "Takhle má znít kladivo! "
gameforge.locale.monster_chat_20016_5 = "Tady není ticho. "
gameforge.locale.monster_chat_20017_1 = "Nemám na hudbu náladu. "
gameforge.locale.monster_chat_20017_2 = "Můj hudební nástroj je v hrozném stavu. "
gameforge.locale.monster_chat_20017_3 = "Možná bych si měl pořídit nový nástroj? "
gameforge.locale.monster_chat_20017_4 = "Můj nástroj je starý a hodně použitý... mám rád staré věci! "
gameforge.locale.monster_chat_20017_5 = "Doufám, že najdu něco, čím bych mohl ty struny opravit. "
gameforge.locale.monster_chat_20018_1 = "Mám toho tolik na práci! "
gameforge.locale.monster_chat_20018_2 = "Každý den přijde tolik zraněných. "
gameforge.locale.monster_chat_20018_3 = "Nemám vůbec čas si vyjít nebo se s někým setkat! "
gameforge.locale.monster_chat_20018_4 = "A my nemáme léky nazbyt. "
gameforge.locale.monster_chat_20018_5 = "Přeju si, aby někdo sehnal pár přísad do léků. "
gameforge.locale.monster_chat_20019_1 = "30 let se potuluju s mým lukem po horách. "
gameforge.locale.monster_chat_20019_2 = "Uživil jsem tím ženu a zaplatil vzdělání mých třech dětí. "
gameforge.locale.monster_chat_20019_3 = "Bylo to těžké, ale stálo to za to. "
gameforge.locale.monster_chat_20019_4 = "Ale teď se přetrhla tětiva luku. Znamená to, že patřím do starého železa? "
gameforge.locale.monster_chat_20019_5 = "Jestli se mi nepodaří ten luk spravit, tak si budu muset koupit nový. Ale na to nemám peníze. "
gameforge.locale.monster_chat_20020_1 = "Hej! Co čumíš? "
gameforge.locale.monster_chat_20020_2 = "Vůbec se sem nepřibližuj! "
gameforge.locale.monster_chat_20020_3 = "Ach, jak dlouho se ještě musím takhle schovávat? "
gameforge.locale.monster_chat_20020_4 = "Zemřu sám, zapomenutý někde v koutě? "
gameforge.locale.monster_chat_20020_5 = "Ááá, doufám, že mi někdo pomůže! "
gameforge.locale.monster_chat_20021_1 = "Tenhle den skončí tak jako každý jiný. "
gameforge.locale.monster_chat_20021_2 = "Co teď můžu asi tak udělat? Hmm? "
gameforge.locale.monster_chat_20021_3 = "Nikdy bych nepomyslela, že takhle skončím. "
gameforge.locale.monster_chat_20021_4 = "Pořád nemohu uvěřit tomu, že on tu již není. "
gameforge.locale.monster_chat_20021_5 = "To si nezasloužil. "
gameforge.locale.monster_chat_20022_1 = "A zase nový pěkný den. "
gameforge.locale.monster_chat_20022_2 = "Manžel Ariyoung zmizel. "
gameforge.locale.monster_chat_20022_3 = "Chtěl jsem pomoct, ale tahle zatvrzelost... "
gameforge.locale.monster_chat_20022_4 = "Kdyby si mě vzala, mohla uniknout jejímu dosavadnímu nudnému životu. "
gameforge.locale.monster_chat_20022_5 = "Možná jsem jí měl dát víc najevo, co k ní cítím. "
gameforge.locale.monster_chat_20023_1 = "Proboha, ne! Co budeme dělat, když na to přijde moje stará? "
gameforge.locale.monster_chat_20023_2 = "Kdybych tak mohl žít jenom ze čtení knížek! "
gameforge.locale.monster_chat_20023_3 = "Knihy jsou tak tajuplné... "
gameforge.locale.monster_chat_20023_4 = "Nemůžu takhle žít napořád, ale... "
gameforge.locale.monster_chat_20023_5 = "Doufám, že pro moji ženu můžu něco udělat, ale... "
gameforge.locale.monster_chat_20024_1 = "Tak dělej, dělej! Trochu víc usilí! "
gameforge.locale.monster_chat_20024_2 = "Chlape, co to tam provádíš? Nejde to rychlejc? "
gameforge.locale.monster_chat_20024_3 = "Představ si válečníka tasícího meč a udělej to také! "
gameforge.locale.monster_chat_20024_4 = "Kde je zas ten blázen? "
gameforge.locale.monster_chat_20024_5 = "Chceš se stát mužem? "
gameforge.locale.monster_chat_2091_1 = "Střih, střih.... vlasy, hrdla... "
gameforge.locale.monster_chat_2091_2 = "Něco pro tebe mám: věčný spánek! "
gameforge.locale.monster_chat_2091_3 = "Zhluboka se nadechni. A neboj, je to jen jed! "
gameforge.locale.monster_chat_2091_4 = "Nechám si jen tvou tvář! To bude pěkná dekorace! "
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Všechno pevně omotám hedvábím a pak to chutná lépe. "
gameforge.locale.monster_chat_2092_1 = "Už nás mnoho nezbývá. "
gameforge.locale.monster_chat_2092_2 = "Nejsou to žádné dvounohé příšery, kterým by bylo souzeno zde vládnout. "
gameforge.locale.monster_chat_2092_3 = "Áá! Vypadá to, že se někdo chytil do pavoučí sítě. "
gameforge.locale.monster_chat_2092_4 = "Jsem kliďas. Jen nemám rád, když jsem vyrušován. "
gameforge.locale.monster_chat_2092_5 = "To je vše, co dokážeš? "
gameforge.locale.monster_chat_2092_6 = "Nikdo se se mnou nemůže měřit! "
gameforge.locale.monster_chat_2104_1 = "Proč jsou lidé zevnitř mokří? Tak mi jen zrezaví meč! "
gameforge.locale.monster_chat_2104_2 = "Už ti vyschl jed v žihadle? "
gameforge.locale.monster_chat_2104_3 = "Co si takhle zabojovat? "
gameforge.locale.monster_chat_2104_4 = "Už jsi tím vedrem vyčerpaný? "
gameforge.locale.monster_chat_2104_5 = "Rád nechávám lidi v poušti. "
gameforge.locale.monster_chat_2104_6 = "Chudáku, jen tak zahrabaný v poušti. "
gameforge.locale.monster_chat_2105_1 = "Prozradím ti tajemství. Nenávidím špínu. "
gameforge.locale.monster_chat_2105_2 = "To je horko, co? "
gameforge.locale.monster_chat_2105_3 = "Tohle místo je nebezpečné. "
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "Trefím tě přesně mezi očima. "
gameforge.locale.monster_chat_2106_1 = "Ssss... cítím něco k ssssnědku. "
gameforge.locale.monster_chat_2106_2 = "Vidím kořisssst! "
gameforge.locale.monster_chat_2106_3 = "Sssskvělé! Líbí sssse mi to! "
gameforge.locale.monster_chat_2106_4 = "Tenhle je pěkně tlusssstý. Ten je můj! "
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Miluju vůni čerssstvé krve. "
gameforge.locale.monster_chat_2107_1 = "Sssss! Není co zabít! "
gameforge.locale.monster_chat_2107_2 = "Ssss! Tihle jsou tlussstí! "
gameforge.locale.monster_chat_2107_3 = "To sssse bude vůdci líbit... "
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Ssss! Do toho! "
gameforge.locale.monster_chat_2107_6 = "Ten šššššíp je jen pro tebe! "
gameforge.locale.monster_chat_2108_1 = "Když ho načapám, tak... "
gameforge.locale.monster_chat_2108_2 = "Proč se tak rozhlížíš? "
gameforge.locale.monster_chat_2108_3 = "Ty si myslíš, že se nade mnou dá lehce vyhrát? Co? "
gameforge.locale.monster_chat_2108_4 = "Taková smůla, potkat tady v poušti zrovna mě! "
gameforge.locale.monster_chat_2108_5 = "Hmm, to je pocit! Vychutnám si ho na věky věků! "
gameforge.locale.monster_chat_2108_6 = "Děkuji pěkně! "
gameforge.locale.monster_chat_2191_1 = "Cítím lidi. Jaká to libá vůně! "
gameforge.locale.monster_chat_2191_2 = "Dobře. Pojd'te všichni ke mně, pojd'te, pojd'te... "
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "To mě těší, někoho potkat tady v poušti! "
gameforge.locale.monster_chat_2191_5 = "Okamžitě zůstat stát! "
gameforge.locale.monster_chat_2191_6 = "Vyrvu ti maso kousek po kousku... "
gameforge.locale.monster_chat_2202_1 = "Pojď blíž, ať ti mohu spálit kůži. "
gameforge.locale.monster_chat_2202_2 = "Víš, jaký druh bolesti může způsobit takový žár? "
gameforge.locale.monster_chat_2202_3 = "Možná se můžu ochladit jejich krví. "
gameforge.locale.monster_chat_2202_4 = "Tvoje krev.... nech mě ochutnat! "
gameforge.locale.monster_chat_2202_5 = "Není důvod začínat boj. "
gameforge.locale.monster_chat_2202_6 = "Staň se to co já! Jen plameny a žár. "
gameforge.locale.monster_chat_2204_1 = "Dokud jsem tady já, nikdo se nedostane dovnitř! "
gameforge.locale.monster_chat_2204_2 = "Z nějakého důvodu jsi mi na obtíž. "
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Plump, víš, kde se nacházíš? "
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Jsi pro mne směšný protivník. "
gameforge.locale.monster_chat_2205_1 = "Meč je užitečný jenom v rukou válečníka. "
gameforge.locale.monster_chat_2205_2 = "Drž meč pořád pěkně v ruce. "
gameforge.locale.monster_chat_2205_3 = "Nastává válka. "
gameforge.locale.monster_chat_2205_4 = "A teď budeme bojovat na smrt! "
gameforge.locale.monster_chat_2205_5 = "Chceš ještě dlouho odolávat útokům? "
gameforge.locale.monster_chat_2205_6 = "Ano, neměl bys umřít tak rychle. "
gameforge.locale.monster_chat_2206_1 = "Láva je elixír života. "
gameforge.locale.monster_chat_2206_2 = "Láva mne drží naživu - tobě přinese smrt! "
gameforge.locale.monster_chat_2206_3 = "Není tenhle svět bublající lávy nádherný? "
gameforge.locale.monster_chat_2206_4 = "Ty máš odvahu mne rušit? "
gameforge.locale.monster_chat_2206_5 = "Měl bys odejít, pokud si vážíš života. "
gameforge.locale.monster_chat_2206_6 = "Budeš litovat mé přítomnosti. "
gameforge.locale.monster_chat_2291_1 = "Nádherné počasí. Bylo by krásné trochu si pospat. "
gameforge.locale.monster_chat_2291_2 = "Nic se nemůže vyrovnat zemi Doyyumhwan. "
gameforge.locale.monster_chat_2291_3 = "Troška čerstvé krve by byla vítaná změna. "
gameforge.locale.monster_chat_2291_4 = "Ty musíš hledat smrt, jinak bys mne nerušil.  "
gameforge.locale.monster_chat_2291_5 = "Vypadá to, že lituješ toho, že´s mě vyrušil. "
gameforge.locale.monster_chat_2291_6 = "Teď už je pozdě, abys utekl. Stejně by ti to nepomohlo. "
gameforge.locale.monster_chat_301_1 = "Já se tak nudím. Copak tady není nic zajímavého na práci? "
gameforge.locale.monster_chat_301_2 = "Před dvěma dny tu byla roztrhána divokými zvířaty mladá dívka. "
gameforge.locale.monster_chat_301_3 = "Divoká zvířata jsou čím dál tím víc agresivní. Měly bychom jich pár pobít. "
gameforge.locale.monster_chat_301_4 = "Dnes nemám dobrou náladu. Cizinci by se měli raději klidit... "
gameforge.locale.monster_chat_301_5 = "Poslední válečník, který zemřel mojí rukou, žebral o život... "
gameforge.locale.monster_chat_302_1 = "Někdo by měl s těmi kameny už něco dělat! "
gameforge.locale.monster_chat_302_2 = "Nedůvěřuji tomu klidu ráno! "
gameforge.locale.monster_chat_302_3 = "To napětí ve vzduchu mě ničí! "
gameforge.locale.monster_chat_302_4 = "Tyhle hlavice šípů byly vyrobeny poctivě! "
gameforge.locale.monster_chat_302_5 = "Vidíš? Míří ti přímo na srdce. "
gameforge.locale.monster_chat_302_6 = "Svatá dobroto! Trefa do černého! "
gameforge.locale.monster_chat_303_1 = "Dobrý bojový výcvik je v dnešní době nezbytný. "
gameforge.locale.monster_chat_303_2 = "Vojsko už dále nezvládá chaos v zemi. "
gameforge.locale.monster_chat_303_3 = "Proč se na mě tak díváš? "
gameforge.locale.monster_chat_303_4 = "Zbabělci mají malou šanci na přežití! "
gameforge.locale.monster_chat_303_5 = "Nepřeceňuj se. "
gameforge.locale.monster_chat_303_6 = "Tenhle boj bude těžší než jsem očekávala! "
gameforge.locale.monster_chat_304_1 = "Dokonce i mí předkové se uplatnili v bitvách na tomto území. "
gameforge.locale.monster_chat_304_2 = "Tady musíš být obezřetný a zkušený. "
gameforge.locale.monster_chat_304_3 = "Lezeš mi na nervy. "
gameforge.locale.monster_chat_304_4 = "Tak jak se cítíš, když je tvůj život v nebezpečí? "
gameforge.locale.monster_chat_304_5 = "No ne! To už to vzdáváš? "
gameforge.locale.monster_chat_304_6 = "Utrpení přijde nenadále. "
gameforge.locale.monster_chat_305_1 = "Sakra! Ten meč vypadá skvěle! "
gameforge.locale.monster_chat_305_2 = "Zvyš svoji odolnost, svoji sílu! "
gameforge.locale.monster_chat_305_3 = "Ty si myslíš, že můžeš být tak silný jako já? "
gameforge.locale.monster_chat_305_4 = "No dobrá. Tak to teda vezmeme vážně? "
gameforge.locale.monster_chat_305_5 = "Panebože! Jsem jenom slabá žena! "
gameforge.locale.monster_chat_305_6 = "Vypadáš unaveně. Proč to jednoduše nevzdáš? Chachacha! "
gameforge.locale.monster_chat_306_1 = "Co bude Serangyi dneska asi dělat? "
gameforge.locale.monster_chat_306_2 = "Strašně se nudím. Hraj si se mnou! "
gameforge.locale.monster_chat_306_3 = "Já sama dávám přednost chlápkům z řad Surů... "
gameforge.locale.monster_chat_306_4 = "Hej! Co bys řekl na boj? "
gameforge.locale.monster_chat_306_5 = "Nepodceňuj mě jenom proto, že jsem žena! "
gameforge.locale.monster_chat_306_6 = "Bolest trvá jenom chviličku. "
gameforge.locale.monster_chat_307_1 = "Chci odsud pryč, pro všechno na světě. "
gameforge.locale.monster_chat_307_2 = "Jak bezpečné jsou domovy jednotlivých členů? Hodně? "
gameforge.locale.monster_chat_307_3 = "Holky, držte huby! "
gameforge.locale.monster_chat_307_4 = "Nech mě, vyřídím tě jednou ránou. "
gameforge.locale.monster_chat_307_5 = "Na to, že utečeš, ani nepomysli. "
gameforge.locale.monster_chat_307_6 = "No tak, na co ještě čekáme? "
gameforge.locale.monster_chat_394_1 = "A nezapomeňte na to: všichni muži jsou nepřátelé. "
gameforge.locale.monster_chat_394_2 = "Když někdo bude mít milý obličej, můžeme být na něj trochu hodnější. "
gameforge.locale.monster_chat_394_3 = "Cože, ty chceš bojovat? "
gameforge.locale.monster_chat_394_4 = "Nebude to trvat dlouho, ale bude to pěkně bolet. "
gameforge.locale.monster_chat_394_5 = "Hmm, vypadá to, že máš pěkné bolesti. "
gameforge.locale.monster_chat_394_6 = "Sbohem, táhněte do pekla! "
gameforge.locale.monster_chat_401_1 = "Tak co, co děláš u klanu Bílé přísahy? "
gameforge.locale.monster_chat_401_2 = "Kampak máš namířeno? "
gameforge.locale.monster_chat_401_3 = "Hej! Chceš bojovat? "
gameforge.locale.monster_chat_401_4 = "Hehe, ty se jenom vyhýbáš mým ranám. "
gameforge.locale.monster_chat_401_5 = "Ty si myslíš, že můžeš uniknout? "
gameforge.locale.monster_chat_401_6 = "Tady máš ránu! "
gameforge.locale.monster_chat_402_1 = "Bože, jsem tak unavený! "
gameforge.locale.monster_chat_402_2 = "Hmm! Pěkné brnění. Stálo hodně? "
gameforge.locale.monster_chat_402_3 = "Ale máme přece pěkné počasí. "
gameforge.locale.monster_chat_402_4 = "Á! Vypadá to, že jsi opravdu trénoval. Výborně! "
gameforge.locale.monster_chat_402_5 = "Jak ti připadají moje schopnosti? Dobré, že? Chachacha! "
gameforge.locale.monster_chat_402_6 = "Co se děje? Samozřejmě! To je ta šílená rychlost! "
gameforge.locale.monster_chat_403_1 = "Proč nejdu lovit? "
gameforge.locale.monster_chat_403_2 = "Dneska nikdo neútočí na naši vesnici. "
gameforge.locale.monster_chat_403_3 = "Jestli chceš bojovat, bojuj! Mizero! "
gameforge.locale.monster_chat_403_4 = "Chachacha! Ty jsi přece jenom pouhý amatér! "
gameforge.locale.monster_chat_403_5 = "Mám tě nejdřív zasáhnout do nohy nebo do ruky? "
gameforge.locale.monster_chat_403_6 = "Pozor - vystřeluju šíp! "
gameforge.locale.monster_chat_404_1 = "Někdy příště musím jít do vesnice a trénovat. "
gameforge.locale.monster_chat_404_2 = "Jsem úplně ztuhlý. "
gameforge.locale.monster_chat_404_3 = "Co je dneska k jídlu? "
gameforge.locale.monster_chat_404_4 = "Pozoruju, takže si řvi jak chceš. "
gameforge.locale.monster_chat_404_5 = "Váhal jsi moc dlouho, co? Ze všeho nejdřív pěkně švihnout mečem! "
gameforge.locale.monster_chat_404_6 = "Ten meč má takový vzrušující zvuk! "
gameforge.locale.monster_chat_405_1 = "Tenhle meč je připraven začít! "
gameforge.locale.monster_chat_405_2 = "Uvidíme, koho ten meč skolí jako první! "
gameforge.locale.monster_chat_405_3 = "Hej! Co tak čumíš? "
gameforge.locale.monster_chat_405_4 = "Nejdřív tvoje břicho! "
gameforge.locale.monster_chat_405_5 = "Copak není pochva standard pro bojovníka s mečem? "
gameforge.locale.monster_chat_405_6 = "Chtěl bych mít tebe jako pochvu na meč. "
gameforge.locale.monster_chat_406_1 = "Hynu hlady! "
gameforge.locale.monster_chat_406_2 = "Máme tady něco k jídlu? "
gameforge.locale.monster_chat_406_3 = "Jak to, že mám po tom všem jídle pořád ještě hlad? "
gameforge.locale.monster_chat_406_4 = "Áááá, zemři! "
gameforge.locale.monster_chat_406_5 = "Můj žaludek rozhodne o tomto boji! "
gameforge.locale.monster_chat_406_6 = "Je to volba mezi nebem a zemí! "
gameforge.locale.monster_chat_491_1 = "Už to nevydržím! Brní mě celé tělo! "
gameforge.locale.monster_chat_491_2 = "A jo. Vidím tam několik prasat. "
gameforge.locale.monster_chat_491_3 = "Na zem? "
gameforge.locale.monster_chat_491_4 = "Vyřídím tě jednou ranou. "
gameforge.locale.monster_chat_491_5 = "To bude pěkná vzpomínka! "
gameforge.locale.monster_chat_491_6 = "Zemři! "
gameforge.locale.monster_chat_492_1 = "Změklo mi úplně tělo. "
gameforge.locale.monster_chat_492_2 = "Proč netrénuju víc? "
gameforge.locale.monster_chat_492_3 = "Strach? Co? "
gameforge.locale.monster_chat_492_4 = "Áá, nová oběť mojí vířící zbraně! "
gameforge.locale.monster_chat_492_5 = "Vidíš moje schéma útoku? Ne? Tvá chyba! "
gameforge.locale.monster_chat_492_6 = "Věděl jsi, že i malá řezná rána škodí zdraví? "
gameforge.locale.monster_chat_493_1 = "Sakra, to je tak nepraktické mít takovouhle ruku. "
gameforge.locale.monster_chat_493_2 = "Co tak koukáš? To´s ještě nikdy neviděl nikoho s takovouhle rukou? "
gameforge.locale.monster_chat_493_3 = "Vy lidi mi opravdu jdete na nervy. "
gameforge.locale.monster_chat_493_4 = "Mám tě zmlátit? Jestli ne, tak radši rychle uteč, jo? "
gameforge.locale.monster_chat_493_5 = "Teď demonstruji moji velkou ránu! "
gameforge.locale.monster_chat_493_6 = "Vím, že zíráš na moji ruku! "
gameforge.locale.monster_chat_494_1 = "Jestli chceš bojovat, přijd' sem! "
gameforge.locale.monster_chat_494_2 = "Připadám si absolutně úchvatný. "
gameforge.locale.monster_chat_494_3 = "Proč něco nenaplánujeme? "
gameforge.locale.monster_chat_494_4 = "Ne, ještě není dovoleno jít pryč. Bojuj! "
gameforge.locale.monster_chat_494_5 = "No dobrá, tady to máš! "
gameforge.locale.monster_chat_494_6 = "Seš skoro mrtvý. "
gameforge.locale.monster_chat_501_1 = "Hraj si se mnou! Zůstaň tady! "
gameforge.locale.monster_chat_501_2 = "Uměl bych vyrobit něco pěkného z tvých kostí... "
gameforge.locale.monster_chat_501_3 = "Ještě jsme netrénovali. "
gameforge.locale.monster_chat_501_4 = "Hmm, copak to je za krásné brnění! To chci mít. "
gameforge.locale.monster_chat_501_5 = "Od ted'ka bude pro tebe těžké, ignorovat moje útoky! "
gameforge.locale.monster_chat_501_6 = "Chachacha! Překvapený, že? "
gameforge.locale.monster_chat_502_1 = "Krása není vše. "
gameforge.locale.monster_chat_502_2 = "Nezírat, bojovat! "
gameforge.locale.monster_chat_502_3 = "Nesnáším, když na mě zíráš. Pojd' sem! "
gameforge.locale.monster_chat_502_4 = "To je čest, zemřít ranou mého meče! "
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "Výborně! "
gameforge.locale.monster_chat_503_1 = "Ty tam, nedotýkat se! "
gameforge.locale.monster_chat_503_2 = "Vypadá to, jako by byla tětiva mého luku přeříznutá! "
gameforge.locale.monster_chat_503_3 = "Sakra. Přišli tihle idioti. "
gameforge.locale.monster_chat_503_4 = "To nebylo špatné. Ale přesto zemřeš! "
gameforge.locale.monster_chat_503_5 = "Takže s tím souhlasíš? To je opravdu paráda! "
gameforge.locale.monster_chat_503_6 = "Tady máš šíp! "
gameforge.locale.monster_chat_504_1 = "Pojďte všichni sem! "
gameforge.locale.monster_chat_504_2 = "La, lala, pískám si písničku... "
gameforge.locale.monster_chat_504_3 = "Jaképak nádherné počasí. "
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Dobře! Oceňuji tvoji ochotu bojovat! "
gameforge.locale.monster_chat_504_6 = "Pojďte sem! Všichni ke mě! "
gameforge.locale.monster_chat_601_1 = "Urgh! Já mít hlad! "
gameforge.locale.monster_chat_601_2 = "Ja chtít trochu jídla. "
gameforge.locale.monster_chat_601_3 = "Já cítit jídlo! Útok! "
gameforge.locale.monster_chat_601_4 = "Neutíkat! Zůstat stát! "
gameforge.locale.monster_chat_601_5 = "Já vyhrát! To jasné! "
gameforge.locale.monster_chat_601_6 = "Já vidět! Ty minout! "
gameforge.locale.monster_chat_601_7 = "Ty dávat víc pozor! Cha! "
gameforge.locale.monster_chat_602_1 = "Hrmpf... kde to já teď být? "
gameforge.locale.monster_chat_602_2 = "Dnes být jasná obloha. "
gameforge.locale.monster_chat_602_3 = "Ty chtít chtít ulovit mě? Ghar ghar, to být směšné! "
gameforge.locale.monster_chat_602_4 = "Ty být má kořist! "
gameforge.locale.monster_chat_602_5 = "Ty cítít bolest? Já skončit to rychle! "
gameforge.locale.monster_chat_602_6 = "Ty vidět jak já silný! Mávat silným kyjem jako nic! "
gameforge.locale.monster_chat_603_1 = "Já nudit se. Já chtít bojovat! "
gameforge.locale.monster_chat_603_2 = "Mě svrbět celé tělo. "
gameforge.locale.monster_chat_603_3 = "Já uvidět, kdo být rychlý! "
gameforge.locale.monster_chat_603_4 = "Ty začít! Já zabít! "
gameforge.locale.monster_chat_603_5 = "My bojovat! Ty neutíkat! "
gameforge.locale.monster_chat_603_6 = "Já mít poslední rána! Zadupat tě do zem! "
gameforge.locale.monster_chat_604_1 = "Já mít nový jed... ty chtít vyzkoušet? "
gameforge.locale.monster_chat_604_2 = "Já teď vidět účinek! Sežer si to! "
gameforge.locale.monster_chat_604_3 = "Ty! Ty být má pokusná zajíc? "
gameforge.locale.monster_chat_604_4 = "Já otrávit tebe! "
gameforge.locale.monster_chat_604_5 = "Ghar! To pokusná zajíc! "
gameforge.locale.monster_chat_604_6 = "Mě nebavit, když ty rychle vzdát! "
gameforge.locale.monster_chat_631_1 = "Čekat! Já hledat něco na lov. "
gameforge.locale.monster_chat_631_2 = "Ty pomalý! Neplazit se jako had! "
gameforge.locale.monster_chat_631_3 = "Já mít takový hlad! "
gameforge.locale.monster_chat_631_4 = "Ghar! Já najít něco na lov! "
gameforge.locale.monster_chat_631_5 = "Ty prohrát! Ty vzdát se! "
gameforge.locale.monster_chat_631_6 = "Teď ty zažít opravdová bolest! "
gameforge.locale.monster_chat_632_1 = "Nuda! "
gameforge.locale.monster_chat_632_2 = "Být tady co použít jako návnada? "
gameforge.locale.monster_chat_632_3 = "Ty jít sem a bojovat! "
gameforge.locale.monster_chat_632_4 = "Ty chtít být válečník? Ty ukázat jak dobrý! "
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "Ty utíkat, jestli chtít zůstat naživu! "
gameforge.locale.monster_chat_633_1 = "Umřít od hlad, to hloupá smrt. "
gameforge.locale.monster_chat_633_2 = "Ghar! Oni přicházet! "
gameforge.locale.monster_chat_633_3 = "Ty chtít bojovat, červ?! "
gameforge.locale.monster_chat_633_4 = "Ty se smát mě?! Já zabít! "
gameforge.locale.monster_chat_633_5 = "Teď přijít velká rána! Ghar! "
gameforge.locale.monster_chat_633_6 = "Kdo teď zdrhat, nebýt válečník! "
gameforge.locale.monster_chat_634_1 = "Teď ten jed být opravdu dobrý! "
gameforge.locale.monster_chat_634_2 = "Když mít hlad, netopýr na oheň dobrá! "
gameforge.locale.monster_chat_634_3 = "Ty chtít bojovat? "
gameforge.locale.monster_chat_634_4 = "Když můj jed v tvá tělo, ty nemít šanci! Ghar! "
gameforge.locale.monster_chat_634_5 = "Má kouzla všechno spálit! Ghar ghar ghar! "
gameforge.locale.monster_chat_634_6 = "Hoř! Ghar ghar! "
gameforge.locale.monster_chat_635_1 = "Vy mít ostré meče! Čekat na nás další boj! "
gameforge.locale.monster_chat_635_2 = "Ty unaven? Já dorazit! "
gameforge.locale.monster_chat_635_3 = "Chcípni! "
gameforge.locale.monster_chat_635_4 = "Ty mě porazit? Nebýt tak jistý! "
gameforge.locale.monster_chat_635_5 = "Ghar! Ty nebojovat jako chlap! "
gameforge.locale.monster_chat_635_6 = "Ty přidat! "
gameforge.locale.monster_chat_636_1 = "Ghar! To být zábavný boj! "
gameforge.locale.monster_chat_636_2 = "Já mít rád boj! Ghar ghar! "
gameforge.locale.monster_chat_636_3 = "Bojovat! Pořád a pořád! Ghar! "
gameforge.locale.monster_chat_636_4 = "Ghar! Ty zkusit znovu! "
gameforge.locale.monster_chat_636_5 = "Ty vidět jak svištět můj meč! "
gameforge.locale.monster_chat_636_6 = "Tebe mrzet, že zasáhnout mě! "
gameforge.locale.monster_chat_636_7 = "Ty být slabý! Ty být červ! "
gameforge.locale.monster_chat_637_1 = "Ty neotravovat, jinak chcípnout. "
gameforge.locale.monster_chat_637_2 = "Ja tebe uspat. Navždy! "
gameforge.locale.monster_chat_637_3 = "Ty stále nechtít padnout? "
gameforge.locale.monster_chat_637_4 = "My chtít boj! Já nečekat! "
