gameforge.main_quest_lv93._260_say = "Hledá tě Yon-I-Walker. "
gameforge.main_quest_lv93._270_say = "Jsi tady, výborně. Potřebuji tvou pomoc. Abych[ENTER]mohl můj nápoj úplně dokončit, potřebuji ještě[ENTER]jednu přísadu: Esenci milénia. "
