gameforge.main_quest_lv93._470_say = "Dobře, hned se vydám na cestu. Kde je vlastně[ENTER]všechny najdu? "
gameforge.main_quest_lv93._480_say = "Tyto rostliny nalezneš v různých částech[ENTER]kontinentu. Zatímco budeš hledat, já se budu[ENTER]věnovat přípravám. "
