gameforge.new_quest_lv26._032_say = "No, víš... já tu nežiji dobrovolně. Musím se[ENTER]skrývat, protože jsem utekl před vojenskou[ENTER]povinností. Moc si přeji, aby moje milovaná žena[ENTER]Ah-Yu věděla, že jsem stále živ. Mohl bys jí[ENTER]donést ode mne zprávu? Ale nezapomeň, že je to[ENTER]jen pro Ah-Yu! Ona ti na oplátku řekne, kde jsme[ENTER]se prvně políbili - tam se s ní chci opět potkat[ENTER]a také je to místo pro skvělý lov! "
gameforge.new_quest_lv26._034_say_title = "Nakajima: "
gameforge.new_quest_lv26._035_say_title = "Ah-Yu: "
gameforge.new_quest_lv26._036_say_title = "Hledej Nakajimu "
gameforge.new_quest_lv26._037_say = "Nakajima by mohl znát nějaké dobré místo na lov.[ENTER]Najdi ho. "
gameforge.new_quest_lv26._038_say = "Nakajima je v %s. "
gameforge.new_quest_lv26._040_say = "Ty jsi nakajima? Yang-Shin mne za tebou posílá.[ENTER]Hledá nějaké dobré místo pro lov a ty bys prý[ENTER]mohl o nějakém vědět? "
gameforge.new_quest_lv26._050_target = "Ah-Yu: "
gameforge.new_quest_lv26._051_say = "Nakajima je zběh a musí se před vojáky skrývat.[ENTER]Jeho žena Ah-Yu si myslí, že padl ve válce. Dej[ENTER]jí vědět, že Nakajima je stále živ. "
gameforge.new_quest_lv26._052_say = "Ah-Yu je v %s. "
gameforge.new_quest_lv26._057_say = "Nakajima... je naživu? Můj drahý manžel žije?[ENTER]Nemohu ti dost poděkovat za tu zprávu! Prvně sme[ENTER]se políbili v údolí Yonjang, hned se tam za ním[ENTER]vypravím. "
gameforge.new_quest_lv26._058_say = "Díky, že jsi nám umožnil opětovné shledání. Vem[ENTER]si tuto růži - třeba ji dáš svojí lásce? A teď[ENTER]mne omluv - opravdu spěchám! "
gameforge.new_quest_lv26._061_say = "Údolí Yonjang, kde se Nakajima a Ah-Yu prvně[ENTER]políbili, je stále velmi dobré místo k lovu. "
gameforge.new_quest_lv26._062_say = "Yang-Shin bude potěšen, až se to dozví. Jdi do %s[ENTER]a řekni mu tu dobrou zprávu. "
gameforge.new_quest_lv26._063_say = "Údolí Yonjang? To zní slibně, dokonce jsem ještě[ENTER]neslyšel, že by tam padaly kameny Metin. Tam se[ENTER]pravděpodobně bude skvěle lovit! A tady - tady je[ENTER]tvá odměna. "
gameforge.new_quest_lv26._064_sayReward = "Obdržíš 35 000 bodů zkušenosti. "
gameforge.new_quest_lv26._065_sayReward = "Obdržíš 17 500 Yangů. "
