gameforge.new_quest_lv28._020_say_title = "Yu-Hwan: "
gameforge.new_quest_lv28._021_say = "Znáš Ah-Yu? Sbírá byliny pro mé lektvary. Bývala[ENTER]v dobré náladě, ale včera se to úplně změnilo.[ENTER]Nechtěla mi říci, co ji trápí - možná proto, že[ENTER]pro mě pracuje? Prosím, zajdi za ní a zeptej se,[ENTER]co ji trápí. "
gameforge.new_quest_lv28._022_say = "Od Yu-Hwana je moc hezké, že má o mne takovou[ENTER]starost. Ale pokud mě neco trápí... Nechtěla jsem[ENTER]o tom mluvit, ale už mě to tíží opravdu moc.[ENTER]Je to o lovci Yang-Shinovi. Od té doby, co[ENTER]zjistil, že můj manžel Nakajima je zběh mě[ENTER]vydírá. On ho klidně udá a pak Nakajimu zabijí... "
gameforge.new_quest_lv28._023_say = "Tentokrát chce medvědí pracku výměnou za své[ENTER]mlčení. Ale já teď nemohu jít na lov. Mohl bys mi[ENTER]pomoci a sehnat, co Yang-Shin žádá? "
gameforge.new_quest_lv28._024_set_title = "Lov medvědů "
gameforge.new_quest_lv28._025_notice = "Tak jdeme lovit! Nejlepší cesta, jak získat[ENTER]medvědí pracku, je zabít Beru! "
gameforge.new_quest_lv28._026_notice = "Mám medvědí pracku! "
gameforge.new_quest_lv28._027_notice = "To by mělo být vše. Tak za Yang-Shinem! "
