gameforge.new_quest_lv28._035_target = "Yang-Shin: "
gameforge.new_quest_lv28._036_say = "Máš požadovaný počet medvědích pracek. Dones je[ENTER]Yang-Shinovi, abys zaplatil za Ah-Yu. "
gameforge.new_quest_lv28._037_say = "Yang-Shin je v %s. "
gameforge.new_quest_lv28._038_say_title = "Yang-Shin: "
gameforge.new_quest_lv28._040_npcChat = "Vydírání "
gameforge.new_quest_lv28._041_say_title = "Medvědí pracky "
gameforge.new_quest_lv28._042_say = "Tys mi donesl medvědí pracky? Prosím, nemysli si[ENTER]o mně nic špatného! Žijeme v těžkých časech a já[ENTER]musím živit rodinu, a v těhle dobách je těžké[ENTER]něco ulovit, když kameny Metin proměnily zvířata[ENTER]ve vraždící bestie. Život lovce nebyl nikdy tak[ENTER]nebezpečný. "
gameforge.new_quest_lv28._043_say = "Prosím, řekni Ah-Yu, že se už nemá čeho obávat.[ENTER]Teda - do doby, než zase budu něco potřebovat...[ENTER]hehehe! "
gameforge.new_quest_lv28._044_say = "Cože, ty mě chceš napálit a nemáš pracky? Já sem[ENTER]chtěl pěkně čerstvé! "
gameforge.new_quest_lv28._045_say_reward = "Je od tebe hezké, že pomáháš Ah-Yu. Ale také to[ENTER]znamená, že musíš udělat co ti řeknu. Takže teď[ENTER]jdi na lov a nevracej se dřív, než budeš mít dvě[ENTER]medvědí pracky! "
gameforge.new_quest_lv28._046_say = "Vyřiď Ah-Yu, že Yang-Shin ji již nebude dále[ENTER]vydírat, protože jsi mu pomohl. "
gameforge.new_quest_lv28._051_say = "Ty jsi můj spasitel! Jsem ti neskonale vděčná.[ENTER]Tady, vezmi si všechny mé Yangy. Není to mnoho,[ENTER]ale ty sis to zasloužil. "
gameforge.new_quest_lv28._055_1_select = "Díky, to se hodí! "
gameforge.new_quest_lv28._055_2_select = "Ne, to nemohu přijmout. "
gameforge.new_quest_lv28._056_say = "Je to vše co mám, ale stojí mi to za to, když se[ENTER]zbavím toho barbarského hrdlořeza! "
gameforge.new_quest_lv28._057_say = "Ach, děkuji! Tvé místo po boku Dračího boha je[ENTER]již jistě připraveno! "
