gameforge.new_quest_lv29._024_say = "Nemám čas na převýchovu nezbedného děcka. Musíš[ENTER]si ho chytit sám. I když pochybuji, že to[ENTER]dokážeš... "
gameforge.new_quest_lv29._030_target = "Taurean: "
gameforge.new_quest_lv29._031_say = "Rodinné dědictví Yonaha, vázy z Seladonského[ENTER]porcelánu, jsou rozbité a Yonah si myslí, že to[ENTER]udělal Taurean. Jdi za Taureanem a zjisti, jak to[ENTER]bylo. "
