gameforge.new_quest_lv29._034_say_reward = "Proč se na mě tak díváš? Nic jsem neudělal... "
gameforge.new_quest_lv29._035_say_reward = "Co jsi udělal s Yonahovými vázami? "
gameforge.new_quest_lv29._036_say_reward = "Vázy? Yonah? Vůbec nevím, o čem mluvíš! "
gameforge.new_quest_lv29._037_say_reward = "No tak mi prosímtě vysvětli, proč jsou tři z nich[ENTER]rozbité. "
gameforge.new_quest_lv29._038_say_reward = "Přísahám, že jsem dnes nebyl nikde poblíž těch[ENTER]váz a také jsem ani neviděl Yonaha. Celý den jsem[ENTER]bojoval s Harang. Dokonce mě zamknula ve stodole![ENTER]Jdi a zeptej se jí, pokud mi nevěříš! "
gameforge.new_quest_lv29._040_target = "Harang: "
gameforge.new_quest_lv29._041_say = "Taurean tvrdí, že je nevinný. Hrál si s Harang[ENTER]celý den a nemohl ty vázy rozbít. Dojdi za Harang[ENTER]a zeptej se jí, zda je to pravda. "
