gameforge.new_quest_lv29._043_say_title = "Harang: "
gameforge.new_quest_lv29._044_say_reward = "Taureana? Ano, hráli jsme si celý den. Děje se[ENTER]něco? "
gameforge.new_quest_lv29._045_say_reward = "Yonah tvrdí, že mu Taurean rozbil tři vázy. "
gameforge.new_quest_lv29._046_say_reward = "Yonah je zlý. Pronásleduje mě a Taureana kdykoliv[ENTER]se k němu přiblížíme. Vždy se tak bojí o svůj[ENTER]porcelán. Jediná osoba, která se smí jeho[ENTER]porcelánu dotknout je pacholek. Možná ty vázy[ENTER]rozbil on? "
gameforge.new_quest_lv29._047_say = "Harang je v %s. "
gameforge.new_quest_lv29._050_target = "Pacholek: "
gameforge.new_quest_lv29._051_say = "Harang potvrdila, že Taurean je nevinný. Ale také[ENTER]mohou být spolu domluveni. Před tím, než vyneseš[ENTER]rozsudek, by sis měl promluvit s pacholkem. "
gameforge.new_quest_lv29._052_say = "Ach, starý dobrý Yonah. Tak se bojí o svůj[ENTER]porcelán, že dokonce pronásleduje vesnické děti.[ENTER]Nějak si neuvědomuje, že kameny Metin mají vliv[ENTER]na celou zemi - a dokonce i na suroviny, protože[ENTER]ty už také nejsou to co bývaly. Dokonce i[ENTER]porcelán začal praskat. Ale Yonah tomu prostě[ENTER]nechce věřit... "
gameforge.new_quest_lv29._053_say = "Kdybys mu nasbíral nějaké seno, mohl by si jeho[ENTER]porcelán lépe uskladnit v truhlách. Myslím, že by[ENTER]to byla dobrá cesta, jak ho ochránit. "
gameforge.new_quest_lv29._054_say = "Pacholek je v[ENTER]%s nebo %s. "
gameforge.new_quest_lv29._061_say = "Taurean není zodpovědný za rozbití Yonahových[ENTER]váz. Je to zhoubný vliv kamenů Metin, který dělá[ENTER]věci křehčími. Nasbírej pro Yonaha nějaké seno,[ENTER]aby si mohl lépe uložit svůj porcelán. Jsem si[ENTER]jist, že ti vesničané nějaké dají. "
gameforge.new_quest_lv29._062_say = "Můžeš najít %s a %s u bran do vesnice. Měl bys[ENTER]být schopen získat slušné množství sena z[ENTER]klobouků, které nosí. "
gameforge.new_quest_lv29._063_notice = "Z toho bude pěkná hromada sena. Ale pořád mi[ENTER]ještě chybí %s. "
