gameforge.new_quest_lv29._065_1_say = "Koukám, že máš u sebe pěknou hromadu sena. Jsem[ENTER]si jist, že Yonah ztichne, když mu ji dáš. "
gameforge.new_quest_lv29._065_notice = "Ten klobouk není z obilí, ale z kokosových[ENTER]vláken. A také je docela poškozený... "
gameforge.new_quest_lv29._066_say = "Můžeš také lovit divoké pěšáky nebo silné divoké[ENTER]pěšáky, z jejich klobouků lze získat seno. "
gameforge.new_quest_lv29._067_say_reward = "Již jsi získal %s z %s balíků sena. "
gameforge.new_quest_lv29._068_notice = "Získal jsi balík sena. "
gameforge.new_quest_lv29._070_npcChat = "Balík sena "
gameforge.new_quest_lv29._071_say_reward = "Taurean, ty jsi předtím říkal, že jsi byl ve[ENTER]stodole. Nevzal sis odtud nějaké seno? "
gameforge.new_quest_lv29._072_say_reward = "Haha, podívejme... to vše jsem měl ve vlasech a[ENTER]botách. Vidíš? Tady je ještě také něco, na, vezmi[ENTER]si to! "
