gameforge.new_quest_premium_lv4._270_say_reward = "Yangy: %d "
gameforge.new_quest_premium_lv4._280_say = "Bez pižmového oleje nemohu vyrobit parfém.[ENTER]Prosím, klikni na točící se minci a objednej jej[ENTER]pro mne. "
gameforge.new_quest_premium_lv4._290_say_reward = "Vrať se zpět s %s x pižmovým olejem! "
gameforge.new_quest_premium_lv4._300_say = "Potřebuji donést pižmový olej obchodníkovi se[ENTER]zbraněmi. Měla by mi pomoci točící se mince,[ENTER]abych objednal tuto přísadu a pak ji vyzvedl u[ENTER]Správce skladiště. "
gameforge.new_quest_premium_lv4._30_say = "Chybí mi moje láska, obchodník se zbraněmi. Celé[ENTER]týdny zpíval pod mými okny každou noc a nyní jsem[ENTER]jeho hlas neslyšela již dlouhou dobu. Možná se[ENTER]bojí mého otce. Mohl by si za ním zajít a[ENTER]zjistit, co se stalo? "
gameforge.new_quest_premium_lv4._310_say_title = "Obchodník se zbraněmi "
gameforge.new_quest_premium_lv4._320_say = "Ach, děkuji ti. Takže říkáš, že se jen zdržel?[ENTER]To je ale překvapení. Budu na něj tedy čekat dnes[ENTER]večer. "
gameforge.new_quest_premium_lv4._330_say_reward = "Za své úsilí obdržíš: %s bodů zkušeností. "
