gameforge.new_quest_premium_lv4._40_qSetTitle = "Nový parfém "
gameforge.new_quest_premium_lv4._45_qSetTitle = "Ulov modré vlky "
gameforge.new_quest_premium_lv4._50_targetVid = "Obchodník se zbraněmi: "
gameforge.new_quest_premium_lv4._55_qSetTitle = "Zpět k obchodníkovi se zbraněmi "
gameforge.new_quest_premium_lv4._60_sayTitle = "Zloděj "
gameforge.new_quest_premium_lv4._65_qSetTitle = "Dones pižmový olej Obchodníkovi se zbraněmi "
gameforge.new_quest_premium_lv4._70_say = "To je pravda, již pod jejími okny nezpívám,[ENTER]protože... no, stydím se o tom mluvit. Ale, že[ENTER]jsi to ty - prostě si přijdu, že smrdím. Pořád se[ENTER]myji, ale prostě ten zápach ze sebe ne a ne[ENTER]dostat. Tak jsem si chtěl vyrobit nějaký parfém,[ENTER]který by ten zápach pohltil a moje láska,[ENTER]obchodnice smíšeným zbožím, by nic nepoznala. Pro[ENTER]ní je totiž tohle velmi důležité. "
