gameforge.npc_talk = {}
gameforge.npc_talk._100_say = "Jsem velmi starý muž, ale přesto jsem čilý a[ENTER]zdravý, mám na to tajný recept. Přišel jsem na[ENTER]to, že lektvar ze šťávy kořenu gango a hub tue[ENTER]prodlužuje život. Jestli chceš taky ochutnat[ENTER]tento lektvar, tak jdi s těmi přísadami k[ENTER]Yu-Hwanovi. Určitě ti ho uvaří. "
gameforge.npc_talk._10_say = "Do pozoru a držet postoj.[ENTER]Chovej se slušně a neprovokuj žádné hádky. Měl[ENTER]bys mít respekt k bližním.[ENTER]Rozumíme si? "
gameforge.npc_talk._110_npcChat = "Vypadáš docela silně... "
gameforge.npc_talk._120_say = "Tajemství síly se v naší rodině předává už po[ENTER]mnoho let. Otec mi vyprávěl, že když se správným[ENTER]způsobem zpracuje zvonek na lektvar, tak ti dodá[ENTER]na krátkou dobu ohromnou sílu.[ENTER]Pokud vím, Beak-Go ví, jak vyrobit tento lektvar.[ENTER]Musíš mu jen donést zvonek.  "
gameforge.npc_talk._130_npcChat = "Jdi, řekni to horám... "
gameforge.npc_talk._140_sayTitle = "Hudebník Yu-Hwan: "
gameforge.npc_talk._150_say = "Až tam za hory, tam za obzory...[ENTER]lalalááá Zpívám si tady v dalekém ústraní abych[ENTER]se nezapletl do války.[ENTER]Ale bez země, která mě ochraňuje, mi nebude[ENTER]dopřáno věnovat se této kratochvíli... "
gameforge.npc_talk._160_npcChat = "Proč tolik čteš? "
gameforge.npc_talk._170_sayTitle = "Knihomol Soon: "
gameforge.npc_talk._180_say = "Á, dobrodruh.[ENTER]Svět je plný problémů, a jen se znalostmi se dá[ENTER]přežít.[ENTER]Vždy uctívej tvé knihy.[ENTER]Ve špatných časech ti poradí, jak dál.[ENTER]Věř mi! "
gameforge.npc_talk._190_npcChat = "Máš děti? "
gameforge.npc_talk._200_say = "Momentálně žádné děti nechci, ale... No, sbírám[ENTER]Kakikvěty, protože když se dobře připraví, tak z[ENTER]nich je nápoj, co ti dodá energii. Baek-go na to[ENTER]přišel. Potřebuje jen jeden kakikvět na 100[ENTER]nápojů. Bohužel mají ale vedlejší účinek. Pokud[ENTER]žena nosí náhrdelník z těchto květů, porodí syna.[ENTER]A mezi námi - moje žena toho dost zneužila. "
gameforge.npc_talk._20_npcChat = "Mmmm, pěkně to tu voní... "
gameforge.npc_talk._210_npcChat = "Co máš s nohou? "
gameforge.npc_talk._220_sayTitle = "Handu-Up: "
gameforge.npc_talk._230_say = "Při poslední procházce jsem zakopl o kořen gango.[ENTER]Protože jsem si u toho zvrtl nohu, jsem musel za[ENTER]lékařem Baek-Go. Pověděl mi, že se z kořenu gango[ENTER]dá vyrobit lektvar, který hojí malé rány. "
gameforge.npc_talk._240_npcChat = "Kdo jsi? "
gameforge.npc_talk._250_sayTitle = "Wonda-Rim: "
gameforge.npc_talk._260_say = "Jsem Wonda-Rim, Chaegirabův synovec. Můj strýc je[ENTER]úžasný! Znáš ho? Hodně experimentuje s lektvary a[ENTER]bylinkami. Během svých pokusů zjistil, jak získat[ENTER]ochranu před magickou energií - smícháním 100 vod[ENTER]Bo a moruše. Pokud vím, tak Huahn-So z těch[ENTER]ingrediencí vyrábí elixíry. "
gameforge.npc_talk._270_npcChat = "Proč si pořád tak vyděšený? "
gameforge.npc_talk._280_sayTitle = "Zrádce Balso: "
gameforge.npc_talk._290_say = "Protože jsem toho v boji moc nevydržel, zeptal[ENTER]jsem se Yu-Hwana, jestli by mi nevyrobil nějaký[ENTER]lektvar, který by mě ochraňoval proti zranění.[ENTER]Uvařil mi lektvar ze zvonkové šťávy a šeříku.[ENTER]Bohužel to nemělo očekávaný účinek. Ale tobě by[ENTER]určitě v boji pomohl. "
gameforge.npc_talk._300_npcChat = "Proč tě tolik lidí nazývá zrádcem? "
gameforge.npc_talk._30_sayTitle = "Hostinský Octavio: "
gameforge.npc_talk._310_say = "Kdysi jsem byl členem bandy Černého větru.[ENTER]Ale pak jsem se k jejich zločineckým praktikám[ENTER]obrátil zády a oni mi to nikdy neodpustili.[ENTER]Zabíjejí nevinné lidi. Na to jsem se už nemohl[ENTER]dál dívat. "
gameforge.npc_talk._320_say = "Tys také zabíjel nevinné lidi? Tak pak si své[ENTER]jméno zasloužíš. Vzal jsi jim možnost vést dobrý[ENTER]život. "
gameforge.npc_talk._330_say = "Žijeme v krutém světě. Ale já už nebudu dělat nic[ENTER]zlého. Nechci, aby si mne lidé pamatovali jako[ENTER]zrádce.[ENTER]Nemáš pro mně náhodou pár Yangů? "
gameforge.npc_talk._340_npcChat = "Tobě říkají učitel? "
gameforge.npc_talk._350_sayTitle = "Učenec Uriel: "
gameforge.npc_talk._360_say = "Na tomto širém světě je tolik věcí, které se[ENTER]člověk může naučit. Ignorance spojená s vojenskou[ENTER]mocí nenadělá žádnou hodnotu.[ENTER]Přeci nechceš žít jak ti ignorantní vojáci, nebo[ENTER]jo? Mít vědomosti znamená mít moc! "
gameforge.npc_talk._370_npcChat = "Mé zdroje říkají, že praktikuješ léčení? "
gameforge.npc_talk._380_say = "Dělám co mohu. Na tomto nebezpečném světě je[ENTER]každý den čím dál tím víc zraněných.[ENTER]A nemáme dostatek léků ani personálu.[ENTER]Časy jsou kruté! Mohl bys mi později pomoct? "
gameforge.npc_talk._390_npcChat = "Tak jak ti jde lov? "
gameforge.npc_talk._400_sayTitle = "Lovec Yang-Shin: "
gameforge.npc_talk._40_say = "Vítáme![ENTER]Naše restaurace patří k velkému internacionálnímu[ENTER]řetězci. Jednou se k nám podívej a dej si něco k[ENTER]jídlu. "
gameforge.npc_talk._410_say = "Hmm, od té doby co padají kameny Metin, divočí[ENTER]nám zvířata. V dnešní době člověk riskuje život i[ENTER]při chytání kuřat. Je to pro lovce nebezpečná[ENTER]doba. Naštěstí, já jsem jeden z nejlepších! "
gameforge.npc_talk._420_npcChat = "Odkud se bere tvá přesná muška? "
gameforge.npc_talk._430_say = "Jsem nejlepší s lukem. Hehe.[ENTER]Protože mám na to speciální recept![ENTER]Když smícháš šťávu z broskvových květů a šeřík,[ENTER]dostaneš lektvar, který ti zvýší pravděpodobnost[ENTER]zásahu. Když doneseš Yu-Hwanovi přísady, tak ti[ENTER]určitě uvaří tento skvělý lektvar. "
gameforge.npc_talk._440_npcChat = "Pěkné květiny... "
gameforge.npc_talk._450_say = "Sbírávali jsme s bratrem broskvové květy. Teď už[ENTER]musím sama. Nejsou moc pěkné, ale mají speciální[ENTER]účinky![ENTER]Když doneseš jeden květ Beak-Goovi, tak ti uvaří[ENTER]lektvar, který ti dodá lehkonohou chůzi. "
gameforge.npc_talk._460_npcChat = "Kde je tvůj bratr? "
gameforge.npc_talk._470_say = "Touto dobou je můj bratr často na cestách.[ENTER]Dělám si starosti. "
gameforge.npc_talk._480_npcChat = "Prodáváš rýžové koláče? "
gameforge.npc_talk._490_sayTitle = "Prodavačka rýžových buchet, Yu-Rang: "
gameforge.npc_talk._500_say = "Kvůli válce je těžké dělat rýžové buchty. Taky[ENTER]jsem ztratila hodně zákazníků kvůli banditům.[ENTER]Už nevím, z čeho mám žít. "
gameforge.npc_talk._50_say = "I když to je velká restaurace, zařízení je[ENTER]překvapivě jednoduché. "
gameforge.npc_talk._510_npcChat = "Kdo od tebe obvykle nakupuje? "
gameforge.npc_talk._520_say = "Nejvíce prodávám poutníkům. Jednoho dne přijel[ENTER]tajemný jezdec a chtěl ode mně koupit rýžovou[ENTER]buchtu. Nemohl zaplatit, tak mi dal namísto toho[ENTER]recept. V receptu stojí, jak uvařit vodu Zin.[ENTER]Lektvar zvyšuje rychlost útoku. Huhan-So umí[ENTER]uvařit takový lektvar, když mu přineseš vodu Sim[ENTER]a rododendron. "
gameforge.npc_talk._530_npcChat = "Proč vypadáš tak otráveně? "
gameforge.npc_talk._540_say = "Nemohla jsem tušit, že se naše sladké sny tak[ENTER]nemilosrdně zničí.[ENTER]Vzlyk, vzlyk...[ENTER]Prosím pomsti smrt mého manžela. "
gameforge.npc_talk._550_npcChat = "Příliš mnoho lidí padlo ve válce... "
gameforge.npc_talk._560_say = "Protože ve válce ztrácíme čím dál tím víc mužů,[ENTER]daly jsme se my ženy dohromady a poprosily[ENTER]Huahn-So, aby zlepšil vodu Dok, která zlepšuje[ENTER]obranu. Po dlouhém výzkumu zjistil, že když přidá[ENTER]k vodě Dok rododendron, tak je lektvar silnější.[ENTER]Běž k němu, on ti určitě vyrobí z těch přísad[ENTER]tento lektvar, kterému říká voda SamBo. "
gameforge.npc_talk._570_npcChat = "Ach, jaké pěkné mrně... "
gameforge.npc_talk._580_sayTitle = "Ah-Yu: "
gameforge.npc_talk._590_say = "Zase pěkně křičí...[ENTER]Jestli chce něco dobrého nebo...[ENTER]ále nee! "
gameforge.npc_talk._600_npcChat = "Jak se vy dvě máte? "
gameforge.npc_talk._60_npcChat = "Řekni mi něco o hrnčířství. "
gameforge.npc_talk._610_say = "Moje dítě! Ach...[ENTER]Manžel padl ve válce. Teď se musím ohánět, abych[ENTER]uživila sama sebe a moje dítě.[ENTER]S vodou Bo jsem našla cestu, jak trochu vylepšit[ENTER]moje živobytí. Musím Yu-Hwangovi donést 100 šťáv[ENTER]z kakikvětů a jednu houbu tue. On z toho uvaří[ENTER]vodu Bo, která jde dobře na odbyt. Něčím se přeci[ENTER]musím uživit. "
gameforge.npc_talk._620_npcChat = "Jak se má manžel? "
gameforge.npc_talk._630_sayTitle = "Aranyo: "
gameforge.npc_talk._640_say = "Můj takzvaný muž je zamilovaný do jeho knížek. Už[ENTER]se na mě ani nepodívá.[ENTER]A to nejsem žádná ošklivá žena. "
gameforge.npc_talk._650_npcChat = "Jak jdou obchody? "
gameforge.npc_talk._660_say = "Od té doby, co propukla válka, nám kšeft moc[ENTER]neběží. Odbory obchodníků byly kdysi mocné. Ale[ENTER]ty časy už pominuly.[ENTER]Divochy, válečníky Bílé přísahy a bandu Černého[ENTER]větru už nemůžeme ignorovat jako dřív. "
gameforge.npc_talk._670_npcChat = "Proč máš tak špatnou náladu? "
gameforge.npc_talk._680_sayTitle = "Taurean: "
gameforge.npc_talk._690_say = "Harang je hrozná holka![ENTER]Nesnáším ji![ENTER]Jsem slabý a ona mi často ještě ztěžuje život! "
gameforge.npc_talk._700_say = "Jsi chlap a máš svaly.[ENTER]Trochu si je potrénuj! "
gameforge.npc_talk._70_sayTitle = "Hrnčíř Yonah: "
gameforge.npc_talk._710_npcChat = "Co zrovna děláš? "
gameforge.npc_talk._720_sayTitle = "Harang: "
gameforge.npc_talk._730_say = "Zatracený Taurean! Často si spolu hrajeme a i[ENTER]bojujeme. Zbabělec jeden! Když dneska nepřijde[ENTER]dobrovolně, tak ho zmlátím! "
gameforge.npc_talk._740_npcChat = "Taurean se tě bojí. "
gameforge.npc_talk._750_say = "Ráda si hraji s Taureanem. Často se taky pereme.[ENTER]A protože má vlastně velkou sílu, něco jsem si na[ENTER]něj vymyslela.[ENTER]Dozvěděla jsem se totiž od jednoho moudrého[ENTER]Ninji, že když se smíchá voda Young s moruší, tak[ENTER]vznikne lektvar, který zvyšuje rychlost útoku.[ENTER]Tím pádem mohu Taureana rychleji přemoct - i když[ENTER]je silnější.[ENTER]Jestli chceš taky takový lektvar, tak běž k[ENTER]Huhan-Soovi. On ti ho jistě vyrobí. "
gameforge.npc_talk._760_npcChat = "Nový pohled... "
gameforge.npc_talk._770_say_title = "Seon-Pyeong: "
gameforge.npc_talk._780_say = "Můj příteli, svět je plný nezodpovězených otázek.[ENTER]A já, já jsem hledač vědomostí. Slyšel jsi již[ENTER]někdy o epických zbraních a Kamenech ducha? "
gameforge.npc_talk._790_npcChat = "Co děláš? "
gameforge.npc_talk._800_say = "Pravděpodobně víš, že hledám ztracené vědomosti.[ENTER]Momentálně hledám metodu, jak dále vylepšit[ENTER]zbraně a zvýšit sílu Kamenů ducha. "
gameforge.npc_talk._80_say = "Co se týče hrnčířského zboží, tak je nejlepší[ENTER]výroba na východě kontinentu.[ENTER]Jednou jsem viděl hrnčířské zboží odtamtud a byl[ENTER]jsem z toho úplně nadšený. Ještě pořád to[ENTER]zkoumám. Nevím, jaký trik na to používají. "
gameforge.npc_talk._810_npcChat = "Výzkum zbraní... "
gameforge.npc_talk._820_say = "Zajímavé, že? Chceš o tom vědět více? Tak mi[ENTER]prosím přines nějakou zbraň úrovně 65 vylepšenou[ENTER]na +9.Také budu potřebovat Strategii protiútoku a[ENTER]dva Požehnané pergameny. Pokusím se pak vyrobit[ENTER]epickou zbraň. "
gameforge.npc_talk._830_say = "Bohužel ti ale nemohu slíbit, že se to povede. "
gameforge.npc_talk._840_npcChat = "Kameny ducha... "
gameforge.npc_talk._850_say = "Kameny ducha jsou často používány, ale přesto[ENTER]většina lidí nezná jejich skutečnou sílu. Ale[ENTER]moje výzkumy mě pomohly zjistit o nich více.[ENTER]Pokud mi přineseš Kámen ducha, vylepším ho. "
gameforge.npc_talk._90_npcChat = "Na svůj věk jsi dost čilý.... "
gameforge.olympic_badge = {}
gameforge.olympic_badge._010_npcChat = "Medaile hrdiny "
gameforge.olympic_badge._020_say = "Pomoz mi! "
gameforge.olympic_badge._030_say = "Zatracení zloději...[ENTER]Minulou noc se vloupali do mého domu![ENTER]Ukradli mi mou Medaili hrdiny! Můj předek jí[ENTER]dostal, když uběhl 42,195 km, aby donesl zprávu o[ENTER]vítězství z bojiště svému panovníkovi.[ENTER]A teď je fuč! "
gameforge.olympic_badge._040_say = "Ach jo... měla velký význam pro mou rodinu i pro[ENTER]mne. Co mám teď k čertu dělat? "
gameforge.olympic_badge._050_say = "Pomůžeš mi? "
gameforge.olympic_badge._060_npcChat = "Medaile hrdiny "
gameforge.olympic_badge._070_say = "Konečně! Jsi zpět![ENTER]Našel jsi Medaili hrdiny? "
gameforge.olympic_badge._080_say = "Ale proč? Co chtějí ti zloději dělat s mou[ENTER]medailí? Je pro ně bezcenná! Pomůžeš mi ji najít? "
gameforge.olympic_badge._090_npcChat = "Najdi Medaili hrdiny! "
gameforge.olympic_badge._100_say = "Vrátil ses s prázdnýma rukama? Ach ne, to je[ENTER]zklamání. Pokud mi nepomůžeš ty, tak pak kdo?[ENTER]Prosím, pokračuj v pátrání! "
gameforge.olympic_badge._110_say = "Našel jsi mou medaili![ENTER]Ale počkej - to je jen její kousek. Ach ne, oni[ENTER]ji rozbili na pět kusů! Prosím, přines mi všechny[ENTER]kusy! Možná se mi povede ji pak zase slepit[ENTER]dohromady. "
gameforge.olympic_badge._120_say = "Tihle zlotřilí kriminálníci úplně zničili odkaz[ENTER]mé rodiny! Děkuji ti, že jsi našel všechny kusy.[ENTER]Nyní se mohu pokusit medaili dát opět dohromady. "
gameforge.olympic_badge._130_say = "Mnohokrát ti děkuji! Získal jsi zpět čest mé[ENTER]rodině. Samozřejmě, že tvé potíže nezůstanou bez[ENTER]odměny. Tady, ber! "
gameforge.olympic_badge._140_say = "Opravdu chceš odmítnout tento úkol? Pokud odpovíš[ENTER]'Ano', později jej již nebudeš moci přijmout[ENTER]znovu. "
gameforge.olympic_badge._150_say = "Chápu, že nyní nemáš mnoho času. Vrať se později,[ENTER]až ti to bude více vyhovovat. Ale nenech mě čekat[ENTER]příliš dlouho! "
gameforge.olympic_badge._160_say = "Bohužel jsi přijel příliš pozdě a již mi nemůžeš[ENTER]pomoci. Přeji ti vše dobré na tvých cestách! "
gameforge.olympic_badge._170_say = "Vydej se na lov příšer, které mě okradly! Ale buď[ENTER]opatrný, jsou velmi silné - minimálně úrovně 30.[ENTER]Pokud mi přineseš zpět mou Medaili hrdiny, bohatě[ENTER]se ti odměním! "
gameforge.olympic_badge._180_say = "Najdi Medaili hrdiny a dones ji Yu-Hwanovi. "
