gameforge.subquest_23._110_targetVid = "Běž za Yu-Hwanem "
gameforge.subquest_23._120_say = "Mirinin starší bratr zase zmizel. Už dlouho[ENTER]nenapsal žádnou zprávu. Od té doby, co napsal[ENTER]poslední dopis, se vůbec neozval.[ENTER]nebylo nic slyšet.[ENTER]Jdi k Yu-Hwanovi a porozhlédni se po Mirinině[ENTER]bratrovi. "
