gameforge.subquest_23._300_npcChat = "Ta mrtvola! "
gameforge.subquest_23._30_say = "Jednou jsi mi přinesl od bratra dopis, že? Byla[ENTER]jsem ti za to moc vděčná. Bohužel, od té doby o[ENTER]něm nemám žádnou zprávu. A on je stále pryč,[ENTER]hledá pro mne léčivé byliny. "
gameforge.subquest_23._310_say = "Leží tam mrtvý muž. Je mladý a útlý a vypadá to,[ENTER]že sbíral gingko. Obávám se, že by to mohl být[ENTER]Mirinin starší bratr, který jej také sbíral. Na[ENTER]těle je náhrdelník s přívěškem. Vezmu ho k[ENTER]Mirine. "
