gameforge.subquest_23._340_say = "Našel jsi přívěšek na těle v údolí Seungryoung.[ENTER]Je na něm vyryto Mirinino jméno. Jdi k Mirine a[ENTER]předej jí náhrdelník a přívěšek. "
gameforge.subquest_23._350_npcChat = "Znáš ten náhrdelník? "
gameforge.subquest_23._360_say = "Ach, ten náhrdelník... Ten pořád nosíval můj[ENTER]bratr. Mám podobný. Vždy jsme je nosili, abychom[ENTER]měli jeden na druhého vzpomínku a našli vždy k[ENTER]sobě cestu. Ale... můj bratr tu není... A jen[ENTER]náhrdelník našel cestu zpět. "
gameforge.subquest_23._370_say = "Můj ubohý bratr...[ENTER]A vše jen díky té válce. Můj bratr prodával[ENTER]léčivé byliny, bylo to to jediné, co nám dávalo[ENTER]nějaké peníze, díky nimž jsme si mohli koupit[ENTER]jídlo. Vždy tvrdě pracoval, abych měla co jíst. A[ENTER]teď je mrtvý. Jen kvůli mě... "
gameforge.subquest_23._380_say = "Děkuji, že si mi to donesl. Teď aspoň vím, co se[ENTER]stalo.[ENTER]Žít v nevědomosti by bylo ještě mnohem horší.[ENTER]Nikdy bych se nedozvěděla, co se bratrovi[ENTER]přihodilo. "
gameforge.subquest_23._390_say = "Budu se o přívěšek staršího bratra dobře starat a[ENTER]tobě dám ten náhrdelník jako výraz mé vděčnosti.[ENTER]Jsem si jista, že se ti bude hodit.[ENTER]Děkuji ti za pomoc. Myslím, že můj bratr je v[ENTER]nebi, co myslíš? "
gameforge.subquest_23._400_sayReward = "Obdržíš 600 000 bodů zkušeností.[ENTER]Obdržíš 35 000 Yangů. "
gameforge.subquest_23._40_say = "Bojím se o něj, protože o něm již dlouho nemám[ENTER]žádné zprávy. Promiň, ale mohl by ses po něm[ENTER]podívat? Yu-Hwan by mohl něco vědět. "
gameforge.subquest_23._50_select = "Odmítnout "
gameforge.subquest_23._60_say = "Chceš hledání vzdát? "
gameforge.subquest_23._70_say = "Ach...[ENTER]Počkám tady. Možná později? Prosím...? "
gameforge.subquest_23._80_say = "Nemám čas.[ENTER]Hodně štěstí tvému bratrovi. "
gameforge.subquest_23._90_say = "Pomoct tomu ubohému děvčátku je moc milé.[ENTER]A není to zas tak moc práce.[ENTER]Tak jdeme! "
