gameforge.locale.monster_chat_11000_28 = "Uważaj na wrogów z innych Królestw. Nigdy nie wiesz, kiedy zjawią się za tobą! "
gameforge.locale.monster_chat_11000_29 = "Chcesz zresetować swoje Umiejętności? Idź do Starszej Pani. Nie zapomnij zabrać za sobą wystarczająco dużo Yang! "
gameforge.locale.monster_chat_11000_3 = "Jeśli chcesz być potężny, udaj się do Trenera Mocy Ostrza! "
gameforge.locale.monster_chat_11000_30 = "Stań się wielkim mistrzem! Tylko tak możesz zapisać się na kartach historii! "
gameforge.locale.monster_chat_11000_31 = "Jeżeli chcesz przejść na jakiś inny obszar, porozmawiaj ze Staruszkiem! "
gameforge.locale.monster_chat_11000_4 = "Jeśli chcesz być prawdziwym wojownikiem, musisz spróbować Siły Duchowej. "
gameforge.locale.monster_chat_11000_5 = "Jeśli chcesz zostać Surą mogącym władać Magicznym Mieczem, wybierz Trenera Mocy Broni! "
gameforge.locale.monster_chat_11000_6 = "Jeśli chcesz kształcić się w dziedzinie czarnej magii, wybierz Trenera Czarnej Magii! "
gameforge.locale.monster_chat_11000_7 = "Trening Mocy Łuku to kierunek, w którym może iść Ninja po osiągnięciu 5 poziomu! "
gameforge.locale.monster_chat_11000_8 = "Jeśli chcesz mieć możliwość Krycia Się, wybierz Walkę W Zwarciu! "
gameforge.locale.monster_chat_11000_9 = "Jeśli chcesz być osobą posiadającą moc Smoka, to wybierz drogę Boga Smoka! "
gameforge.locale.monster_chat_11002_1 = "Słuchajcie, wszyscy, którym zależy na zdrowiu! "
gameforge.locale.monster_chat_11002_10 = "Jeśli chcesz władać mocami natury, wybierz Trening Leczenia! "
gameforge.locale.monster_chat_11002_11 = "Każdy Wojownik ma inny styl walki. Wzmacniaj swoją budowę ciała! "
gameforge.locale.monster_chat_11002_12 = "Jako potężny Sura, powinieneś rozwijać Inteligencję, szybkość i siłę ciała! "
gameforge.locale.monster_chat_11002_13 = "Po wyborze umiejętności Ninja, skup się na szybkości i sile ciała! "
gameforge.locale.monster_chat_11002_14 = "Szamani potrafią leczyć, często pełnią role pomocników. Ich siłą jest inteligencja! "
gameforge.locale.monster_chat_11002_15 = "Nie zapominaj o treningu! "
gameforge.locale.monster_chat_11002_16 = "Nie zapominaj o trenowaniu! "
gameforge.locale.monster_chat_11002_17 = "Poziom M uaktywni się po dodaniu do umiejętności od 17 do 20 punktów! "
gameforge.locale.monster_chat_11002_18 = "Chcesz zostać Wielkim Mistrzem? W takim razie potrzebujesz specjalną Księgę Umiejętności! "
gameforge.locale.monster_chat_11002_19 = "Po przeczytaniu Księga Umiejętności znika! "
gameforge.locale.monster_chat_11002_2 = "Wojownicy poniżej 5. poziomu, słuchajcie! "
gameforge.locale.monster_chat_11002_20 = "Chcesz przeczytać kolejną Księgę Umiejętności? Musisz odczekać jeden dzień! "
gameforge.locale.monster_chat_11002_21 = "Podwyższ parametr Inteligencji, aby ulepszyć swoje Umiejętności Sura. "
gameforge.locale.monster_chat_11002_22 = "Sprawdź wspaniałą moc Sura w walce! "
