gameforge.locale.monster_chat_491_3 = "Na ziemi? "
gameforge.locale.monster_chat_491_4 = "Pokonam Cię jednym ciosem. "
gameforge.locale.monster_chat_491_5 = "To będzie piękne wspomnienie! "
gameforge.locale.monster_chat_491_6 = "Giń! "
