gameforge.locale.monster_chat_492_4 = "Oh, kolejna ofiara mojej wirującej broni? "
gameforge.locale.monster_chat_492_5 = "Widziałeś mój styl ataku? Nie? Twoja strata! "
gameforge.locale.monster_chat_492_6 = "Nawet najmniejsze zadrapanie będzie dla Ciebie śmiertelne! "
