gameforge.locale.monster_chat_493_2 = "Na co się gapisz? Nigdy nie widziałeś takiej dłoni?! "
gameforge.locale.monster_chat_493_3 = "Ludzie naprawdę mnie denerwują. "
gameforge.locale.monster_chat_493_4 = "Mam Cię powalić na ziemię czy co? Jeśli nie, to lepiej uciekaj! "
gameforge.locale.monster_chat_493_5 = "Pokażę Ci mój wspaniały cios! "
gameforge.locale.monster_chat_493_6 = "Wiem, że gapisz się na moją rękę! "
