gameforge.locale.old_pirate.baekgo_order_find_virus_text_2 = "Zabij wszystkich, którzy są zarażeni tajemniczą[ENTER]chorobą i zdobądź próbkę patogenu dla Baek-Go. "
gameforge.locale.old_pirate.baekgo_order_too_late = "Baek-Go:[ENTER]Ktoś inny dostarczył mi już próbkę patogenu.[ENTER]Wysłałem już lekarstwo do Starego Pirata, podobno[ENTER]jest już zdrowy. "
gameforge.locale.old_pirate.baekgo_start_say = "Baek-Go:[ENTER]Miał on pojedynczy kontakt z zarażoną osobą i od[ENTER]tamtej pory jego rana ropieje i swędzi? "
gameforge.locale.old_pirate.baekgo_start_say2 = "Baek-Go:[ENTER]Oh!! Jestem prawie pewien, że zaraził się on tą[ENTER]tajemniczą chorobą! Jeżeli nie zajmę się nim,[ENTER]choroba rozprzestrzeni się na cała wioskę! "
gameforge.locale.old_pirate.baekgo_start_say3 = "Baek-Go:[ENTER]Nie może nikogo zarazić! Musi być natychmiastowo[ENTER]leczony. Potrzebna nam do tego próbka zarazka tej[ENTER]tajemniczej choroby. Próbkę tą otrzymasz od[ENTER]chorych, tłumnie zebranych na Górze Sohan, na[ENTER]północ stąd. "
gameforge.locale.old_pirate.baekgo_success_say = "Baek-Go:[ENTER]Dokonałeś tego! Poczekaj chwilę na lekarstwo![ENTER][DELAY value;1000] [/DELAY][ENTER][ENTER]Zrobiłem lekarstwo tak szybko, że nie wiem czy[ENTER]zadziała. Ale na pewno poczuje się lepiej. Zanieś[ENTER]mu to lekarstwo tak szybko jak to możliwe. "
gameforge.locale.old_pirate.baekgo_talk = "Przysyła mnie Stary Pirat. "
gameforge.locale.old_pirate.baekgo_talk_about_old_pirate = "Choroba Starego Pirata "
gameforge.locale.old_pirate.complete_say = "Stary Pirat:[ENTER]To nadal swędzi. Ale czuję się już lepiej.[ENTER]Przeżyję. "
gameforge.locale.old_pirate.do_you_get_the_medicine = "Nie mam jeszcze lekarstwa. "
gameforge.locale.old_pirate.do_you_mean_this_medicine = "Oto lekarstwo! "
gameforge.locale.old_pirate.etc_shop_start_say = "Handlarka Różności:[ENTER]Stary Pirat? Oczywiście, że go znam. To ten gość,[ENTER]który zawsze przeklina i się drapie, prawda?[ENTER]Zawsze przychodzi kupić jakieś płynne lekarstwo.[ENTER]Wydał wiele tysięcy Yang na Czerwone Mikstury. "
gameforge.locale.old_pirate.etc_shop_start_say2 = "Handlarka Różności:[ENTER]Skąd na miłość boską ma on tyle Yang, skoro nawet[ENTER]nigdzie nie pracuje? Już mu tyle razy mówiłam,[ENTER]żeby poszedł na badania do lekarza, ale on mnie w[ENTER]ogóle nie słucha.[ENTER]Czerwone lekarstwo łagodzi jedynie symptomy, nie[ENTER]leczy jednak samej choroby. Boję się, że źle się[ENTER]to skończy. "
gameforge.locale.old_pirate.etc_shop_talk_about_old_pirate = "Znasz Starego Pirata? "
gameforge.locale.old_pirate.order_accept = "Pomogę Ci. "
gameforge.locale.old_pirate.order_accept_answer = "Stary Pirat:[ENTER]Świetnie. Powodzenia![ENTER]Lekarz na pewno jest w mieście. "
gameforge.locale.old_pirate.order_find_medicine_label = "Lekarstwo dla Starego Pirata "
gameforge.locale.old_pirate.order_find_medicine_text_1 = "Zdobądź lekarstwo dla Starego Pirata. "
gameforge.locale.old_pirate.order_find_medicine_text_2 = "Udaj się do Baek-Go w mieście i spytaj się go o[ENTER]lekarstwo dla Starego Pirata. "
gameforge.locale.old_pirate.order_refuse = "Niestety, nie mogę. "
gameforge.locale.old_pirate.order_refuse_answer = "Stary Pirat:[ENTER]W takim razie, nie mam nic więcej do powiedzenia! "
gameforge.locale.old_pirate.order_say1 = "Stary Pirat:[ENTER]Cholera! Czego chcesz? Cholera! Ten rupieć[ENTER]sprzedawca sklepowy powiedział Ci coś o mnie,[ENTER]tak?[ENTER]Taa, to prawda. Ale ja nie mam czasu, żeby iść do[ENTER]lekarza. Mam tylu wrogów! "
gameforge.locale.old_pirate.order_say2 = "Stary Pirat:[ENTER]Nigdy nie wiesz, czy jakiś drań nagle nie[ENTER]znajdzie się przy tobie i nie dźgnie Cię! Ale to[ENTER]naprawdę swędzi... Nie mogę wytrzymać... Więc -[ENTER]czy mógłbyś pójść do lekarza i przynieść mi[ENTER]lekarstwo na tą chorobę? Zostaniesz sowicie[ENTER]wynagrodzony... "
gameforge.locale.old_pirate.order_too_late = "Stary Pirat:[ENTER]Ktoś inny przyniósł mi już lekarstwo. Więc nie[ENTER]dostaniesz ode mnie nagrody! "
