gameforge.new_quest_lv43._030_target = "Chaegirab: "
gameforge.new_quest_lv43._031_say_title = "Chaegirab: "
gameforge.new_quest_lv43._032_say = "Wonda-Rim... tak, nie jestem jego prawdziwym[ENTER]wujkiem. Nie wiem, kim byli jego rodzice. Do tej[ENTER]pory chciałem mu oszczędzić cierpienia, bo nie[ENTER]był jeszcze gotów, dowiedzieć się całej prawdy o[ENTER]swoim pochodzeniu. Może jednak teraz przyszła[ENTER]pora... "
gameforge.new_quest_lv43._033_say = "Znalazłem go w Dolinie Seungryong u zgrai Orek[ENTER]Wojowników. Jeśli chcesz się czegoś dowiedzieć o[ENTER]pochodzeniu Wonda-Rima to najlepiej u Orków. "
gameforge.new_quest_lv43._034_say = "Może i jestem surowy dla niego, ale na zawsze[ENTER]zostanę jego wujkiem. Nawet jeśli dalej będzie[ENTER]takim nierobem. "
gameforge.new_quest_lv43._035_say_reward = "Udaj się do Doliny Seungryong i poluj na Orki[ENTER]Wojowników, aż znajdziesz jakąś wskazówkę[ENTER]dotyczącą pochodzenia Wonda-Rima. "
gameforge.new_quest_lv43._036_say = "W zabrudzonej torbie jednego z zabitych Ork[ENTER]Wojowników znalazłeś stary List. Ledwo można go[ENTER]odczytać, ale rozpoznałeś na nim imię Wonda-Rima.[ENTER]Właśnie znalazłeś poczebną wskazówkę. Zanieś[ENTER]natychmiast List Wonda-Rima. "
gameforge.new_quest_lv43._041_say = "Ruszaj! Najlepszą możliwością, aby dowiedzieć się[ENTER]coś o pochodzeniu Wonda-Rima, jest zabicie Ork[ENTER]Wojowników. Może mają przy sobie odpowiednią[ENTER]wskazówkę! "
gameforge.new_quest_lv43._043_notice = "Wskazówki dotyczące Wonda-Rim... "
gameforge.new_quest_lv43._045_say = "Wszystko co wiedziałem, to ci już powiedziałem.[ENTER]Jedyną wskazówką, którą może nam jeszcze pomóc,[ENTER]to Orki Wojownicy. Niestety z nimi nie można[ENTER]rozsądnie porozmawiać... "
gameforge.new_quest_lv43._049_say = "List? Pokaż... Litery są trochę zamazane...[ENTER]Chodzi o napad... Orki spaliły farmę.. i oni[ENTER]uciekli, aby ratować swoje życie... musieli[ENTER]pozostawić swoje dziecko... Tutaj jest moje imię. "
gameforge.new_quest_lv43._050_say = "Dziękuję, że przyniosłeś mi. W końcu wiem, co się[ENTER]naprawdę wydarzyło. "
gameforge.new_quest_lv43._051_say = "Powinienem porozmawiać z Chaegirabem. Naraził się[ENTER]na niebezpieczeństwo, aby mnie uratować. Postaram[ENTER]się poprawić. Do zobaczenia, przyjacielu! "
gameforge.new_quest_lv43._052_say_title = "Informacja: "
gameforge.new_quest_lv43._053_say_reward = "Otrzymałeś 32.000 Yang. "
gameforge.new_quest_lv43._054_say_reward = "Otrzymałeś 1.250.000 Punktów Doświadczenia. "
gameforge.new_quest_lv43._060_say = "Rozmawiałeś z moim wujkiem Chaegirab? Wskazówka?[ENTER]O co ci chodzi? Jeśli nie posiadasz żadnych[ENTER]dowodów, zachowaj swoje domyły dla siebie![ENTER][ENTER]Proszę spytaj się jeszcze raz mojego wuja.[ENTER]W końcu za to ci płacę. "
