gameforge.locale.monster_chat_1191_2 = "Nimic altceva de facut decat sa-ti ascuti armele..."
gameforge.locale.monster_chat_1191_3 = "Oare ar trebui să lucrez la ele?"
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Ceva lipsește. Nu sunt mulțumit."
gameforge.locale.monster_chat_1191_6 = "Am să pun repede capăt suferinței tale. Acesta ar fi ultimul."
gameforge.locale.monster_chat_1301_1 = "Gat alungit...!"
gameforge.locale.monster_chat_1301_2 = "Culoare pielii iti va palii si mai mult intr-un minut."
gameforge.locale.monster_chat_1301_3 = "Vrei sa ma provoci? OK!"
gameforge.locale.monster_chat_1301_4 = "M-ai atacat! Pweck! Nu poți supraviețui."
gameforge.locale.monster_chat_1301_5 = "Oameni slabi, slabi!"
gameforge.locale.monster_chat_1301_6 = "Sa vedem cat poti rezista!"
gameforge.locale.monster_chat_1302_1 = "Vrei sa scapi? Nici sa nu te gandesti."
gameforge.locale.monster_chat_1302_2 = "Așa. Vino mai aproape. Pwiehk!"
gameforge.locale.monster_chat_1302_3 = "Sabia tremură În mâna mea. Pwiehk!"
gameforge.locale.monster_chat_1302_4 = "Nu vei supravietui acestei incercari!"
gameforge.locale.monster_chat_1302_5 = "Aici vine sabia mea!"
gameforge.locale.monster_chat_1302_6 = "Dureros, nu? (Râs)"
gameforge.locale.monster_chat_1303_1 = "Chiar și iarba călcată din greșeală poate ucide."
gameforge.locale.monster_chat_1303_2 = "Atenție la iarbă."
gameforge.locale.monster_chat_1303_3 = "Așa. Vino frumos la mine. (Râs)"
gameforge.locale.monster_chat_1303_4 = "Indraznesti sa te apropii de mine? Bine... atunci trebuie sa suporti consecintele!"
gameforge.locale.monster_chat_1303_5 = "Mori! Mori!"
gameforge.locale.monster_chat_1303_6 = "Regreți că ai venit?"
gameforge.locale.monster_chat_1305_1 = "Tu? Vrei să lupți?"
gameforge.locale.monster_chat_1305_2 = "Tu? Vrei să lupți?"
gameforge.locale.monster_chat_1305_3 = "Vino dacă vrei!"
gameforge.locale.monster_chat_1305_4 = "Te gandeai sa fugi, nu-i asa?"
gameforge.locale.monster_chat_1305_5 = "Ai subestimat valoarea sabiei mele."
gameforge.locale.monster_chat_1305_6 = "ți-e frică? (Râs)"
gameforge.locale.monster_chat_1901_1 = "Cred ca o sa mori in mainile mele azi."
gameforge.locale.monster_chat_1901_2 = "Oh, Îmi place de tine."
gameforge.locale.monster_chat_1901_3 = "Cum arată? Schimbarea mea dragă."
gameforge.locale.monster_chat_1901_4 = "Hei! Sa fugi ca un las nu te va ajuta cu nimic."
gameforge.locale.monster_chat_1901_5 = "Aspectul meu ti-a provocat fiori pe sira spinarii?"
gameforge.locale.monster_chat_1901_6 = "Nu vrei să mai strigi ceva? (Râs)"
gameforge.locale.monster_chat_20001_1 = "Pari nervos... Nu te ingrijora, nu va exploda nimic."
gameforge.locale.monster_chat_20001_2 = "Din când În când aș vrea să merg la plimbare."
gameforge.locale.monster_chat_20001_3 = "E așa de plictisitor. Nici măcare nepoții mei nu mă mai vizitează."
gameforge.locale.monster_chat_20001_4 = "Când ești bătrân ai nevoie de companie."
gameforge.locale.monster_chat_20001_5 = "Zilele mele de glorie au apus."
gameforge.locale.monster_chat_20002_1 = "Soarele este așa de cald."
gameforge.locale.monster_chat_20002_2 = "Minunată vreme pentru a usca o pânză udă, nu-i așa?"
gameforge.locale.monster_chat_20002_3 = "Unde mi-o fi neisprăvitul de bărbat?"
gameforge.locale.monster_chat_20002_4 = "Își spune tată, și nici măcar nu se rușinează În fața copiilor din cauza marionetei sale!"
gameforge.locale.monster_chat_20002_5 = "Cărțile sunt cărți. Lucrează și nu câștigă nimic. Citește doar!"
gameforge.locale.monster_chat_20003_1 = "Oh, dududu, dragă. Ai dormit bine?"
gameforge.locale.monster_chat_20003_2 = "Zâmbești ca un Îngeraș."
gameforge.locale.monster_chat_20003_3 = "Mama ta se bucură că ești așa sănătos."
gameforge.locale.monster_chat_20003_4 = "Iară vrei atenție? ți-e foame?"
gameforge.locale.monster_chat_20003_5 = "Copil cuminte, copilul nostru. Dormi, copilule, dormi."
gameforge.locale.monster_chat_20004_1 = "Pur și simplu o nouă zi."
gameforge.locale.monster_chat_20004_2 = "Unde pot mânca astăzi ceva?"
gameforge.locale.monster_chat_20004_3 = "Intotdeauna este la fel cu spadasinii astia."
gameforge.locale.monster_chat_20004_4 = "Ia sa vedem...Poate ar trebui sa merg la Octavio la restaurant."
gameforge.locale.monster_chat_20004_5 = "Ah da, se spune că nu va mai fi război..."
gameforge.locale.monster_chat_20005_1 = "Sufletul unui maestru in olarit se afla in varful degetelor sale."
gameforge.locale.monster_chat_20005_2 = "De când țara a fost cuprinsă de haos, vezi peste tot numai luptători."
gameforge.locale.monster_chat_20005_3 = "Toți vor să se lutpe mereu."
gameforge.locale.monster_chat_20005_4 = "Eu mă rog pentru o lume liniștită și Împăcată."
gameforge.locale.monster_chat_20005_5 = "Hei, tu acolo! Ia-ți măcar puțin timp să te relaxezi și să cugeți."
gameforge.locale.monster_chat_20006_1 = "Ahhh, ce fac oare?"
gameforge.locale.monster_chat_20006_2 = "Nu dau de urma fratelui meu. Zicea că vrea să vină."
gameforge.locale.monster_chat_20006_3 = "Iar acum nu văd decât acești luptători cu săbiile lor."
gameforge.locale.monster_chat_20006_4 = "Mi-e dor de fratele meu."
gameforge.locale.monster_chat_20006_5 = "L-a văzut cineva pe fratele meu?"
gameforge.locale.monster_chat_20007_1 = "Mmmmmh, carnea aceasta este proaspătă!"
gameforge.locale.monster_chat_20007_2 = "Ce bine că oamenii mănâncă din nou mai multă carne."
gameforge.locale.monster_chat_20007_3 = "Afacerea Înflorește, dar carnea e cam puțină."
gameforge.locale.monster_chat_20007_4 = "Când ajunge carnea pe care am comandat-o?"
gameforge.locale.monster_chat_20007_5 = "Dacă nu vine la timp trebuie să mergem la vânătoare."
gameforge.locale.monster_chat_20008_1 = "Sper că pe așa o vreme, clienții ne vor năvăli În casă."
gameforge.locale.monster_chat_20008_2 = "Oh! Câinele leneș va veni sigur În curând și va vrea să mănânce ceva."
gameforge.locale.monster_chat_20008_3 = "Na, ce amărât!"
gameforge.locale.monster_chat_20008_4 = "Mai demult era un războinic foarte bun."
gameforge.locale.monster_chat_20008_5 = "Mă Întreb, de ce nu mai luptă?"
gameforge.locale.monster_chat_20009_1 = "Tinerii ăștia...atât de plini de viață și imaturi!"
gameforge.locale.monster_chat_20009_2 = "Luptătorii arată mult mai bine când poartă arme."
gameforge.locale.monster_chat_20009_3 = "Acest spirit criminal Împlinește pe fiecare."
gameforge.locale.monster_chat_20009_4 = "Asculta-ti inima, concentreaza-te si elibereaza-ti sufletul."
gameforge.locale.monster_chat_20009_5 = "Nu exista reteta pentru fericire insa, iti pot da cateva sfaturi pe cate te poti axa."
gameforge.locale.monster_chat_20010_1 = "Am adus un obiect bun!"
gameforge.locale.monster_chat_20010_2 = "Bunuri din tinuturi indepartate! Nemaipomenit, lucruri pretioase!"
gameforge.locale.monster_chat_20010_3 = "Veniți Încoace, veniți cu toții Încoace!"
gameforge.locale.monster_chat_20010_4 = "Cumpărați, cumpărați atâta timp cât mai este. Așa ceva nu se găsește peste tot și oricând!"
gameforge.locale.monster_chat_20010_5 = "Sunt prea mulți tâlhari pe aici. Ar trebui să stea departe de bunurile mele!"
gameforge.locale.monster_chat_20011_1 = "Acum este foarte uscat aici."
gameforge.locale.monster_chat_20011_2 = "Recolta de grâne nu este așa de bună ca mai demult."
gameforge.locale.monster_chat_20011_3 = "Ce Împiedică dezvoltarea pe câmpii?"
gameforge.locale.monster_chat_20011_4 = "Cerealele sunt sfaramicioase si au gust de putred."
gameforge.locale.monster_chat_20011_5 = "Semn rău. Agricultura este baza tuturor lucrurilor."
gameforge.locale.monster_chat_20012_1 = "Placinta cu orez! Delicioasa placinta cu orez!"
gameforge.locale.monster_chat_20012_2 = "Vand prajituri de orez dulce!"
gameforge.locale.monster_chat_20012_3 = "Oh! Afacerea merge prost."
gameforge.locale.monster_chat_20012_4 = "Ar trebui să Încerc În alt oraș."
gameforge.locale.monster_chat_20012_5 = "Dar exista asa multe animale salbatice de prada pe aici..."
gameforge.locale.monster_chat_20013_1 = "Într-o astfel de zi, cel mai bine este În barcă."
gameforge.locale.monster_chat_20013_2 = "Vreme perfectă de pescuit."
gameforge.locale.monster_chat_20013_3 = "Acum un al nod...Perfect! Nici un peste nu o sa scape din acest carlig!"
gameforge.locale.monster_chat_20013_4 = "Acest urs blestemat mi-a distrus undița."
gameforge.locale.monster_chat_20013_5 = "Ar trebui sa repar din nou plasele."
gameforge.locale.monster_chat_20014_1 = "Hu!"
gameforge.locale.monster_chat_20014_2 = "Nu Îți face griji din cauza mea. Așa sunt eu."
gameforge.locale.monster_chat_20014_3 = "Haaaaa!"
gameforge.locale.monster_chat_20014_4 = "Ce ar trebui sa fac ca sa... Offf, ce am mai spus acum?"
gameforge.locale.monster_chat_20014_5 = "Nu pot fi curajos. Hu!"
gameforge.locale.monster_chat_20015_1 = "De 20 de ani această secure Îmi servește cu Încredere."
gameforge.locale.monster_chat_20015_2 = "Este Încă ascuțită, dar nu așa ca mai demult."
gameforge.locale.monster_chat_20015_3 = "Aș vrea să o Întăresc, prin folosirea ei și prin faptul că lovesc cu ea."
gameforge.locale.monster_chat_20015_4 = "Dar pentru asta am nevoie de niște pietre bogate În minerale."
gameforge.locale.monster_chat_20015_5 = "Dar cum să mi le procur? "
gameforge.locale.monster_chat_20016_1 = "Clienții Îmi năvălesc pe ușă."
gameforge.locale.monster_chat_20016_2 = "Sper sa termin totul la timp."
gameforge.locale.monster_chat_20016_3 = "Lovitura dupa lovitura si nicio plangere..."
gameforge.locale.monster_chat_20016_4 = "Asa trebuie sa sune lovitura de ciocan!"
gameforge.locale.monster_chat_20016_5 = "Cam cald aici."
gameforge.locale.monster_chat_20017_1 = "Nu Îmi vine să cânt la un instrument."
gameforge.locale.monster_chat_20017_2 = "Instrumentul meu se află Într-o stare Îngrozitoare."
gameforge.locale.monster_chat_20017_3 = "Poate ca ar trebui sa-mi cumpar un instrument nou?"
gameforge.locale.monster_chat_20017_4 = "Instrumentul meu este vechi si uzat... iubesc lucrurile vechi!"
gameforge.locale.monster_chat_20017_5 = "Imi trebuie urgent ceva sa inlocuiesc corzile."
gameforge.locale.monster_chat_20018_1 = "Sunt așa de ocupat!"
gameforge.locale.monster_chat_20018_2 = "În fiecare zi vin alți răniți."
gameforge.locale.monster_chat_20018_3 = "Nu avem timp de distractie!"
gameforge.locale.monster_chat_20018_4 = "Iar medicamentele se termină."
gameforge.locale.monster_chat_20018_5 = "Ar fi bine dacă cineva ar putea procura ingredientele pentru medicamente."
gameforge.locale.monster_chat_20019_1 = "De 30 de ani străbat munții cu arcul."
gameforge.locale.monster_chat_20019_2 = "Astfel mi-am hrănit soția și am plătit pentru educația celor 3 copii."
gameforge.locale.monster_chat_20019_3 = "A fost greu, dar a meritat."
gameforge.locale.monster_chat_20019_4 = "Dar acum s-a rupt coarda arcului. Înseamnă că acum sunt și eu bătrân?"
gameforge.locale.monster_chat_20019_5 = "Daca nu-mi pot repara arcul trebuie sa cumpar unul nou, dar nu am suficienti bani pentru asta."
gameforge.locale.monster_chat_20020_1 = "Hei! La ce te uiți?"
gameforge.locale.monster_chat_20020_2 = "Nu te apropia!"
gameforge.locale.monster_chat_20020_3 = "Oh, cât va trebui să mă mai ascund În acest hal?"
gameforge.locale.monster_chat_20020_4 = "Voi muri uitat Într-un colț?"
gameforge.locale.monster_chat_20020_5 = "Ahhhhh, sper să mă ajute cineva!"
gameforge.locale.monster_chat_20021_1 = "Această zi se va sfârși ca oricare alta."
gameforge.locale.monster_chat_20021_2 = "Ce pot să fac? Hm?"
gameforge.locale.monster_chat_20021_3 = "Nu am crezut niciodată că voi sfârși așa."
gameforge.locale.monster_chat_20021_4 = "Nu-mi vine sa cred ca el nu mai este aici."
gameforge.locale.monster_chat_20021_5 = "El nu merita asta."
gameforge.locale.monster_chat_20022_1 = "Și dinnou o zi bună."
gameforge.locale.monster_chat_20022_2 = "Sotul lui Ariyoung a disparut."
gameforge.locale.monster_chat_20022_3 = "Am vrut să ajut, dar această Încăpățânare..."
gameforge.locale.monster_chat_20022_4 = "Dacă s-ar fi căsătorit cu mine, ar fi putut renunța la viața ei plictisitoare."
gameforge.locale.monster_chat_20022_5 = "Poate ar fi trebuit să Îi arăt mai bine sentimentele mele."
gameforge.locale.monster_chat_20023_1 = "Oh nu! Ce mă fac dacă află soția mea?"
gameforge.locale.monster_chat_20023_2 = "Dacă aș putea trăi din cititul cărților!"
gameforge.locale.monster_chat_20023_3 = "Cărțile sunt atât de misterioase!"
gameforge.locale.monster_chat_20023_4 = "Nu pot trăi așa la nesfârșit, dar..."
gameforge.locale.monster_chat_20023_5 = "Sper că pot face ceva pentru soția mea, dar..."
gameforge.locale.monster_chat_20024_1 = "Haide, pune mai mult efort!"
gameforge.locale.monster_chat_20024_2 = "Ce faci acolo, băiete? Nu merge mai repede?"
gameforge.locale.monster_chat_20024_3 = "Doar imaginează-ți un războinic mânuind o sabie și fă la fel!"
gameforge.locale.monster_chat_20024_4 = "Unde e prostul ăsta? Unde e?"
gameforge.locale.monster_chat_20024_5 = "Acest fiu de cățea! Tu nu ești bărbat. Ha!"
gameforge.locale.monster_chat_2091_1 = "Taie, taie... parul, gatul..."
gameforge.locale.monster_chat_2091_2 = "Am ceva pentru tine: somnul de veci!"
gameforge.locale.monster_chat_2091_3 = "Trage aer in piept. Nu te ingrijora, este doar otrava!"
gameforge.locale.monster_chat_2091_4 = "Iti voi lasa doar fata! Vei arata ca un ornament dragut..."
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Infasurat in matase vei arata ca un aperitiv elegant mai tarziu..."
gameforge.locale.monster_chat_2092_1 = "Nu au mai ramas multi dintre noi."
gameforge.locale.monster_chat_2092_2 = "Ei nu sunt creaturile cu doua picioare care se presupune ca ar conduce aici."
gameforge.locale.monster_chat_2092_3 = "Oh!? Se pare că s-a prins cineva În pânza de păianjen."
gameforge.locale.monster_chat_2092_4 = "Sunt o persoana pasnica. Doar ca nu-mi place sa fiu iritat."
gameforge.locale.monster_chat_2092_5 = "Asta este tot ce poti face?"
gameforge.locale.monster_chat_2092_6 = "Nimeni nu se poate pune cu mine!"
gameforge.locale.monster_chat_2104_1 = "De ce oamenii sunt asa aposi pe dinauntru? Imi rugineste sabia!"
gameforge.locale.monster_chat_2104_2 = "Inca nu si-a facut efectul otrava?"
gameforge.locale.monster_chat_2104_3 = "Cum stă treaba cu luptatul?"
gameforge.locale.monster_chat_2104_4 = "Deja obosit de la arșiță? "
gameforge.locale.monster_chat_2104_5 = "Las cu plăcere pe cineva În urmă În deșert."
gameforge.locale.monster_chat_2104_6 = "Săracul, pur și simplu Îngropat În deșert."
gameforge.locale.monster_chat_2105_1 = "Îți spun un secret. Urăsc mizeria."
gameforge.locale.monster_chat_2105_2 = "Este foarte cald, nu?"
gameforge.locale.monster_chat_2105_3 = "Acest loc este periculos."
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "Te nimeresc Între sprâncene."
gameforge.locale.monster_chat_2106_1 = "Se pare ca acolo este cineva pe care am sa-l pun la pamant."
gameforge.locale.monster_chat_2106_2 = "Oh da, imi vad urmatoarea osanda."
gameforge.locale.monster_chat_2106_3 = "(Râs) Îmi place, Îmi place foarte mult!"
gameforge.locale.monster_chat_2106_4 = "Acela de acolo gras si frumos. E al meu!"
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Iubesc mirosul de sânge."
gameforge.locale.monster_chat_2107_1 = "(Vântură) În ziua de azi e puțin de lucru. (Vântură)"
gameforge.locale.monster_chat_2107_2 = "Hei! Sunt chiar grase!"
gameforge.locale.monster_chat_2107_3 = "Coducatorul va fi incantat..."
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Să mai vânturăm odată piciorul!"
gameforge.locale.monster_chat_2107_6 = "Aceasta sageata este numai pentru tine."
gameforge.locale.monster_chat_2108_1 = "Dacă Îl nimeresc pe vreunul, atunci... "
gameforge.locale.monster_chat_2108_2 = "Hei, de ce te uiți?"
gameforge.locale.monster_chat_2108_3 = "Crezi că sunt ușor de Învins? Hm?"
gameforge.locale.monster_chat_2108_4 = "Așa un ghinion, să mă prindă aici În deșert!"
gameforge.locale.monster_chat_2108_5 = "Ah, sentimentul ăsta!! O să mă bucur de el la nesfârșit!"
gameforge.locale.monster_chat_2108_6 = "Mulțumesc!"
gameforge.locale.monster_chat_2191_1 = "Simt miros de oameni. Ce miros delicios!"
gameforge.locale.monster_chat_2191_2 = "Bine. Veniți la mine."
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "Ce ma bucur ca te-ai ratacit si ai ajuns pe aici!"
gameforge.locale.monster_chat_2191_5 = "Rămâi pe loc! Kkkkkkk!"
gameforge.locale.monster_chat_2191_6 = "Muscatura cu muscatura o sa te las fara carne..."
gameforge.locale.monster_chat_2202_1 = "Apropie-te ca sa-ti smulg pielea."
gameforge.locale.monster_chat_2202_2 = "Stii cate dureri ti-ar putea provoca o temperatura atat de ridicata?"
gameforge.locale.monster_chat_2202_3 = "Poate ar trebui sa-mi sting arsurile cu niste sange proaspat."
gameforge.locale.monster_chat_2202_4 = "Sangele tau...lasa-ma sa-l gust."
gameforge.locale.monster_chat_2202_5 = "Nu exista niciun motiv pentru a incepe o lupta."
gameforge.locale.monster_chat_2202_6 = "Poti deveni ca mine! Numai flacari si groaza."
gameforge.locale.monster_chat_2204_1 = "Atâta timp cât sunt eu aici, nu intră nimeni!"
gameforge.locale.monster_chat_2204_2 = "Dintr-un oarecare motiv Îmi devii o povară."
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Kwuuh, știi unde ești?"
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Esti cel mai patetic oponent al meu!"
gameforge.locale.monster_chat_2205_1 = "O sabie Își Îndeplinește rolul doar În mâna unui războinic."
gameforge.locale.monster_chat_2205_2 = "ține sabia tot timpul În mână."
gameforge.locale.monster_chat_2205_3 = "Războiul bate la ușă."
gameforge.locale.monster_chat_2205_4 = "Și acum luptăm până la moarte!"
gameforge.locale.monster_chat_2205_5 = "Vrei să reziști mai departe atacurilor?"
gameforge.locale.monster_chat_2205_6 = "Da, nu ar trebui să mori așa repede."
gameforge.locale.monster_chat_2206_1 = "Lava este elixirul vieții."
gameforge.locale.monster_chat_2206_2 = "Lava mă ține În viață iar pe tine te va ucide!"
gameforge.locale.monster_chat_2206_3 = "Nu este minunata aceasta lume care clocoteste in lava?"
gameforge.locale.monster_chat_2206_4 = "Indraznesti sa ma deranjezi?"
gameforge.locale.monster_chat_2206_5 = "Ar trebui să pleci dacă Îți pasă de viața ta."
gameforge.locale.monster_chat_2206_6 = "Vei regreta prezenta mea aici."
gameforge.locale.monster_chat_2291_1 = "Este o zi frumoasa. Tot ce as vrea ar fi sa am putina liniste."
gameforge.locale.monster_chat_2291_2 = "Nimic nu se poate măsura cu țara lui Doyyumhwan."
gameforge.locale.monster_chat_2291_3 = "Putin sange proaspat ar fi o schimbare placuta."
gameforge.locale.monster_chat_2291_4 = "Se pare ca-ti cauti moartea, altfel nu m-ai fi deranjat. "
gameforge.locale.monster_chat_2291_5 = "Deja regreti ca ai ajuns aici?"
gameforge.locale.monster_chat_2291_6 = "Prea tarziu sa mai fugi. Infrunta-ti temerile."
gameforge.locale.monster_chat_301_1 = "Aceste vremuri tensionate ma fac sa-mi pierd rabdarea."
gameforge.locale.monster_chat_301_2 = "Acum doua zile o fata tanara a fost mutilata de animalele din aceasta zona."
gameforge.locale.monster_chat_301_3 = "Animalele salbatice devin din ce in ce mai agresive. Trebuie sa omoram cat de multe putem."
gameforge.locale.monster_chat_301_4 = "Azi nu sunt intr-o dispozitie buna. Strainii ar trebui sa stea departe de mine."
gameforge.locale.monster_chat_301_5 = "Ultimul războinic pe care l-am omorât mă implora pentru viața lui."
gameforge.locale.monster_chat_302_1 = "Cineva trebuie sa faca ceva cu aceste pietre."
gameforge.locale.monster_chat_302_2 = "Nu am incredere in dimineata asta linistita."
gameforge.locale.monster_chat_302_3 = "Presiunea atmosferica imi da o stare de inconfort."
gameforge.locale.monster_chat_302_4 = "Aceste sageti au fost ascutite foarte bine."
gameforge.locale.monster_chat_302_5 = "Daca as fi in locul tau as avea grija. Sageata se indreapta chiar spre inima ta."
gameforge.locale.monster_chat_302_6 = "Ar trebui sa merg acolo si sa ucid cateva dintre acele bestii."
gameforge.locale.monster_chat_303_1 = "Învățătura artelor marțiale este esențială În aceste vremuri."
gameforge.locale.monster_chat_303_2 = "Ce se Întâmplă dacă deodată dau buzna soldați ai guvernului?"
gameforge.locale.monster_chat_303_3 = "De ce te uiți așa la mine? "
gameforge.locale.monster_chat_303_4 = "Lasii au o sansa mica de supravietuire."
gameforge.locale.monster_chat_303_5 = "Nu te supraestima."
gameforge.locale.monster_chat_303_6 = "Se pare ca cineva te-a antrenat foarte bine."
gameforge.locale.monster_chat_304_1 = "Chiar si stramosii mei au reusit sa se afirme in lupta pe aceste teritorii."
gameforge.locale.monster_chat_304_2 = "Trebuie sa stai in alerta si bine pregatit pe aici."
gameforge.locale.monster_chat_304_3 = "Nici sa nu te gandesti sa ma faci sa-mi pierd timpul."
gameforge.locale.monster_chat_304_4 = "Deci, cum te simți când viața ta este În pericol?"
gameforge.locale.monster_chat_304_5 = "Oh nu! Renunți deja? "
