gameforge.locale.monster_chat_1038_6 = "Hızlı ölmek güzel değil - önce seninle eğlenmek istiyoruz... "
gameforge.locale.monster_chat_1039_1 = "Yeni biri gelene kadar... "
gameforge.locale.monster_chat_1039_2 = "Gerçekten kaçabileceğini düşünüyor musun? "
gameforge.locale.monster_chat_1039_3 = "Buradaki herşey insanları korkutuyor; çok, çok korkutuyor... "
gameforge.locale.monster_chat_1039_4 = "Evimize hoşgeldin (kahkaha) "
gameforge.locale.monster_chat_1039_5 = "Gördün mü? Iskaladın... "
gameforge.locale.monster_chat_1039_6 = "Hmm, çok taze... "
gameforge.locale.monster_chat_1040_1 = "Senin tüm hareketlerini görüyoruz. "
gameforge.locale.monster_chat_1040_2 = "Yavaş ol. "
gameforge.locale.monster_chat_1040_3 = "Eğer birine dokunursan, ölürsün. (kahkaha) "
gameforge.locale.monster_chat_1040_4 = "Çanımın sesini duyuyor musun? Bu duyduğun son ses olacak! "
gameforge.locale.monster_chat_1040_5 = "Eğer bu yara kanamaya devam ederse, senin için iyi olmayacak... "
gameforge.locale.monster_chat_1040_6 = "Evet! Savaş, savaş! (kahkaha) "
gameforge.locale.monster_chat_1041_1 = "Ahahahahahaha "
gameforge.locale.monster_chat_1041_2 = "Yeni bedenler gelmiş. "
gameforge.locale.monster_chat_1041_3 = "Evet, şimdi bu yeni bedenleri alma zamanı. "
gameforge.locale.monster_chat_1041_4 = "Biz yeniden diriliriz. Biz ölümlü değiliz! "
gameforge.locale.monster_chat_1041_5 = "Bize saldırılabilecek bir yol yok! (kahkaha) "
gameforge.locale.monster_chat_1041_6 = "Ne alacaksın: Tükenip gitmeyi mi yoksa ölmeyi mi? "
gameforge.locale.monster_chat_1091_1 = "Kim benden daha hızlı uçar ve saldırı yapar? "
gameforge.locale.monster_chat_1091_2 = "Benim gibi olacaksın. "
gameforge.locale.monster_chat_1091_3 = "Benim gibi olmayı istemiyor musun? "
gameforge.locale.monster_chat_1091_4 = "Bedenin ve ruhun bana ait! (kahkaha) "
gameforge.locale.monster_chat_1091_5 = "Tükendin mi? Dizlerin titriyor... "
gameforge.locale.monster_chat_1091_6 = "Daha ne kadar bu şekilde devam edebilirsin? (kahkaha) "
gameforge.locale.monster_chat_1093_1 = "Buraya gelenler için ölüm daha iyi bir seçenek değil midir? "
gameforge.locale.monster_chat_1093_2 = "Hoşgeldin. "
gameforge.locale.monster_chat_1093_3 = "Av ol. "
gameforge.locale.monster_chat_1093_4 = "Bedenin ve ruhunla ziyafet yapacağım! "
gameforge.locale.monster_chat_1093_5 = "Daha ne kadar dayanabilirsin? Ha? "
gameforge.locale.monster_chat_1093_6 = "Ölüm arkandan gülümsüyor. (kahkaha) "
gameforge.locale.monster_chat_11000_1 = "Kendi gelişimleri için endişelenen kim varsa dinlesin! "
gameforge.locale.monster_chat_11000_10 = "Eğer doğanın ve şimşeklerin gücünü kontrol etmek istiyorsanız. İyileştirme eğitimini tavsiye ederim! "
gameforge.locale.monster_chat_11000_11 = "Her savaşçı farklı bir yolla savaşır! Bedenini güçlendirmeye odaklan! "
gameforge.locale.monster_chat_11000_12 = "Sura, gücünü büyüden alır. Zeka ve hızını geliştir ama beden gücünü ihmal etme! "
gameforge.locale.monster_chat_11000_13 = "Ninjanın yeteneklerini kullan! Hız ve beden gücüne çalış! "
gameforge.locale.monster_chat_11000_14 = "Şamanlar yenileme, destekleme ve uzak dövüş büyüsü kullanıyor. Zekalı olmak lazım! "
gameforge.locale.monster_chat_11000_15 = "Çıraklığını unutma! "
gameforge.locale.monster_chat_11000_16 = "Yeteneklerini geliştirmek istiyorsan, bir eğitim dalının çıraklığına katıl! "
gameforge.locale.monster_chat_11000_17 = "Yetenekler? Yeteneklerinde rastgele olarak 17-21 seviyeler arasında ustalaşabilirsin. "
gameforge.locale.monster_chat_11000_18 = "Grand Master gücüne erişmek ister misin? O zaman, özel beceri kitaplarına ihtiyacın var! "
gameforge.locale.monster_chat_11000_19 = "Beceri kitabını bir defa okuğunda, kitap yok olur! "
gameforge.locale.monster_chat_11000_2 = "5. seviyenin altındaki tüm savaşçılar, beni dinleyin! "
gameforge.locale.monster_chat_11000_20 = "Bir beceri kitabı daha okumak ister misin? Öyleyse 1 gün bekle. "
gameforge.locale.monster_chat_11000_21 = "Sura yeteneklerini geliştirmek için zekanı yükselt! "
gameforge.locale.monster_chat_11000_22 = "Suranın büyük gücünü savaştayken deneyimle! "
gameforge.locale.monster_chat_11000_23 = "Gizli mezhebin tapınağı nerede biliyor musun? Seungryong vadisinin tam ortasında! "
gameforge.locale.monster_chat_11000_24 = "Örümcek mağaraları tekrar göründü! Yongbi çölünün ortasındalar! "
gameforge.locale.monster_chat_11000_25 = "Görevleri bitir ve ödülleri eşyalarını geliştirmek için kullan! "
gameforge.locale.monster_chat_11000_26 = "Balıkçılık mı? Kulağa harika geliyor. Balıkçıdan zaten bir olta ve kanca almamış mıydın? "
gameforge.locale.monster_chat_11000_27 = "Avlayacak daha güçlü birşeyler mi arıyorsun? Kapıyı bul. Oradan da seviyene uygun avlanma bölgesine git! "
gameforge.locale.monster_chat_11000_28 = "Diğer krallıklardan gelecek düşmanlara karşı hazırlıklı ol. Seni ne vakit arkandan vuracaklarını kestiremezsin! "
gameforge.locale.monster_chat_11000_29 = "Yeteneklerini sıfırlamayı mı düşünüyorsun? Yaşlı kadına git. Yanında yeterince Yang götürdüğünden emin ol! "
gameforge.locale.monster_chat_11000_3 = "Eğer saldırgan bir savaşçı olmak istiyorsan, yakın dövüş öğretmenine git ve eğitim al. "
gameforge.locale.monster_chat_11000_30 = "Hedefin perfect master seviyesi olsun! Gerçek bir kahraman olabilmenin başka yolu yok! "
gameforge.locale.monster_chat_11000_31 = "Eğer başka bir cephe veya şehre gitmek istersen kıtadaki yaşlı adamı bul. "
gameforge.locale.monster_chat_11000_4 = "Savaşçı yeteneklerinde usta olmak istiyorsan, zihinsel savaş eğitimlerine katıl! "
gameforge.locale.monster_chat_11000_5 = "Eğer büyülü kılıçlar kullanan bir Sura olmak istiyorsan, büyülü silah eğitimine katıl! "
gameforge.locale.monster_chat_11000_6 = "Kara büyü öğrenmek ve ustalaşmak istiyorsan, kara büyü eğitimlerine katıl! "
gameforge.locale.monster_chat_11000_7 = "Uzak dövüş eğitimi seviye 5 ve üstü ninjalarla dolu! "
gameforge.locale.monster_chat_11000_8 = "Eğer gizli bir ninja olacaksan, yakın dövüş eğitimlerine katıl. "
gameforge.locale.monster_chat_11000_9 = "Eğer savaşları kontrol eden bir Ejderha Tanrı savaşçısı olacaksan, Ejderha gücü eğitimini al! "
