gameforge.locale.monster_chat_1105_1 = "Donmuş hal iyi. "
gameforge.locale.monster_chat_1105_2 = "Sohan Dağı kesinlikle en iyisi. "
gameforge.locale.monster_chat_1105_3 = "Hava ne güzel. "
gameforge.locale.monster_chat_1105_4 = "Aynı benim gibi olacaksın. "
gameforge.locale.monster_chat_1105_5 = "Sonsuza dek yaşayabilirsin, en azından buzlar eriyene dek. "
gameforge.locale.monster_chat_1105_6 = "Buza dönüşmen ne kadar sürecek? "
gameforge.locale.monster_chat_1106_1 = "İnsanlar...burada?! "
gameforge.locale.monster_chat_1106_2 = "Yorgun... ama senin için yeterince uyanığım... "
gameforge.locale.monster_chat_1106_3 = "Benim dağımda, birisi mi var?  "
gameforge.locale.monster_chat_1106_4 = "Sen buraya ait değilsin! "
gameforge.locale.monster_chat_1106_5 = "En iyisi geri dön! "
gameforge.locale.monster_chat_1106_6 = "Yabancı...hemde yalnız? "
gameforge.locale.monster_chat_1191_1 = "Bakalım yakışıklı bir adam gelir mi. "
gameforge.locale.monster_chat_1191_2 = "Çok soğuk ve yalnız. Benim gelecekteki kocam nerede? "
gameforge.locale.monster_chat_1191_3 = "Onlarla biraz çalışabilir miyim? "
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Birşeyler eksik. Ben soytarılık yapmıyorum. "
gameforge.locale.monster_chat_1191_6 = "Hissedeceğin son acıyı da ben bitireceğim! "
gameforge.locale.monster_chat_1301_1 = "Bir uzunboyun...! "
gameforge.locale.monster_chat_1301_2 = "Soluk benzin bir dakika içinde daha da solacak. "
gameforge.locale.monster_chat_1301_3 = "Bana meydan okumak mı istiyorsun? Güzel! "
gameforge.locale.monster_chat_1301_4 = "Bana saldırdın! Pweck! Kurtuluşuna müsaade edilmeyecek! "
gameforge.locale.monster_chat_1301_5 = "Zayıf, zayıf insan! "
gameforge.locale.monster_chat_1301_6 = "Hadi, ne kadar dayanabileceğini görelim! "
gameforge.locale.monster_chat_1302_1 = "Kaçmak? Aklından bile geçirme. "
gameforge.locale.monster_chat_1302_2 = "Aferin... Yaklaş... Pwiehk! "
gameforge.locale.monster_chat_1302_3 = "Elimdeki kılıç, titriyor. Pweck! "
gameforge.locale.monster_chat_1302_4 = "Bu mücadeleden sağ çıkamayacaksın! "
gameforge.locale.monster_chat_1302_5 = "İşte kılıcım geliyor! "
gameforge.locale.monster_chat_1302_6 = "Acı verici, değil mi? (kahkaha) "
gameforge.locale.monster_chat_1303_1 = "Senin üzerine bastığın ot bile yanlışlıkla ölmüştür. "
gameforge.locale.monster_chat_1303_2 = "Otlara dikkat et. "
gameforge.locale.monster_chat_1303_3 = "Doğru. Yaklaş bana. (kahkaha) "
gameforge.locale.monster_chat_1303_4 = "Seni korkak! Bana gel ki seni öldüreyim! "
gameforge.locale.monster_chat_1303_5 = "Öl! Öl! "
gameforge.locale.monster_chat_1303_6 = "Geldiğine pişman mısın? "
gameforge.locale.monster_chat_1305_1 = "Kim beni yok edebilir? "
gameforge.locale.monster_chat_1305_2 = "Sen mi? Savaşmak mı istiyorsun? "
gameforge.locale.monster_chat_1305_3 = "Eğer istiyorsan buraya gel! "
gameforge.locale.monster_chat_1305_4 = "Gel şu işi doğru dürüst yap! Kaçma! "
gameforge.locale.monster_chat_1305_5 = "Kılıcım nasıl? "
gameforge.locale.monster_chat_1305_6 = "Korktun mu? (kahkaha) "
gameforge.locale.monster_chat_1901_1 = "Bugün kimi dönüştüreceğim? "
gameforge.locale.monster_chat_1901_2 = "Ah, sevdim seni... "
gameforge.locale.monster_chat_1901_3 = "Nasıldı? Benim sevgili eğlencem... "
gameforge.locale.monster_chat_1901_4 = "Hey! Şu işi doğru yap ve korkak tavuklar gibi kaçma! "
gameforge.locale.monster_chat_1901_5 = "Zaten büyümün etkisindesin? Hmm? "
gameforge.locale.monster_chat_1901_6 = "Biraz çığlık atmak ister misin? (kahkaha) "
gameforge.locale.monster_chat_20001_1 = "Gergin görünüyorsun... Merak etme, hiçbirşey patlamayacak. "
gameforge.locale.monster_chat_20001_2 = "Bazen, ben de gezinmek istiyorum. "
gameforge.locale.monster_chat_20001_3 = "Bu çok sıkıcı. Torunlarım bile artık beni ziyaret etmiyor. "
gameforge.locale.monster_chat_20001_4 = "Yaşlandığın zaman, başka insanlara ihtiyaç duymaya başlıyorsun... "
gameforge.locale.monster_chat_20001_5 = "En güzel günlerim gitti... "
gameforge.locale.monster_chat_20002_1 = "Güneş çok sıcak. "
gameforge.locale.monster_chat_20002_2 = "Islak kıyafetleri kurutmak için harika bir gün, değil mi? "
gameforge.locale.monster_chat_20002_3 = "Kocam olacak işe yaramaz adam nerde? "
gameforge.locale.monster_chat_20002_4 = "Kendine baba diyor ve çocukların önünde bile kötü alışkanlıklarından utanmıyor! "
gameforge.locale.monster_chat_20002_5 = "Kitap kitaptır. Hiç para kazanmıyor. Sadece kitap okuyor. "
gameforge.locale.monster_chat_20003_1 = "Ooohh, dududu, bebek. İyi uyudun mu? "
gameforge.locale.monster_chat_20003_2 = "Küçük bir melek gibi gülümsüyorsun. "
gameforge.locale.monster_chat_20003_3 = "Sağlıklı olduğunda annen çok mutlu oluyor. "
gameforge.locale.monster_chat_20003_4 = "Yine mi ilgi istiyorsun? Karnın mı acıktı bakalım? "
gameforge.locale.monster_chat_20003_5 = "İyi çocuk... Uyu, güzel çocuğum, uyu... "
gameforge.locale.monster_chat_20004_1 = "Sadece başka bir gün. "
gameforge.locale.monster_chat_20004_2 = "Bugün nereden yemek bulacağım? "
gameforge.locale.monster_chat_20004_3 = "Bu kılıç sahipleri çok sıkıcı. "
gameforge.locale.monster_chat_20004_4 = "Bakalım. Belki de Octavio'nun restorantına gitmeliyim. "
gameforge.locale.monster_chat_20004_5 = "Ah, hazır söylemişken, savaş sanırım artık devam etmiyor.. "
gameforge.locale.monster_chat_20005_1 = "Usta çömlekçinin ruhu parmak uçlarından geçiyor. "
gameforge.locale.monster_chat_20005_2 = "Ülke kaosa girdiğinden beri, her yerde savaşçı bulmak mümkün. "
gameforge.locale.monster_chat_20005_3 = "Herkes savaşmak istiyor. "
gameforge.locale.monster_chat_20005_4 = "Mutlu ve huzurlu bir dünya için çok dua ediyorum. "
gameforge.locale.monster_chat_20005_5 = "Hey, orda mısın? Birkaç dakika rahatla ve düşün... "
gameforge.locale.monster_chat_20006_1 = "Off.. Ne yapmalıyım? "
gameforge.locale.monster_chat_20006_2 = "Kardeşimden en ufak bir iz yok, ama burada olacağına söz vermişti. "
gameforge.locale.monster_chat_20006_3 = "Ve şimdi, burada gördüğüm bütün şey silahlı savaşçılar. "
gameforge.locale.monster_chat_20006_4 = "Kardeşimi özledim. "
gameforge.locale.monster_chat_20006_5 = "Kardeşimi gören oldu mu? "
gameforge.locale.monster_chat_20007_1 = "Hm, bu gerçekten taze et! "
gameforge.locale.monster_chat_20007_2 = "Bu güzel, insanlar artık daha fazla et yiyebilecek. "
gameforge.locale.monster_chat_20007_3 = "Ticaret iyi işliyor, ama et hala nadiren bulunuyor. "
gameforge.locale.monster_chat_20007_4 = "Verdiğimiz et siparişi ne zaman gelir? "
gameforge.locale.monster_chat_20007_5 = "Eğer zamanında gelmezse, avlanmak zorunda kalacağız. "
gameforge.locale.monster_chat_20008_1 = "Umarım havalar güzelleştiğinde müşteriler çoğalır. "
gameforge.locale.monster_chat_20008_2 = "Yaramaz köpeğim birazdan gelir ve yiyecek birşeyler ister. "
gameforge.locale.monster_chat_20008_3 = "Ah, ne fakir bir komşu! "
gameforge.locale.monster_chat_20008_4 = "O harika bir savaşçıydı. "
gameforge.locale.monster_chat_20008_5 = "Neden artık savaşmadığını merak ediyorum. "
gameforge.locale.monster_chat_20009_1 = "Gençler... ne kadar canlı ve hayat dolular! "
gameforge.locale.monster_chat_20009_2 = "Savaşçılar, silah taşırken daha güzel görünüyorlar. "
gameforge.locale.monster_chat_20009_3 = "Şu günlerde herkeste katil bir zihniyet mevcut. "
gameforge.locale.monster_chat_20009_4 = "Kalbini kontrol et, düşüncelerine odaklan ve ruhunu sakinleştir. "
gameforge.locale.monster_chat_20009_5 = "Mutluluğun mutlak bir formülü yoktur ama bağlı kalman gereken bazı basit prensipler var. "
gameforge.locale.monster_chat_20010_1 = "Sana harika bir eşya aldım. "
gameforge.locale.monster_chat_20010_2 = "Uzak ülkelerin malları! Güzel, olağanüstü parçalar! "
gameforge.locale.monster_chat_20010_3 = "Buraya gel! Herkes, buraya gelsin! "
gameforge.locale.monster_chat_20010_4 = "Gelin millet! Satın alın! Bir daha şansınız olmayacak! "
gameforge.locale.monster_chat_20010_5 = "Puff! Neden çok fazla hırsız var burada? "
gameforge.locale.monster_chat_20011_1 = "Son günlerde kuraklık var... "
gameforge.locale.monster_chat_20011_2 = "Hasat eskisi gibi iyi değil. "
gameforge.locale.monster_chat_20011_3 = "Neden artık tarlalar verimli değil? "
gameforge.locale.monster_chat_20011_4 = "Tahıl kıtır kıtır ve tadı berbat. "
gameforge.locale.monster_chat_20011_5 = "Bu kötüye işaret. Tarımcılık bizim temel yaşam kaynağımız. "
gameforge.locale.monster_chat_20012_1 = "Yaş Pasta! Çok lezzetli yaş pasta burada! "
gameforge.locale.monster_chat_20012_2 = "Ben pirinç pastası satıyorum! "
gameforge.locale.monster_chat_20012_3 = "Evet, ticaret kötüleşti şu günlerde! "
gameforge.locale.monster_chat_20012_4 = "Belki başka bir şehre gitmeliyim. "
gameforge.locale.monster_chat_20012_5 = "Fakat yolda çok fazla kaplan var. "
gameforge.locale.monster_chat_20013_1 = "Böyle günlerde bir sandalın içinde olmak başa gelebilecek en güzel şey sanırım. "
gameforge.locale.monster_chat_20013_2 = "Balık tutmak için harika bir hava var. "
gameforge.locale.monster_chat_20013_3 = "Bir düğüm daha... Tamam! Bu kancadan hiç bir balık kaçamaz! "
gameforge.locale.monster_chat_20013_4 = "Bu pis ayı benim oltamı kırdı! "
gameforge.locale.monster_chat_20013_5 = "Ağlarımı tekrar tamir etmeliyim. "
gameforge.locale.monster_chat_20014_1 = "Ha?! "
gameforge.locale.monster_chat_20014_2 = "Benim için endişelenme; ben böyleyim. "
gameforge.locale.monster_chat_20014_3 = "Haaaaa! "
gameforge.locale.monster_chat_20014_4 = "Ne yapabilirim ki, yani o... İyi de, ben şimdi ne dedim ki? "
gameforge.locale.monster_chat_20014_5 = "Artık eskisi gibi cesur değilim. "
gameforge.locale.monster_chat_20015_1 = "Bu balta 20 senedir bana çalışır. "
gameforge.locale.monster_chat_20015_2 = "Hala keskin, ama sanırım eskisi kadar iyi değil. "
gameforge.locale.monster_chat_20015_3 = "Onu kullanmak ve güçlendirmek istiyorum. "
gameforge.locale.monster_chat_20015_4 = "Ama bunun için tamamen mineralli taşlara ihtiyacım var. "
gameforge.locale.monster_chat_20015_5 = "Peki bu taşları nereden bulabilirim? "
gameforge.locale.monster_chat_20016_1 = "Müşteriler çok çok geliyorlar. "
gameforge.locale.monster_chat_20016_2 = "Umarım her şeyi zamanında bitirebilirim. "
gameforge.locale.monster_chat_20016_3 = "Darbe üstüne darbe ve hiç şikayet yok... "
gameforge.locale.monster_chat_20016_4 = "İşte bir çekiç böyle ses çıkartmalı! "
gameforge.locale.monster_chat_20016_5 = "Burası çok sıcak. "
gameforge.locale.monster_chat_20017_1 = "Bir çalgı çalıyormuş gibi hissetmiyorum. "
gameforge.locale.monster_chat_20017_2 = "Benim çalgım gerçekten kötü durumda. "
gameforge.locale.monster_chat_20017_3 = "Belki de yeni bir enstrüman almalıyım? "
gameforge.locale.monster_chat_20017_4 = "Enstrümanım eski ve yıpranmış... Eski şeyleri severim! "
gameforge.locale.monster_chat_20017_5 = "Bu kopmuş telleri acilen değiştirmeliyim. "
gameforge.locale.monster_chat_20018_1 = "Çok meşgulüm! "
gameforge.locale.monster_chat_20018_2 = "Her gün, yeni bir yaralı insan geliyor. "
gameforge.locale.monster_chat_20018_3 = "Dışarı çıkıp yeni insanlarla tanışmaya vakit yok! "
gameforge.locale.monster_chat_20018_4 = "Ve ilaçsız kalıyoruz. "
gameforge.locale.monster_chat_20018_5 = "Keşke bizim için dışarı gidip ilaç malzemeleri getirecek biri olsa. "
gameforge.locale.monster_chat_20019_1 = "30 yıldır, dağlarda yayımla avlanırım. "
gameforge.locale.monster_chat_20019_2 = "Bununla karıma baktım, 3 çocuk büyüttüm. "
gameforge.locale.monster_chat_20019_3 = "Hayat zor ama buna değer. "
gameforge.locale.monster_chat_20019_4 = "Fakat şimdi yayımın teli koptu ve artık avlanamıyorum. Bu şekilde aileme bakamam. "
gameforge.locale.monster_chat_20019_5 = "Eğer okumu onaramazsam, emekli olacağım. Aileme artık bakamıyorum. "
gameforge.locale.monster_chat_20020_1 = "Hey, sen neye bakıyorsun? "
gameforge.locale.monster_chat_20020_2 = "Buradan uzak dur! "
gameforge.locale.monster_chat_20020_3 = "Ah, ne kadar süre daha saklamalıyım. "
gameforge.locale.monster_chat_20020_4 = "Bir yerde unutularak ölecek miyim? "
gameforge.locale.monster_chat_20020_5 = "Ah, umarım biri bana yardımcı olur... "
gameforge.locale.monster_chat_20021_1 = "Bugün de diğer günlerden farksız... "
gameforge.locale.monster_chat_20021_2 = "Şimdi ne yapacağım ha?! "
gameforge.locale.monster_chat_20021_3 = "Bunun böyle biteceğini hiç düşünmemiştim. "
gameforge.locale.monster_chat_20021_4 = "Hayır! Bunu bitirecek tek kişi o olamaz! "
gameforge.locale.monster_chat_20021_5 = "Umarım intikamını alır! "
gameforge.locale.monster_chat_20022_1 = "Harika bir başka gün. "
gameforge.locale.monster_chat_20022_2 = "Ariyoung'un kocası kayboldu. "
gameforge.locale.monster_chat_20022_3 = "Yardım etmek istedim, ama çok inatçı... "
gameforge.locale.monster_chat_20022_4 = "Eğer benimle evlenseydi, sıkıcı hayatını geride bıraktırabilirdim. "
gameforge.locale.monster_chat_20022_5 = "Belki ona duygularımı daha açık ifade etmeliyim. "
gameforge.locale.monster_chat_20023_1 = "Tanrım hayır! Karım fark ederse ne yaparım? "
gameforge.locale.monster_chat_20023_2 = "Keşke sadece kitap okuyarak yaşayabilsem! "
gameforge.locale.monster_chat_20023_3 = "Kitaplar çok gizemli. "
gameforge.locale.monster_chat_20023_4 = "Bu şekilde sonsuza dek süremez ki, ama... "
gameforge.locale.monster_chat_20023_5 = "Umarım eşim için birşey yapabilirim, ama... "
gameforge.locale.monster_chat_20024_1 = "Hadi ama! Hareketlerine biraz duygusallık katmalısın! "
gameforge.locale.monster_chat_20024_2 = "Ne yapıyorsun!? Biraz daha hızlı olamaz mısın? "
gameforge.locale.monster_chat_20024_3 = "Kılıcını kaldırıp aynı şeyi yapan bir savaşçı hayal et! "
gameforge.locale.monster_chat_20024_4 = "Nerde bu aptal? Nerede şimdi? "
gameforge.locale.monster_chat_20024_5 = "Korkak! Yeterince cesur değilsin! Ha! "
gameforge.locale.monster_chat_2091_1 = "Kes, kes... saçları, boğazları.. "
gameforge.locale.monster_chat_2091_2 = "Senin için bir şeyim var: sonsuz uyku! "
gameforge.locale.monster_chat_2091_3 = "Derin bir nefes al. Merak etme, bu sadece zehir! "
gameforge.locale.monster_chat_2091_4 = "Sadece yüzünü bırakacağım! Güzel bir süs eşyası olacak... "
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Seni ipek ağımla kaplayıp sonraki öğün için saklayacağım... "
gameforge.locale.monster_chat_2092_1 = "Karımın dırdırına katlanamıyorum. "
gameforge.locale.monster_chat_2092_2 = "Neden uyuyamıyorum? "
gameforge.locale.monster_chat_2092_3 = "Ah, görünüşe göre birileri örümcek ağına takılmış. "
gameforge.locale.monster_chat_2092_4 = "Ben barış yanlısıyım. Sadece rahatsız edilmeyi sevmiyorum. "
gameforge.locale.monster_chat_2092_5 = "Eveett, hepsini ver! "
gameforge.locale.monster_chat_2092_6 = "Kimse boyumun ölçüsünü alamaz! "
gameforge.locale.monster_chat_2104_1 = "İnsanların içi neden bu kadar ıslak? Bu yüzden kılıcım paslanıyor! "
gameforge.locale.monster_chat_2104_2 = "Kuyruğundaki zehir kurudu mu? "
gameforge.locale.monster_chat_2104_3 = "Eee, nasıl dövüştü? "
gameforge.locale.monster_chat_2104_4 = "Sıcaktan bitkinleşmiş gibisin? "
gameforge.locale.monster_chat_2104_5 = "İnsanları çölde bırakmayı seviyorum. "
gameforge.locale.monster_chat_2104_6 = "Seni zavallı, çöle gömüleceksin! "
gameforge.locale.monster_chat_2105_1 = "Sana bir sır vereceğim - ben pislikten nefret ederim! "
gameforge.locale.monster_chat_2105_2 = "Çok sıcak değil mi? "
gameforge.locale.monster_chat_2105_3 = "Burası tehlikeli. "
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "Gözlerinin ortasından vuracağım seni! "
gameforge.locale.monster_chat_2106_1 = "Bugün birşeyler aldın mı? "
gameforge.locale.monster_chat_2106_2 = "Ah evet, avlanmak için birileri gelmiş. "
gameforge.locale.monster_chat_2106_3 = "(Kahkaha) Bunu sevdim; bunu çok sevdim! "
gameforge.locale.monster_chat_2106_4 = "Şu taraftaki güzel ve tombul. O benim! "
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Kan kokusunu seviyorum. "
gameforge.locale.monster_chat_2107_1 = "Bu günlerde yapacak fazla birşey yok. "
gameforge.locale.monster_chat_2107_2 = "Hey, bunlar gerçekten şişman! "
gameforge.locale.monster_chat_2107_3 = "Liderimiz buna bayılacak... "
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Hadi yürüyelim. "
gameforge.locale.monster_chat_2107_6 = "Ok geliyor! "
gameforge.locale.monster_chat_2108_1 = "Eğer birini alabilirsem, alacağım... "
gameforge.locale.monster_chat_2108_2 = "Hey, neden arkana bakıyorsun? "
gameforge.locale.monster_chat_2108_3 = "Sence kolayca yenilecek birine mi benziyorum? Hmm? "
gameforge.locale.monster_chat_2108_4 = "Çok kötü, çölde bana koşmalıydın! "
gameforge.locale.monster_chat_2108_5 = "Ah, şu his; Sonsuza dek hoşuma gidecek! "
gameforge.locale.monster_chat_2108_6 = "Teşekkürler! "
gameforge.locale.monster_chat_2191_1 = "İnsan kokuları alıyorum - çok lezzetli! "
gameforge.locale.monster_chat_2191_2 = "Güzel. Bana gel, bana gel... "
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "Çölde birilerine rastlamak güzel! "
gameforge.locale.monster_chat_2191_5 = "Dur öyle! Kkkkk! "
gameforge.locale.monster_chat_2191_6 = "Hadi önce küçük bir ısırık alalım... "
gameforge.locale.monster_chat_2202_1 = "Neden bu kadar sıcak olduğunu gerçekten anlamıyorum. "
gameforge.locale.monster_chat_2202_2 = "Keşke serin birşeyler olsaydı... "
gameforge.locale.monster_chat_2202_3 = "Belki onların kanlarıyla serinleyebilirim. "
gameforge.locale.monster_chat_2202_4 = "Sıcak, sıcak! Kanlarıyla serinlemeliyim. "
gameforge.locale.monster_chat_2202_5 = "Bana kanını ver! "
gameforge.locale.monster_chat_2202_6 = "Benim gibi ol! "
gameforge.locale.monster_chat_2204_1 = "Ben burada oldukça, kimse giremez! "
gameforge.locale.monster_chat_2204_2 = "Neden bilmiyorum ama, beni sinirlendirmeye başlıyorsun. "
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Kwuuh, nerede olduğunu biliyor musun? "
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Çok aşırı zayıf! (kahkaha) "
gameforge.locale.monster_chat_2205_1 = "Bir kılıç ancak bir savaşçının ellerinde işe yarar. "
gameforge.locale.monster_chat_2205_2 = "Kılıcı ellerinde tut. "
gameforge.locale.monster_chat_2205_3 = "Savaş geliyor. "
gameforge.locale.monster_chat_2205_4 = "Ve şimdi, birimiz ölene kadar savaşacağız! "
gameforge.locale.monster_chat_2205_5 = "Ölüme direnebildiğin kadar direnmek istiyor musun? "
gameforge.locale.monster_chat_2205_6 = "Evet, çok hızlı ölmemelisin. "
gameforge.locale.monster_chat_2206_1 = "Hayatın özütü lavdır. "
gameforge.locale.monster_chat_2206_2 = "Lav banyosunda kendimi yenilemeyi seviyorum! "
gameforge.locale.monster_chat_2206_3 = "Lav eskisi gibi değil. "
gameforge.locale.monster_chat_2206_4 = "Seni alçak! Huzurumu bozdun! "
gameforge.locale.monster_chat_2206_5 = "Mahremiyetime karışma. "
gameforge.locale.monster_chat_2206_6 = "Eğer karışırsan, pişman ederim... "
gameforge.locale.monster_chat_2291_1 = "Hava güzel. Sadece uyumak hoş olacak. "
gameforge.locale.monster_chat_2291_2 = "Doyyumhwan gibi bir ülke daha yok. "
gameforge.locale.monster_chat_2291_3 = "Neden uyumaya gitmiyorum ki? "
gameforge.locale.monster_chat_2291_4 = "Beni rahatsız ettin. "
gameforge.locale.monster_chat_2291_5 = "Beni rahatsız ettiğine pişman olacaksın. "
gameforge.locale.monster_chat_2291_6 = "Kaçmak için çok geç. Ve bu seçenek sana yardımcı olmayacak. "
gameforge.locale.monster_chat_301_1 = "Çok sıkıldım. Yapacak ilginç birşey yok mu? "
gameforge.locale.monster_chat_301_2 = "İki gün önce, gerçekten yakışıklı biri gelmişti. "
gameforge.locale.monster_chat_301_3 = "Hadi saçlarımızı yapalım, kız kardeşlerim! "
gameforge.locale.monster_chat_301_4 = "Sorun yok! Hehe, güzel görünmek yetmez. "
gameforge.locale.monster_chat_301_5 = "Bu kılıcı istiyor musun? Şaka yapmıyoruz. "
gameforge.locale.monster_chat_302_1 = "Hey! Bu adam yakışıklı görünüyor! "
gameforge.locale.monster_chat_302_2 = "Bilirsin, diyet yapıyorum. "
gameforge.locale.monster_chat_302_3 = "Alışverişe gitmek istiyorum. "
gameforge.locale.monster_chat_302_4 = "Bu benim okumdu! "
gameforge.locale.monster_chat_302_5 = "Gördün mü? Kalbine nişan aldım. "
gameforge.locale.monster_chat_302_6 = "Tanrım! Saçmalık! "
gameforge.locale.monster_chat_303_1 = "Bu günlerde, çocuklarımız çok tembelleşmiş. "
gameforge.locale.monster_chat_303_2 = "Eğer askerler bütün sessizlik içinden yıldırım gibi yağarsa ne olur? "
gameforge.locale.monster_chat_303_3 = "Neden bana öyle bakıyorsun? "
gameforge.locale.monster_chat_303_4 = "Tanrım, korkak! "
gameforge.locale.monster_chat_303_5 = "Bu kadar basite alma. "
gameforge.locale.monster_chat_303_6 = "Bu savaş düşündüğümden daha zorlu olacak. "
gameforge.locale.monster_chat_304_1 = "Siz, kocalarınız sizi terkettikten sonra katıldınız. Hala bir erkek özlüyorsunuz. "
gameforge.locale.monster_chat_304_2 = "Buralarda hiç iyi bir adam yok mu? "
gameforge.locale.monster_chat_304_3 = "Sinirlerime dokunuyorsun. "
gameforge.locale.monster_chat_304_4 = "Nasıl bir histi? Ölümün sana baktığını göremiyor musun? "
gameforge.locale.monster_chat_304_5 = "Hayır! Pes mi ettin? "
gameforge.locale.monster_chat_304_6 = "Izdırap çabuk gelir. "
gameforge.locale.monster_chat_305_1 = "Hm... Kılıcın ilgi çekici görünüyor. "
gameforge.locale.monster_chat_305_2 = "Daha fazla dayanıklı ol ve gücünü arttır! "
gameforge.locale.monster_chat_305_3 = "Benim kadar güçlü olabileceğini düşünüyor musun? "
gameforge.locale.monster_chat_305_4 = "Tamam, hadi ciddi olalım. "
gameforge.locale.monster_chat_305_5 = "Tanrım! Ben sadece zayıf bir kadınım! "
gameforge.locale.monster_chat_305_6 = "Yorgun görünüyorsun. Neden bırakmıyorsun? Hehe. "
gameforge.locale.monster_chat_306_1 = "Serangyi bugün ne yapacak, hm? "
gameforge.locale.monster_chat_306_2 = "Ben çok sıkıldım. Hadi benimle oyna! "
gameforge.locale.monster_chat_306_3 = "İtiraf etmeliyim, en çok Sura erkeklerini seviyorum. "
gameforge.locale.monster_chat_306_4 = "Hey! Dövüşmeye ne dersin? "
gameforge.locale.monster_chat_306_5 = "Kadın olduğum için beni hafife alma! "
gameforge.locale.monster_chat_306_6 = "Acı sadece anın sonudur. "
gameforge.locale.monster_chat_307_1 = "Ne pahasına olursa olsun, sadece buradan çıkmak istiyorum. "
gameforge.locale.monster_chat_307_2 = "Yalnız yaşayan insanların evleri güvenli mi? "
gameforge.locale.monster_chat_307_3 = "Kapat çeneni! "
gameforge.locale.monster_chat_307_4 = "Bırak da seni tek bir okumla öldüreyim. "
gameforge.locale.monster_chat_307_5 = "Kaçmayı düşünme bile! "
gameforge.locale.monster_chat_307_6 = "O zaman, neden başlamıyoruz? "
gameforge.locale.monster_chat_394_1 = "Unutma; tüm erkekler düşmandır. "
gameforge.locale.monster_chat_394_2 = "Eğer güzel bir yüzü varsa, daha yumuşak olabiliriz. "
gameforge.locale.monster_chat_394_3 = "Ne, savaşmak istiyor musun? "
gameforge.locale.monster_chat_394_4 = "Çok uzun sürmeyecek, ama acıtacak. "
gameforge.locale.monster_chat_394_5 = "Ah, gerçekten acı çekiyormuş gibi görünüyorsun. "
gameforge.locale.monster_chat_394_6 = "Hoşça kal; cehenneme git! "
gameforge.locale.monster_chat_401_1 = "Hm, Beyaz Yeminlilerde ne yapacaksın? "
gameforge.locale.monster_chat_401_2 = "Nereye gidiyorsun? "
gameforge.locale.monster_chat_401_3 = "Hey, dövüşmek istiyor musun? "
gameforge.locale.monster_chat_401_4 = "Hehe, saldırılarımdan kaçıyorsun. "
gameforge.locale.monster_chat_401_5 = "Benden kaçabileceğini mi düşünüyorsun? "
gameforge.locale.monster_chat_401_6 = "Lanetim geliyor! "
gameforge.locale.monster_chat_402_1 = "Tanrım, çok yorgunum! "
gameforge.locale.monster_chat_402_2 = "Hey, güzel zırh! Fiyatı nedir? "
gameforge.locale.monster_chat_402_3 = "Ama hava bugün güzel. "
gameforge.locale.monster_chat_402_4 = "Ah, bugün çok eğitim yaptın! Harika! "
gameforge.locale.monster_chat_402_5 = "Yeteneklerim hakkında ne düşünüyorsun? İyi mi? Hehe. "
gameforge.locale.monster_chat_402_6 = "Ne oldu? Tabii ki, bu deli bir hız! "
gameforge.locale.monster_chat_403_1 = "Neden avlanmaya gitmiyorum ki? "
gameforge.locale.monster_chat_403_2 = "Bugün kimse şehre saldırmadı. "
gameforge.locale.monster_chat_403_3 = "Eğer dövüşmek istersen, dövüş, seni korkak! "
gameforge.locale.monster_chat_403_4 = "Hehe, sadece aceminin tekisin. "
gameforge.locale.monster_chat_403_5 = "Nerene ilk vurayım, koluna mı bacağna mı? "
gameforge.locale.monster_chat_403_6 = "İşte okum geliyor! "
gameforge.locale.monster_chat_404_1 = "Yakın zamanda şehre gidip eğitim yapmalıyım. "
gameforge.locale.monster_chat_404_2 = "Kendimi çok gergin hissediyorum. "
gameforge.locale.monster_chat_404_3 = "Bugün ne yemek var? "
gameforge.locale.monster_chat_404_4 = "İzliyorum, atabildiğin kadar çığlık at. "
gameforge.locale.monster_chat_404_5 = "Çok mu bekledin? İlk sen saldır bakalım! "
gameforge.locale.monster_chat_404_6 = "Kılıcın sesi heyecan verici! "
gameforge.locale.monster_chat_405_1 = "Kılıç hazır! "
gameforge.locale.monster_chat_405_2 = "Bakalım bu kılıcın ilk kurbanı kim olacak. "
gameforge.locale.monster_chat_405_3 = "Hey, neye bakıyorsun sen? "
gameforge.locale.monster_chat_405_4 = "Önce karnına vuracağım! "
gameforge.locale.monster_chat_405_5 = "Seni kılıcımın kını olarak kullanmak hoşuma gidecek. "
gameforge.locale.monster_chat_405_6 = "Seni kılıcımın kını olarak kullanmak hoşuma gidecek. "
gameforge.locale.monster_chat_406_1 = "Açlıktan ölüyorum. "
gameforge.locale.monster_chat_406_2 = "Yiyecek birşey var mı? "
gameforge.locale.monster_chat_406_3 = "Bütün bu yemeklerden sonra niye hala açım? "
gameforge.locale.monster_chat_406_4 = "Aarrgh, öl! "
gameforge.locale.monster_chat_406_5 = "Bu dövüşe midem karar verecek! "
gameforge.locale.monster_chat_406_6 = "Bu cennet ve cehennem arasındaki seçim! "
gameforge.locale.monster_chat_491_1 = "Daha fazla dayanamıyorum! Bedenim hevesleniyor. "
gameforge.locale.monster_chat_491_2 = "Oh evet. Biraz domuz görebilirim. "
gameforge.locale.monster_chat_491_3 = "Yerde? "
gameforge.locale.monster_chat_491_4 = "Seni bir vuruşta öldüreceğim. "
gameforge.locale.monster_chat_491_5 = "Bu güzel bir anı olacak! "
gameforge.locale.monster_chat_491_6 = "Geber! "
gameforge.locale.monster_chat_492_1 = "Bedenim yumuşadı. "
gameforge.locale.monster_chat_492_2 = "Neden daha fazla alıştırma yapmıyorum ki? "
gameforge.locale.monster_chat_492_3 = "Korktun mu ha? "
gameforge.locale.monster_chat_492_4 = "Ah, silahıma sadece yeni bir kurban. "
gameforge.locale.monster_chat_492_5 = "Saldırı şeklimi görebiliyor musun? Hayır mı? Senin kaybın! "
gameforge.locale.monster_chat_492_6 = "Biliyor muydun? Belki de küçük bir kesik bile sağlığın için kötü olabilir. "
gameforge.locale.monster_chat_493_1 = "Böyle bir ele sahip olmak hoş değil! "
gameforge.locale.monster_chat_493_2 = "Sen neye bakıyorsun öyle? Şimdiye dek elleri böyle olan birini daha görmemiş miydin? "
gameforge.locale.monster_chat_493_3 = "Siz insanlar beni gerçekten öfkelendiriyorsunuz. "
gameforge.locale.monster_chat_493_4 = "Seni alaşağı etmemi mi istiyorsun? Eğer istemiyorsan, bas git. Anladın mı? "
gameforge.locale.monster_chat_493_5 = "Sana en iyi vuruşumu göstereceğim! "
gameforge.locale.monster_chat_493_6 = "Ellerime baktığını biliyorum! "
gameforge.locale.monster_chat_494_1 = "Hey sen, eğer dövüşmek istiyorsan, buraya gel! "
gameforge.locale.monster_chat_494_2 = "Bence nefes kesiciyim! "
gameforge.locale.monster_chat_494_3 = "Neden birşeyler planlamıyoruz? "
gameforge.locale.monster_chat_494_4 = "Hayır, gitmene izin vermiyorum. Dövüş! "
gameforge.locale.monster_chat_494_5 = "Al sana! "
gameforge.locale.monster_chat_494_6 = "Öldün sayılır! "
gameforge.locale.monster_chat_501_1 = "Oyna benimle! Gitme! "
gameforge.locale.monster_chat_501_2 = "Kemiklerinden güzel bir heykel yapabilirim... "
gameforge.locale.monster_chat_501_3 = "Henüz alıştırma yapmadık. "
gameforge.locale.monster_chat_501_4 = "Ne harika bir zırh! Ona sahip olmak istiyorum. "
gameforge.locale.monster_chat_501_5 = "Şimdiden sonra, saldırılarımı daha zor karşılayabileceksin. "
gameforge.locale.monster_chat_501_6 = "Hehe, şaşırdın mı? "
gameforge.locale.monster_chat_502_1 = "Güzellik herşey değildir. "
gameforge.locale.monster_chat_502_2 = "Öyle boş boş bakma, dövüş! "
gameforge.locale.monster_chat_502_3 = "Öyle bakmandan hoşlanmıyorum. Dövüş! "
gameforge.locale.monster_chat_502_4 = "Benim kılıcımla ölmek onur verici olacaktır! "
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "Harika! "
gameforge.locale.monster_chat_503_1 = "Sakın dokunma! "
gameforge.locale.monster_chat_503_2 = "Yayımın teli kopmuş sanırım. "
gameforge.locale.monster_chat_503_3 = "Kahretsin, bu kuşbeyinliler yine burada. "
gameforge.locale.monster_chat_503_4 = "Fena değil. Ama yine de öleceksin! "
gameforge.locale.monster_chat_503_5 = "Bu şekilde iyi misin? Bu harika! "
gameforge.locale.monster_chat_503_6 = "Ok geliyor! "
gameforge.locale.monster_chat_504_1 = "Millet, buraya gelin! "
gameforge.locale.monster_chat_504_2 = "La, lala, şarkı mırıldanıyorum... "
gameforge.locale.monster_chat_504_3 = "Ne harika hava! "
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Güzel, dövüşme azmini sevdim! "
gameforge.locale.monster_chat_504_6 = "Gel buraya! Herkes bana gelsin! "
gameforge.locale.monster_chat_601_1 = "Açlıktan ölüyorum! "
gameforge.locale.monster_chat_601_2 = "Biraz meyve yemek istiyorum. "
gameforge.locale.monster_chat_601_3 = "Evet. Balık da olur. "
gameforge.locale.monster_chat_601_4 = "Kaçma! Burada kal! "
gameforge.locale.monster_chat_601_5 = "Ben kazandım. Bu apaçık belli. "
gameforge.locale.monster_chat_601_6 = "Görüyor musun? Kaçırdın! "
