gameforge.locale.monster_chat_602_2 = "Güzel hava. "
gameforge.locale.monster_chat_602_3 = "Ben indirmeye mi geldin? Ne saçma! "
gameforge.locale.monster_chat_602_4 = "Sen benim avımsın. "
gameforge.locale.monster_chat_602_5 = "Ne biçim işkence, değil mi? Pekala, senin işini direkt bitireceğim. "
gameforge.locale.monster_chat_602_6 = "Ben çok güçlüyüm, bilmiyor musun? "
gameforge.locale.monster_chat_603_1 = "Sıkıcı! "
gameforge.locale.monster_chat_603_2 = "Tüm bedenim savaşmak için can atıyor. "
gameforge.locale.monster_chat_603_3 = "İlk her kimse. "
gameforge.locale.monster_chat_603_4 = "İlk vuruşu sen mi yapacaksın? Seni buradan defedeceğim! "
gameforge.locale.monster_chat_603_5 = "Buraya bir defa girdin mi, artık çıkamazsın. Kaçış yok! "
gameforge.locale.monster_chat_603_6 = "Bu son darbem olacak. Bitireceğim! "
gameforge.locale.monster_chat_604_1 = "Yeni ve lezzetli bir zehir yaptım. Tadına bakmak ister misin? "
gameforge.locale.monster_chat_604_2 = "Nasıl çalıştığını görmek isterim! "
gameforge.locale.monster_chat_604_3 = "Sen, benim kobayım olmak ister misin? "
gameforge.locale.monster_chat_604_4 = "Seni zehirleyeceğim! "
gameforge.locale.monster_chat_604_5 = "Harika, dahi ahmak! "
gameforge.locale.monster_chat_604_6 = "Hayır, eğer hemen pes edersen ne eğlencesi kalır! "
gameforge.locale.monster_chat_631_1 = "Bir dakika. Avlanacak birşey bakıyorum. "
gameforge.locale.monster_chat_631_2 = "Eğer bu kadar yavaş olursan işe yaramaz! "
gameforge.locale.monster_chat_631_3 = "Çok açım! "
gameforge.locale.monster_chat_631_4 = "İyi ki avlayacak birşey buldum! "
gameforge.locale.monster_chat_631_5 = "Kurtuluşumuza dair umutlarını kırma! "
gameforge.locale.monster_chat_631_6 = "Sana gerçek acıyı tattıracağım! "
gameforge.locale.monster_chat_632_1 = "Sıkıcıııııı! "
gameforge.locale.monster_chat_632_2 = "Yem olarak kullanabileceğimiz birşey var mı? "
gameforge.locale.monster_chat_632_3 = "Eğer istiyorsan gel ve dövüş! "
gameforge.locale.monster_chat_632_4 = "Savaşçı olmak istiyorsan, marifetlerini göster bakalım! "
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "Eğer hayatta kalmak istiyorsan şehre kaç! "
gameforge.locale.monster_chat_633_1 = "Açlık acıverici. "
gameforge.locale.monster_chat_633_2 = "Ah, direkt bize doğru geliyorlar!! "
gameforge.locale.monster_chat_633_3 = "Dövüş nasıldı? "
gameforge.locale.monster_chat_633_4 = "Beni bu alçaklarla kıyaslama! "
