gameforge.locale.monster_chat_633_6 = "Kaçan kişi gerçek savaşçı değildir! "
gameforge.locale.monster_chat_634_1 = "Bu sefer zehir daha iyi olmalı! "
gameforge.locale.monster_chat_634_2 = "Eğer açsan, yarasalar lezzetli görünmeye başlayabilir. "
gameforge.locale.monster_chat_634_3 = "Dövüşe ne dersin? "
gameforge.locale.monster_chat_634_4 = "Bir defa zehrim vücuduna girdi mi, panzehiri olmadığından artık dönüşü olmaz. "
gameforge.locale.monster_chat_634_5 = "Benim büyüm herşeyi yakacak, hehehe! "
gameforge.locale.monster_chat_634_6 = "Yan! Haha! "
gameforge.locale.monster_chat_635_1 = "Kılıcını bilemeyi unutma! Her daim yeni bir dövüş seni bekliyor olacak. "
gameforge.locale.monster_chat_635_2 = "Bayıldın mı? "
gameforge.locale.monster_chat_635_3 = "Öl ki yeniden kalkabilesin! "
gameforge.locale.monster_chat_635_4 = "Beni yenmek mi? Gerçekten mi? "
gameforge.locale.monster_chat_635_5 = "Bir savaşçı gibi dövüş! "
gameforge.locale.monster_chat_635_6 = "Hadi, biraz daha. "
gameforge.locale.monster_chat_636_1 = "İyi bir dövüşçü arıyorum... "
gameforge.locale.monster_chat_636_2 = "Dövüşmek çok keyifli. "
gameforge.locale.monster_chat_636_3 = "Bütün zamanımı dövüşerek harcayabilirim! "
gameforge.locale.monster_chat_636_4 = "Çok iyi! Tekrar dene! "
gameforge.locale.monster_chat_636_5 = "(kılıç sallama) "
gameforge.locale.monster_chat_636_6 = "Bana vurduğuna pişman olacaksın! "
gameforge.locale.monster_chat_636_7 = "Çok zayıf! Ne gülünç! "
gameforge.locale.monster_chat_637_1 = "Rahatsız edilmek istemiyorum. "
gameforge.locale.monster_chat_637_2 = "Git uyu. Sonsuza kadar. "
gameforge.locale.monster_chat_637_3 = "Hala uyumadın mı? "
gameforge.locale.monster_chat_637_4 = "Yapacak çok şey var! "
gameforge.locale.monster_chat_637_5 = "Benim gücüm seni şaşırtmasın! "
gameforge.locale.monster_chat_637_6 = "Seni ezip unufak edeceğim! "
gameforge.locale.monster_chat_691_1 = "Her daim güçlü rakipler istemişimdir! "
