gameforge.npc_talk._580_sayTitle = "Ah-Yu: "
gameforge.npc_talk._590_say = "Bir kez daha çığlık atıyor... Ne istediğini[ENTER]çözemiyorum şeker falan mı acaba... Ahh hayır! "
